snippets.css:
/* â”€â”€ Reset & Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --dark:    #050910;
  --dark2:   #0d1117;
  --accent:  #1db954;
  --text:    #f2f6ff;
  --muted:   #64748b;
  --border:  rgba(242, 246, 255, 0.08);
  --surface: rgba(242, 246, 255, 0.04);
  --radius:  12px;
}

html { scroll-behavior: smooth; }

body {
  background: var(--dark);
  color: var(--text);
  font-family: system-ui, 'Inter', -apple-system, sans-serif;
  line-height: 1.6;
  overflow-x: hidden;
}

/* â”€â”€ Nav â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.nav {
  position: fixed;
  top: 0; left: 0; right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px 48px;
  background: rgba(5, 9, 16, 0.75);
  backdrop-filter: blur(16px);
  -webkit-backdrop-filter: blur(16px);
  border-bottom: 1px solid var(--border);
}

.nav-logo {
  font-size: 1.1rem;
  font-weight: 700;
  letter-spacing: -0.02em;
  color: var(--text);
  display: flex;
  align-items: center;
  gap: 8px;
}

.nav-logo-dot {
  width: 8px;
  height: 8px;
  background: var(--accent);
  border-radius: 50%;
}

.nav-links {
  display: flex;
  align-items: center;
  gap: 32px;
  list-style: none;
}

.nav-links a {
  color: var(--muted);
  text-decoration: none;
  font-size: 0.875rem;
  font-weight: 500;
  transition: color 0.2s;
}

.nav-links a:hover { color: var(--text); }

.nav-cta {
  background: var(--accent);
  color: #000;
  border: none;
  padding: 9px 20px;
  border-radius: 50px;
  font-size: 0.875rem;
  font-weight: 600;
  cursor: pointer;
  transition: opacity 0.2s, transform 0.2s;
}

.nav-cta:hover { opacity: 0.85; transform: translateY(-1px); }

/* â”€â”€ Hero â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 120px 48px 80px;
  gap: 64px;
  background: radial-gradient(ellipse 80% 60% at 60% 50%, rgba(29, 185, 84, 0.06) 0%, transparent 70%);
}

.hero-text { flex: 1; }

.hero-eyebrow {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 0.75rem;
  font-weight: 600;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 20px;
}

.hero-eyebrow-line {
  width: 24px;
  height: 1px;
  background: var(--accent);
}

.hero-title {
  font-size: clamp(2.4rem, 5vw, 4rem);
  font-weight: 800;
  line-height: 1.1;
  letter-spacing: -0.03em;
  margin-bottom: 20px;
}

.hero-title em {
  font-style: normal;
  color: var(--accent);
}

.hero-desc {
  font-size: 1.05rem;
  color: var(--muted);
  max-width: 440px;
  margin-bottom: 36px;
  line-height: 1.7;
}

.hero-actions {
  display: flex;
  align-items: center;
  gap: 16px;
}

.hero-play {
  display: flex;
  align-items: center;
  gap: 10px;
  background: var(--accent);
  color: #000;
  border: none;
  padding: 14px 28px;
  border-radius: 50px;
  font-size: 0.95rem;
  font-weight: 700;
  cursor: pointer;
  transition: opacity 0.2s, transform 0.2s;
}

.hero-play:hover { opacity: 0.85; transform: scale(1.02); }
.hero-play .play-icon { font-size: 0.9em; }

.hero-sub {
  color: var(--muted);
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: color 0.2s;
}

.hero-sub:hover { color: var(--text); }

/* Featured Episode Card */
.hero-card {
  flex: 0 0 400px;
  background: rgba(255, 255, 255, 0.04);
  border: 1px solid var(--border);
  border-radius: 20px;
  overflow: hidden;
  backdrop-filter: blur(20px);
  box-shadow: 0 32px 64px rgba(0, 0, 0, 0.5), 0 0 0 1px rgba(29, 185, 84, 0.08);
}

.hero-card-art {
  height: 200px;
  background: linear-gradient(135deg, #1a2a1a 0%, #0d1f1a 40%, #050910 100%);
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}

.hero-card-art-pattern {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(29,185,84,0.3) 0%, transparent 70%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 2rem;
}

.hero-card-live {
  position: absolute;
  top: 14px;
  left: 14px;
  background: #e11d48;
  color: #fff;
  font-size: 0.65rem;
  font-weight: 700;
  letter-spacing: 0.1em;
  padding: 4px 10px;
  border-radius: 50px;
  display: flex;
  align-items: center;
  gap: 6px;
}

.live-dot {
  width: 5px;
  height: 5px;
  background: #fff;
  border-radius: 50%;
  animation: pulse-dot 1.2s ease-in-out infinite;
}

@keyframes pulse-dot {
  0%, 100% { opacity: 1; transform: scale(1); }
  50%       { opacity: 0.3; transform: scale(0.7); }
}

.hero-card-body {
  padding: 20px;
}

.hero-card-tag {
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 8px;
}

.hero-card-title {
  font-size: 1rem;
  font-weight: 700;
  margin-bottom: 6px;
  line-height: 1.35;
}

.hero-card-meta {
  font-size: 0.8rem;
  color: var(--muted);
  display: flex;
  align-items: center;
  gap: 12px;
  margin-bottom: 16px;
}

/* Waveform */
.waveform {
  display: flex;
  align-items: center;
  gap: 3px;
  height: 32px;
  margin-bottom: 14px;
}

.waveform-bar {
  width: 3px;
  border-radius: 2px;
  background: var(--accent);
  animation: wave-scale 1.2s ease-in-out infinite;
  transform-origin: center;
}

.waveform-bar:nth-child(1)  { height: 40%; animation-delay: 0s; }
.waveform-bar:nth-child(2)  { height: 65%; animation-delay: 0.08s; }
.waveform-bar:nth-child(3)  { height: 80%; animation-delay: 0.16s; }
.waveform-bar:nth-child(4)  { height: 55%; animation-delay: 0.24s; }
.waveform-bar:nth-child(5)  { height: 95%; animation-delay: 0.32s; }
.waveform-bar:nth-child(6)  { height: 70%; animation-delay: 0.40s; }
.waveform-bar:nth-child(7)  { height: 85%; animation-delay: 0.48s; }
.waveform-bar:nth-child(8)  { height: 50%; animation-delay: 0.56s; }
.waveform-bar:nth-child(9)  { height: 75%; animation-delay: 0.64s; }
.waveform-bar:nth-child(10) { height: 60%; animation-delay: 0.72s; }
.waveform-bar:nth-child(11) { height: 90%; animation-delay: 0.80s; }
.waveform-bar:nth-child(12) { height: 45%; animation-delay: 0.88s; }
.waveform-bar:nth-child(13) { height: 70%; animation-delay: 0.96s; }
.waveform-bar:nth-child(14) { height: 55%; animation-delay: 1.04s; }
.waveform-bar:nth-child(15) { height: 38%; animation-delay: 1.12s; }

@keyframes wave-scale {
  0%, 100% { transform: scaleY(0.4); opacity: 0.5; }
  50%       { transform: scaleY(1);   opacity: 1;   }
}

.waveform-paused .waveform-bar {
  animation-play-state: paused;
  transform: scaleY(0.4);
  opacity: 0.3;
}

.hero-card-controls {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.play-btn {
  width: 38px;
  height: 38px;
  border-radius: 50%;
  background: var(--accent);
  border: none;
  color: #000;
  font-size: 0.9rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: transform 0.2s, opacity 0.2s;
}

.play-btn:hover { transform: scale(1.08); opacity: 0.9; }

.hero-card-time {
  font-size: 0.75rem;
  color: var(--muted);
  font-variant-numeric: tabular-nums;
}

/* â”€â”€ Section Shared â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
section { padding: 96px 48px; }

.section-header {
  display: flex;
  align-items: flex-end;
  justify-content: space-between;
  margin-bottom: 48px;
}

.section-label {
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.14em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 10px;
}

.section-title {
  font-size: clamp(1.6rem, 3vw, 2.2rem);
  font-weight: 800;
  letter-spacing: -0.025em;
}

.view-all {
  font-size: 0.85rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 6px;
  transition: color 0.2s;
  white-space: nowrap;
  flex-shrink: 0;
  padding-bottom: 4px;
}

.view-all:hover { color: var(--text); }

/* â”€â”€ Filter Pills â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.filter-pills {
  display: flex;
  gap: 8px;
  flex-wrap: wrap;
  margin-bottom: 36px;
}

.filter-pill {
  padding: 7px 16px;
  border-radius: 50px;
  border: 1px solid var(--border);
  background: var(--surface);
  color: var(--muted);
  font-size: 0.8rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
}

.filter-pill:hover {
  border-color: rgba(29, 185, 84, 0.3);
  color: var(--text);
}

.filter-pill.active {
  background: rgba(29, 185, 84, 0.12);
  border-color: rgba(29, 185, 84, 0.5);
  color: var(--accent);
}

/* â”€â”€ Episodes Grid â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.episodes-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
}

@media (max-width: 1100px) { .episodes-grid { grid-template-columns: repeat(2, 1fr); } }
@media (max-width: 640px)  { .episodes-grid { grid-template-columns: 1fr; } }

.episode-card {
  background: var(--dark2);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  overflow: hidden;
  transition: transform 0.25s, border-color 0.25s, box-shadow 0.25s;
  cursor: pointer;
}

.episode-card:hover {
  transform: translateY(-4px);
  border-color: rgba(29, 185, 84, 0.25);
  box-shadow: 0 16px 40px rgba(0, 0, 0, 0.4);
}

.episode-card.hidden { display: none; }

.episode-card-art {
  height: 140px;
  background: linear-gradient(135deg, #0f1f10 0%, #0a1a1a 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  font-size: 2rem;
}

.episode-card-cat {
  position: absolute;
  top: 10px;
  left: 10px;
  font-size: 0.65rem;
  font-weight: 700;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  padding: 4px 10px;
  border-radius: 50px;
  background: rgba(29, 185, 84, 0.15);
  color: var(--accent);
  border: 1px solid rgba(29, 185, 84, 0.3);
}

.episode-card-body {
  padding: 16px;
}

.episode-card-num {
  font-size: 0.7rem;
  color: var(--muted);
  margin-bottom: 6px;
  font-variant-numeric: tabular-nums;
}

.episode-card-title {
  font-size: 0.9rem;
  font-weight: 700;
  line-height: 1.35;
  margin-bottom: 10px;
}

.episode-card-footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.episode-card-dur {
  font-size: 0.75rem;
  color: var(--muted);
}

.episode-card-play {
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background: var(--surface);
  border: 1px solid var(--border);
  color: var(--text);
  font-size: 0.7rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background 0.2s, border-color 0.2s;
}

.episode-card-play:hover {
  background: rgba(29, 185, 84, 0.15);
  border-color: rgba(29, 185, 84, 0.4);
  color: var(--accent);
}

/* â”€â”€ Host Section â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.host-section {
  background: var(--dark2);
  border-top: 1px solid var(--border);
  border-bottom: 1px solid var(--border);
}

.host-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 80px;
  align-items: center;
}

@media (max-width: 768px) {
  .host-grid { grid-template-columns: 1fr; gap: 40px; }
}

.host-avatar-wrap {
  position: relative;
}

.host-avatar {
  width: 100%;
  aspect-ratio: 1;
  max-width: 380px;
  background: linear-gradient(135deg, #0f2a14 0%, #0a1a1a 100%);
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 5rem;
  border: 1px solid var(--border);
}

.host-badge {
  position: absolute;
  bottom: 20px;
  right: 0;
  background: var(--dark);
  border: 1px solid var(--border);
  border-radius: 12px;
  padding: 14px 18px;
  backdrop-filter: blur(12px);
}

.host-badge-num {
  font-size: 1.5rem;
  font-weight: 800;
  color: var(--accent);
}

.host-badge-label {
  font-size: 0.72rem;
  color: var(--muted);
  margin-top: 2px;
}

.host-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 16px;
}

.host-name {
  font-size: clamp(1.8rem, 3.5vw, 2.8rem);
  font-weight: 800;
  letter-spacing: -0.025em;
  margin-bottom: 16px;
}

.host-bio {
  color: var(--muted);
  font-size: 0.95rem;
  line-height: 1.75;
  margin-bottom: 32px;
}

.host-socials {
  display: flex;
  gap: 12px;
}

.host-social {
  padding: 9px 20px;
  border-radius: 50px;
  border: 1px solid var(--border);
  background: var(--surface);
  color: var(--muted);
  font-size: 0.8rem;
  font-weight: 500;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.2s;
}

.host-social:hover {
  border-color: rgba(29, 185, 84, 0.4);
  color: var(--accent);
}

/* â”€â”€ Platforms Row â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.platforms-section {
  text-align: center;
  padding: 80px 48px;
}

.platforms-title {
  font-size: 1rem;
  color: var(--muted);
  margin-bottom: 32px;
  font-weight: 500;
}

.platforms-row {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 16px;
  flex-wrap: wrap;
}

.platform-btn {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px 24px;
  border-radius: 50px;
  border: 1px solid var(--border);
  background: var(--surface);
  color: var(--text);
  font-size: 0.875rem;
  font-weight: 600;
  cursor: pointer;
  text-decoration: none;
  transition: all 0.25s;
}

.platform-btn:hover {
  border-color: rgba(29, 185, 84, 0.35);
  background: rgba(29, 185, 84, 0.06);
  color: var(--accent);
  transform: translateY(-2px);
}

.platform-icon { font-size: 1.1rem; }

/* â”€â”€ Footer â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.footer {
  padding: 48px;
  border-top: 1px solid var(--border);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.footer-copy {
  font-size: 0.8rem;
  color: var(--muted);
}

.footer-links {
  display: flex;
  gap: 24px;
}

.footer-links a {
  font-size: 0.8rem;
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.footer-links a:hover { color: var(--text); }

