*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --radius: 10px;
  --nav-h: 60px;
  --transition: 0.22s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
}

/* â”€â”€ Navbar â”€â”€ */
.navbar {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 200;
  background: rgba(5, 9, 16, 0.72);
  border-bottom: 1px solid var(--border);
  backdrop-filter: blur(16px);
  -webkit-backdrop-filter: blur(16px);
}

.navbar__inner {
  max-width: 1100px;
  margin: 0 auto;
  height: var(--nav-h);
  display: flex;
  align-items: center;
  gap: 2rem;
  padding: 0 1.25rem;
}

/* â”€â”€ Logo â”€â”€ */
.navbar__logo {
  display: flex;
  align-items: center;
  gap: 0.4rem;
  text-decoration: none;
  color: var(--text);
  font-weight: 700;
  font-size: 1rem;
  flex-shrink: 0;
}

.logo-mark {
  color: var(--accent);
  font-size: 1.1rem;
}

/* â”€â”€ Desktop nav â”€â”€ */
.navbar__nav {
  flex: 1;
  position: relative;
  display: flex;
  align-items: center;
}

.nav-items {
  display: flex;
  align-items: center;
  gap: 0.25rem;
}

.nav-link,
.nav-link--trigger {
  padding: 0.4rem 0.75rem;
  border-radius: 6px;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  background: none;
  border: none;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  gap: 0.3rem;
  transition: color var(--transition);
  white-space: nowrap;
}

.nav-link:hover,
.nav-link--trigger:hover,
.nav-link--active {
  color: var(--text);
}

.nav-link--active {
  font-weight: 600;
}

.nav-chevron {
  transition: transform var(--transition);
  flex-shrink: 0;
}

.nav-link--trigger[aria-expanded="true"] .nav-chevron {
  transform: rotate(180deg);
}

/* Animated underline */
.nav-indicator {
  position: absolute;
  bottom: -1px;
  height: 2px;
  background: var(--accent);
  border-radius: 1px;
  transition: left var(--transition), width var(--transition), opacity var(--transition);
  opacity: 0;
  pointer-events: none;
}

/* â”€â”€ Mega-menu â”€â”€ */
.nav-item-wrap {
  position: static;
}

.mega-menu {
  position: absolute;
  top: calc(100% + 12px);
  left: 50%;
  transform: translateX(-50%);
  width: min(680px, 96vw);
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: 14px;
  box-shadow: 0 20px 60px rgba(0,0,0,0.5), 0 4px 16px rgba(0,0,0,0.3);
  padding: 1.25rem;
  opacity: 0;
  pointer-events: none;
  transform: translateX(-50%) translateY(-6px);
  transition: opacity var(--transition), transform var(--transition);
  z-index: 300;
}

.mega-menu.open {
  opacity: 1;
  pointer-events: all;
  transform: translateX(-50%) translateY(0);
}

.mega-menu__grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.5rem;
}

.mega-card {
  display: flex;
  align-items: flex-start;
  gap: 0.875rem;
  padding: 0.875rem;
  border-radius: 8px;
  text-decoration: none;
  color: var(--text);
  transition: background var(--transition);
}

.mega-card:hover {
  background: rgba(255,255,255,0.04);
}

.mega-card__icon {
  font-size: 1.4rem;
  width: 36px;
  height: 36px;
  display: grid;
  place-items: center;
  background: rgba(56,189,248,0.08);
  border-radius: 8px;
  flex-shrink: 0;
}

.mega-card__title {
  font-size: 0.875rem;
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.mega-card__desc {
  font-size: 0.8rem;
  color: var(--muted);
  line-height: 1.5;
}

.mega-menu__footer {
  margin-top: 1rem;
  padding-top: 0.875rem;
  border-top: 1px solid var(--border);
}

.mega-footer-link {
  font-size: 0.825rem;
  color: var(--accent);
  text-decoration: none;
  transition: opacity 0.2s;
}

.mega-footer-link:hover { opacity: 0.75; }

/* â”€â”€ CTA â”€â”€ */
.navbar__cta {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  flex-shrink: 0;
}

.cta-link {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.cta-link:hover { color: var(--text); }

.cta-btn {
  font-size: 0.875rem;
  font-weight: 600;
  color: #050910;
  background: var(--accent);
  padding: 0.4rem 1rem;
  border-radius: 999px;
  text-decoration: none;
  transition: opacity 0.2s;
  white-space: nowrap;
}

.cta-btn:hover { opacity: 0.85; }

/* â”€â”€ Hamburger â”€â”€ */
.hamburger {
  display: none;
  flex-direction: column;
  justify-content: center;
  gap: 5px;
  width: 36px;
  height: 36px;
  background: none;
  border: none;
  cursor: pointer;
  padding: 4px;
  margin-left: auto;
}

.hamburger__bar {
  display: block;
  height: 2px;
  width: 100%;
  background: var(--text);
  border-radius: 1px;
  transition: transform 0.25s, opacity 0.25s;
}

.hamburger.open .hamburger__bar:nth-child(1) { transform: translateY(7px) rotate(45deg); }
.hamburger.open .hamburger__bar:nth-child(2) { opacity: 0; }
.hamburger.open .hamburger__bar:nth-child(3) { transform: translateY(-7px) rotate(-45deg); }

/* â”€â”€ Mobile nav â”€â”€ */
.mobile-nav {
  padding: 0.75rem 1.25rem 1.25rem;
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
  border-top: 1px solid var(--border);
  animation: mobile-in 0.22s ease;
}

@keyframes mobile-in {
  from { opacity: 0; transform: translateY(-6px); }
  to   { opacity: 1; transform: translateY(0); }
}

.mobile-nav__link {
  padding: 0.625rem 0.75rem;
  border-radius: 8px;
  color: var(--muted);
  text-decoration: none;
  font-size: 0.9rem;
  font-weight: 500;
  transition: background 0.2s, color 0.2s;
}

.mobile-nav__link:hover,
.mobile-nav__link--active {
  background: rgba(255,255,255,0.04);
  color: var(--text);
}

.mobile-nav__divider {
  height: 1px;
  background: var(--border);
  margin: 0.25rem 0;
}

.mobile-nav__cta-btn {
  display: block;
  padding: 0.625rem 1rem;
  background: var(--accent);
  color: #050910;
  border-radius: 8px;
  font-size: 0.875rem;
  font-weight: 600;
  text-decoration: none;
  text-align: center;
  margin-top: 0.25rem;
  transition: opacity 0.2s;
}

.mobile-nav__cta-btn:hover { opacity: 0.85; }

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 768px) {
  .navbar__nav,
  .navbar__cta { display: none; }
  .hamburger { display: flex; }
}

/* â”€â”€ Page content â”€â”€ */
.page-content {
  padding-top: calc(var(--nav-h) + 4rem);
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
}

.hero {
  text-align: center;
  max-width: 480px;
  padding: 2rem;
}

.hero__title {
  font-size: 2rem;
  font-weight: 700;
  letter-spacing: -0.03em;
  margin-bottom: 1rem;
}

.hero__desc {
  color: var(--muted);
  line-height: 1.7;
  font-size: 0.95rem;
}

