.rs-track {
  position: relative;
  height: 6px;
  background: rgba(255,255,255,0.08);
  border-radius: 999px;
  margin: 14px 0;
}

/* â”€â”€ Fill â”€â”€ */
.rs-fill {
  position: absolute;
  top: 0; bottom: 0;
  background: #38bdf8;
  border-radius: 999px;
  pointer-events: none;
}

/* â”€â”€ Thumb â”€â”€ */
.rs-thumb {
  position: absolute;
  top: 50%; transform: translate(-50%, -50%);
  width: 20px; height: 20px;
  background: #38bdf8;
  border: 3px solid #0d1117;
  border-radius: 50%;
  cursor: grab;
  outline: none;
  transition: box-shadow 0.15s, transform 0.1s;
  z-index: 2;
}
.rs-thumb:hover { box-shadow: 0 0 0 6px rgba(56,189,248,0.18); }
.rs-thumb:focus { box-shadow: 0 0 0 8px rgba(56,189,248,0.25); }
.rs-thumb:active { cursor: grabbing; transform: translate(-50%, -50%) scale(1.15); }

/* Tooltip on thumb */
.rs-thumb::before {
  content: attr(aria-valuenow);
  position: absolute;
  bottom: calc(100% + 8px);
  left: 50%; transform: translateX(-50%);
  background: #0d1117;
  border: 1px solid rgba(255,255,255,0.12);
  border-radius: 0.375rem;
  padding: 0.2rem 0.5rem;
  font-size: 0.72rem; color: #f2f6ff; white-space: nowrap;
  opacity: 0; pointer-events: none;
  transition: opacity 0.15s;
}
.rs-thumb:hover::before, .rs-thumb:focus::before { opacity: 1; }

/* â”€â”€ Labels â”€â”€ */
