.rs-color--green .rs-thumb:focus { box-shadow: 0 0 0 8px rgba(34,197,94,0.25); }

.rs-color--purple .rs-fill,
.rs-color--purple .rs-thumb { background: #a855f7; }
.rs-color--purple .rs-thumb { border-color: #0d1117; }
.rs-color--purple .rs-thumb:hover { box-shadow: 0 0 0 6px rgba(168,85,247,0.18); }
.rs-color--purple .rs-thumb:focus { box-shadow: 0 0 0 8px rgba(168,85,247,0.25); }

.rs-color--orange .rs-fill,
.rs-color--orange .rs-thumb { background: #f97316; }
.rs-color--orange .rs-thumb { border-color: #0d1117; }
.rs-color--orange .rs-thumb:hover { box-shadow: 0 0 0 6px rgba(249,115,22,0.18); }
.rs-color--orange .rs-thumb:focus { box-shadow: 0 0 0 8px rgba(249,115,22,0.25); }

