*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
body { font-family: "Inter", system-ui, sans-serif; background: #050910; color: #f2f6ff; }

/* NAV */
.nav { position: fixed; top: 0; left: 0; right: 0; z-index: 100; display: flex; align-items: center; justify-content: space-between; padding: 1rem 2.5rem; background: rgba(5,9,16,0.85); backdrop-filter: blur(12px); border-bottom: 1px solid rgba(255,255,255,0.06); }
.nav-logo { font-size: 1rem; font-weight: 900; letter-spacing: 0.08em; }
.nav-year { color: #f97316; }
.nav-links { list-style: none; display: flex; gap: 2rem; }
.nav-links a { font-size: 0.875rem; color: #64748b; text-decoration: none; font-weight: 500; transition: color 0.15s; }
.nav-links a:hover { color: #f2f6ff; }
.btn-tickets { font-size: 0.875rem; font-weight: 700; padding: 0.55rem 1.25rem; background: #f97316; color: #fff; border-radius: 0.5rem; text-decoration: none; transition: opacity 0.15s; }
.btn-tickets:hover { opacity: 0.85; }

/* HERO */
.hero { position: relative; min-height: 100vh; display: flex; align-items: center; justify-content: center; padding: 6rem 2rem 4rem; overflow: hidden; }
.hero-bg { position: absolute; inset: 0; background: radial-gradient(ellipse 80% 60% at 50% 30%, rgba(249,115,22,0.12) 0%, transparent 70%), radial-gradient(ellipse 50% 40% at 70% 60%, rgba(99,102,241,0.1) 0%, transparent 70%), #050910; }
.hero-content { position: relative; text-align: center; display: flex; flex-direction: column; align-items: center; gap: 2rem; max-width: 700px; }
.event-meta { display: flex; gap: 0.75rem; align-items: center; font-size: 0.8rem; font-weight: 600; letter-spacing: 0.06em; color: #f97316; text-transform: uppercase; }
.meta-sep { color: rgba(249,115,22,0.4); }
.hero-title { font-size: clamp(3rem, 8vw, 6rem); font-weight: 900; letter-spacing: -0.04em; line-height: 0.95; }
.hero-title em { font-style: italic; color: #f97316; }
.hero-sub { font-size: 1rem; color: #64748b; line-height: 1.65; max-width: 500px; }

/* COUNTDOWN */
.countdown { display: flex; align-items: center; gap: 0.5rem; }
.cd-unit { display: flex; flex-direction: column; align-items: center; gap: 0.25rem; min-width: 4.5rem; background: rgba(255,255,255,0.04); border: 1px solid rgba(255,255,255,0.08); border-radius: 0.75rem; padding: 0.875rem 0.5rem; }
.cd-num { font-size: 2rem; font-weight: 900; font-variant-numeric: tabular-nums; color: #f97316; }
.cd-label { font-size: 0.6rem; font-weight: 600; letter-spacing: 0.1em; text-transform: uppercase; color: #334155; }
.cd-sep { font-size: 1.5rem; color: #1e293b; padding-bottom: 1.5rem; }

.btn-primary { display: inline-flex; align-items: center; padding: 1rem 2rem; background: #f97316; color: #fff; font-weight: 700; font-size: 1rem; border-radius: 0.625rem; text-decoration: none; transition: opacity 0.15s; }
.btn-primary:hover { opacity: 0.85; }

/* SECTIONS */
.section { padding: 5rem 2rem; }
.section--dark { background: #0d1117; }
.container { max-width: 1000px; margin: 0 auto; }
.section-eyebrow { font-size: 0.72rem; font-weight: 700; letter-spacing: 0.14em; text-transform: uppercase; color: #f97316; margin-bottom: 0.5rem; }
.section-title { font-size: clamp(1.75rem, 4vw, 2.75rem); font-weight: 900; letter-spacing: -0.03em; margin-bottom: 2.5rem; line-height: 1.1; }
.section-title--light { color: #f2f6ff; }

/* SPEAKERS */
.speakers-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(150px, 1fr)); gap: 1.25rem; }
.speaker-card { background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.07); border-radius: 0.875rem; padding: 1.5rem 1rem; text-align: center; display: flex; flex-direction: column; align-items: center; gap: 0.5rem; transition: border-color 0.2s; }
.speaker-card:hover { border-color: rgba(249,115,22,0.3); }
.sp-photo { width: 3.5rem; height: 3.5rem; border-radius: 50%; display: grid; place-items: center; font-weight: 800; font-size: 0.85rem; color: #fff; margin-bottom: 0.25rem; }
.sp-photo--1 { background: linear-gradient(135deg, #f97316, #fb923c); }
.sp-photo--2 { background: linear-gradient(135deg, #6366f1, #818cf8); }
.sp-photo--3 { background: linear-gradient(135deg, #22c55e, #4ade80); }
.sp-photo--4 { background: linear-gradient(135deg, #ec4899, #f472b6); }
.sp-photo--5 { background: linear-gradient(135deg, #38bdf8, #67e8f9); }
.sp-photo--6 { background: linear-gradient(135deg, #f59e0b, #fbbf24); }
.speaker-card h3 { font-size: 0.875rem; font-weight: 700; }
.speaker-card p { font-size: 0.75rem; color: #64748b; }
.speakers-more { margin-top: 1.5rem; font-size: 0.85rem; color: #334155; text-align: center; }

/* SCHEDULE */
.day-tabs { display: flex; gap: 0.5rem; margin-bottom: 2rem; }
.day-tab { padding: 0.6rem 1.25rem; border: 1px solid rgba(255,255,255,0.08); border-radius: 999px; background: transparent; color: #64748b; font-size: 0.8rem; font-weight: 600; cursor: pointer; transition: all 0.15s; font-family: inherit; }
.day-tab.active { background: #f97316; border-color: #f97316; color: #fff; }
.schedule-list { display: flex; flex-direction: column; gap: 0; }
.schedule-item { display: flex; gap: 2rem; padding: 1.25rem 0; border-bottom: 1px solid rgba(255,255,255,0.06); }
.sched-time { font-size: 0.8rem; font-weight: 700; color: #f97316; min-width: 3.5rem; margin-top: 0.1rem; font-variant-numeric: tabular-nums; }
.sched-content h4 { font-size: 0.9rem; font-weight: 600; color: #e2e8f0; margin-bottom: 0.2rem; }
.sched-content p { font-size: 0.8rem; color: #475569; }

/* SPONSORS */
.sponsors-title { text-align: center; margin-bottom: 2rem; }
.sponsors-tier-label { font-size: 0.7rem; font-weight: 700; letter-spacing: 0.12em; text-transform: uppercase; color: #334155; text-align: center; margin: 1.5rem 0 1rem; }
.marquee { overflow: hidden; }
.marquee-track { display: flex; gap: 3rem; animation: marquee 20s linear infinite; width: max-content; }
.marquee--reverse .marquee-track { animation-direction: reverse; }
.marquee:hover .marquee-track { animation-play-state: paused; }
@keyframes marquee { to { transform: translateX(-50%); } }
.sp-logo { font-size: 1.25rem; font-weight: 900; color: rgba(255,255,255,0.08); letter-spacing: -0.02em; white-space: nowrap; }
.sp-logo--sm { font-size: 0.9rem; }

/* TICKETS */
.tickets-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(240px, 1fr)); gap: 1.25rem; margin-top: 2rem; }
.ticket-card { background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.07); border-radius: 1rem; padding: 2rem; display: flex; flex-direction: column; gap: 1rem; position: relative; }
.ticket-card--featured { border-color: rgba(249,115,22,0.3); background: rgba(249,115,22,0.04); }
.t-badge { position: absolute; top: -0.75rem; left: 50%; transform: translateX(-50%); background: #f97316; color: #fff; font-size: 0.7rem; font-weight: 700; padding: 0.2rem 0.75rem; border-radius: 999px; white-space: nowrap; }
.ticket-card h3 { font-size: 0.9rem; font-weight: 700; color: #94a3b8; text-transform: uppercase; letter-spacing: 0.08em; }
.t-price { font-size: 2rem; font-weight: 900; letter-spacing: -0.03em; }
.t-early { font-size: 0.7rem; font-weight: 600; background: rgba(249,115,22,0.15); color: #f97316; padding: 0.15rem 0.5rem; border-radius: 0.25rem; vertical-align: middle; }
.t-features { list-style: none; display: flex; flex-direction: column; gap: 0.5rem; font-size: 0.85rem; color: #64748b; flex: 1; }
.btn-primary-ticket, .btn-outline-ticket { display: block; text-align: center; padding: 0.75rem; border-radius: 0.625rem; font-weight: 700; font-size: 0.875rem; text-decoration: none; transition: opacity 0.15s; }
.btn-primary-ticket { background: #f97316; color: #fff; }
.btn-outline-ticket { border: 1px solid rgba(255,255,255,0.1); color: #94a3b8; }
.btn-outline-ticket:hover { background: rgba(255,255,255,0.05); }

/* FOOTER */
.footer { background: #040810; padding: 1.5rem 2.5rem; }
.footer-inner { display: flex; justify-content: space-between; align-items: center; font-size: 0.8rem; color: #334155; }
.footer-inner a { color: #475569; text-decoration: none; }

