*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo {
  width: 100%;
  max-width: 620px;
}

.demo-title {
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 0.375rem;
}

.demo-sub {
  color: #475569;
  font-size: 0.875rem;
  margin-bottom: 2rem;
}

.alerts-stack {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

/* â”€â”€ Alert base â”€â”€ */
.alert {
  display: flex;
  align-items: flex-start;
  gap: 0.875rem;
  padding: 1rem 1.125rem;
  border-radius: 0.75rem;
  border-left: 4px solid;
  background: var(--alert-bg);
  border-color: var(--alert-color);
  transition: opacity 0.3s ease, transform 0.3s ease, max-height 0.3s ease;
  overflow: hidden;
}

.alert--dismissing {
  opacity: 0;
  transform: translateX(12px);
  max-height: 0 !important;
  padding-top: 0;
  padding-bottom: 0;
  margin-bottom: -0.75rem;
}

/* â”€â”€ Variants â”€â”€ */
.alert--info    { --alert-color: #38bdf8; --alert-bg: rgba(56,189,248,0.08); }
.alert--success { --alert-color: #22c55e; --alert-bg: rgba(34,197,94,0.08); }
.alert--warning { --alert-color: #f59e0b; --alert-bg: rgba(245,158,11,0.08); }
.alert--error   { --alert-color: #ef4444; --alert-bg: rgba(239,68,68,0.08); }

/* â”€â”€ Icon â”€â”€ */
.alert-icon {
  font-size: 1rem;
  line-height: 1.5;
  color: var(--alert-color);
  flex-shrink: 0;
  margin-top: 0.05rem;
}

/* â”€â”€ Body â”€â”€ */
.alert-body {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.2rem;
  font-size: 0.875rem;
  line-height: 1.5;
}

.alert-title {
  color: #f2f6ff;
  font-weight: 600;
}

.alert-desc {
  color: #94a3b8;
}

/* â”€â”€ Dismiss â”€â”€ */
.alert-dismiss {
  flex-shrink: 0;
  background: none;
  border: none;
  color: #475569;
  cursor: pointer;
  font-size: 1.2rem;
  line-height: 1;
  padding: 0.125rem 0.25rem;
  border-radius: 0.25rem;
  transition: color 0.15s, background 0.15s;
}

.alert-dismiss:hover {
  color: #f2f6ff;
  background: rgba(255,255,255,0.06);
}

