*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo { width: 100%; max-width: 520px; }
.demo-title { font-size: 1.5rem; font-weight: 800; margin-bottom: 0.375rem; }
.demo-sub { color: #475569; font-size: 0.875rem; margin-bottom: 2rem; }

.examples { display: flex; flex-direction: column; gap: 1.75rem; }
.example {}
.example-label {
  font-size: 0.72rem; font-weight: 600; letter-spacing: 0.08em;
  text-transform: uppercase; color: #475569; margin-bottom: 0.625rem;
}

/* â”€â”€ Base separator â”€â”€ */
.sep {
  border: none;
  border-top: 1px solid rgba(255,255,255,0.08);
  margin: 0;
}

/* â”€â”€ Dashed â”€â”€ */
.sep--dashed { border-top-style: dashed; }

/* â”€â”€ Dotted â”€â”€ */
.sep--dotted { border-top-style: dotted; }

/* â”€â”€ Text variants â”€â”€ */
.sep--text {
  display: flex;
  align-items: center;
  gap: 1rem;
  border: none;
  color: #334155;
  font-size: 0.72rem;
  font-weight: 600;
  letter-spacing: 0.08em;
}
.sep--text::before, .sep--text::after {
  content: "";
  flex: 1;
  height: 1px;
  background: rgba(255,255,255,0.08);
}
.sep--text::before { content: ""; }
.sep--text::after  { content: ""; }

/* Inject label text */
.sep--text[data-label]::before { flex: 1; }
.sep--text[data-label]::after  { flex: 1; }

/* Override: inject content after ::before using a trick */
.sep--text { position: relative; }
.sep--text[data-label] {
  display: flex;
  align-items: center;
  gap: 1rem;
}
/* Use the data attribute as text via JS-free approach with ::after on parent */
/* Actually use a pseudo trick: can't use attr() in content for styled text easily without JS */
/* So we simply rely on the data-label being rendered via additional span below */

/* Left-aligned text */
.sep--text-left::before { flex: 0 0 0; display: none; }
.sep--text-left { justify-content: flex-start; }

/* â”€â”€ Gradient â”€â”€ */
.sep--gradient {
  border: none;
  height: 1px;
  background: linear-gradient(90deg, transparent 0%, rgba(255,255,255,0.15) 50%, transparent 100%);
}

/* â”€â”€ Diamond â”€â”€ */
.sep--diamond {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  color: rgba(255,255,255,0.15);
  font-size: 0.6rem;
}
.sep--diamond::before, .sep--diamond::after {
  content: "";
  flex: 1;
  height: 1px;
  background: rgba(255,255,255,0.08);
}
.sep--diamond::after {
  content: "â—†";
  flex: 0;
  color: rgba(255,255,255,0.2);
  font-size: 0.6rem;
}
.sep--diamond::before { content: ""; }

/* â”€â”€ Vertical â”€â”€ */
.sep--vertical {
  width: 1px;
  align-self: stretch;
  background: rgba(255,255,255,0.1);
  border: none;
  height: auto;
}

.flex-row {
  display: flex;
  align-items: center;
  gap: 1rem;
  font-size: 0.875rem;
  color: #64748b;
}

*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo { width: 100%; max-width: 520px; }
.demo-title { font-size: 1.5rem; font-weight: 800; margin-bottom: 0.375rem; }
.demo-sub { color: #475569; font-size: 0.875rem; margin-bottom: 2rem; }

.section { margin-bottom: 2rem; }
.section-label {
  font-size: 0.72rem; font-weight: 600; letter-spacing: 0.08em;
  text-transform: uppercase; color: #475569; margin-bottom: 0.75rem;
}

.bars { display: flex; flex-direction: column; gap: 0.75rem; }
.bar-row { display: flex; align-items: center; gap: 0.75rem; }
.bar-row .progress { flex: 1; }
.bar-pct { font-size: 0.75rem; color: #475569; width: 2.5rem; text-align: right; flex-shrink: 0; }

/* â”€â”€ Track â”€â”€ */
.progress {
  height: 8px;
  border-radius: 999px;
  background: #0d1117;
  overflow: hidden;
  position: relative;
}

/* â”€â”€ Fill â”€â”€ */
.progress-fill {
  height: 100%;
  border-radius: 999px;
  background: #38bdf8;
  width: 0%;
  transition: width 0.8s cubic-bezier(0.23,1,0.32,1);
}

.progress--green  .progress-fill { background: #22c55e; }
.progress--amber  .progress-fill { background: #f59e0b; }
.progress--red    .progress-fill { background: #ef4444; }

/* â”€â”€ Indeterminate â”€â”€ */
.progress--indeterminate { overflow: hidden; }
.progress-fill--indeterminate {
  width: 40% !important;
  animation: indeterminate 1.4s ease-in-out infinite;
}
@keyframes indeterminate {
  0%   { transform: translateX(-100%); }
  100% { transform: translateX(350%); }
}
@media (prefers-reduced-motion: reduce) {
  .progress-fill--indeterminate { animation: none; width: 60% !important; }
}

/* â”€â”€ Steps â”€â”€ */
.progress-steps {
  display: flex;
  gap: 0.375rem;
  margin-bottom: 0.625rem;
}
.step {
  flex: 1;
  height: 6px;
  border-radius: 999px;
  background: #0d1117;
}
.step--done   { background: #38bdf8; }
.step--active { background: linear-gradient(90deg, #38bdf8, #818cf8); }

.steps-label { font-size: 0.8rem; color: #64748b; }

/* â”€â”€ Stacked â”€â”€ */
.progress--stacked {
  height: 12px;
  display: flex;
  overflow: hidden;
  gap: 0;
  border-radius: 999px;
}
.progress-segment {
  height: 100%;
  transition: width 0.8s cubic-bezier(0.23,1,0.32,1);
}
.progress-segment--blue   { background: #38bdf8; }
.progress-segment--purple { background: #a78bfa; }
.progress-segment--green  { background: #22c55e; }

.stacked-legend {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  margin-top: 0.75rem;
}
.legend-item {
  display: flex;
  align-items: center;
  gap: 0.375rem;
  font-size: 0.75rem;
  color: #64748b;
}
.legend-dot {
  display: inline-block;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: rgba(255,255,255,0.15);
  flex-shrink: 0;
}
.legend-dot--blue   { background: #38bdf8; }
.legend-dot--purple { background: #a78bfa; }
.legend-dot--green  { background: #22c55e; }

snippets.js:
(function () {
  var reduced = window.matchMedia("(prefers-reduced-motion: reduce)").matches;

  document.querySelectorAll(".progress-fill[data-value]").forEach(function (fill) {
    var target = parseFloat(fill.dataset.value) || 0;
    if (reduced) { fill.style.width = target + "%"; return; }
    requestAnimationFrame(function () {
      setTimeout(function () { fill.style.width = target + "%"; }, 100);
    });
  });
}());

snippets.css:
/* â”€â”€ Reset & Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:     #050910;
  --accent: #f2f6ff;
  --muted:  #334155;
  --mid:    #64748b;
  --border: rgba(242, 246, 255, 0.07);
  --surface: rgba(242, 246, 255, 0.03);
}

html { scroll-behavior: smooth; }

body {
  background: var(--bg);
  color: var(--accent);
  font-family: system-ui, 'Inter', -apple-system, sans-serif;
  line-height: 1.6;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* â”€â”€ Scroll Progress Bar â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
#scroll-progress {
  position: fixed;
  top: 0;
  left: 0;
  height: 2px;
  width: 0%;
  background: var(--accent);
  z-index: 9999;
  transition: width 0.05s linear;
}

/* â”€â”€ Nav â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.nav {
  position: fixed;
  top: 0; left: 0; right: 0;
  z-index: 200;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px 48px;
  background: rgba(5, 9, 16, 0);
  mix-blend-mode: normal;
}

.nav-name {
  font-size: 0.85rem;
  font-weight: 600;
  letter-spacing: 0.06em;
  color: var(--accent);
  text-decoration: none;
}

.nav-badge {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 6px 14px;
  border-radius: 50px;
  border: 1px solid rgba(34, 197, 94, 0.35);
  background: rgba(34, 197, 94, 0.08);
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.08em;
  color: #22c55e;
}

.nav-badge-dot {
  width: 6px;
  height: 6px;
  background: #22c55e;
  border-radius: 50%;
  animation: blink 2s ease-in-out infinite;
}

@keyframes blink {
  0%, 100% { opacity: 1; }
  50%       { opacity: 0.3; }
}

/* â”€â”€ Hero â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  padding: 80px 40px;
  position: relative;
}

.hero-index {
  font-size: 0.7rem;
  font-weight: 500;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 24px;
}

.hero-title {
  font-size: clamp(3.5rem, 12vw, 10rem);
  font-weight: 900;
  letter-spacing: -0.04em;
  line-height: 0.9;
  color: var(--accent);
  margin-bottom: 16px;
}

.hero-role-wrap {
  font-size: clamp(1.1rem, 3vw, 2rem);
  font-weight: 300;
  color: var(--muted);
  letter-spacing: 0.02em;
  margin-bottom: 40px;
  height: 1.5em;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
}

.hero-role {
  transition: opacity 0.4s ease, transform 0.4s ease;
}

.hero-role.fade-out {
  opacity: 0;
  transform: translateY(-8px);
}

.hero-role.fade-in {
  opacity: 1;
  transform: translateY(0);
}

.hero-cta {
  display: inline-flex;
  align-items: center;
  gap: 10px;
  font-size: 0.85rem;
  font-weight: 500;
  color: var(--muted);
  text-decoration: none;
  letter-spacing: 0.04em;
  border-bottom: 1px solid var(--muted);
  padding-bottom: 2px;
  transition: color 0.2s, border-color 0.2s;
}

.hero-cta:hover { color: var(--accent); border-color: var(--accent); }

.hero-scroll-hint {
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  font-size: 0.7rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--muted);
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
}

.hero-scroll-line {
  width: 1px;
  height: 40px;
  background: linear-gradient(to bottom, var(--muted), transparent);
}

/* â”€â”€ Work List â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.work {
  padding: 96px 48px;
  border-top: 1px solid var(--border);
}

.work-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 0;
}

.work-label {
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--muted);
}

.work-count {
  font-size: 0.7rem;
  color: var(--muted);
}

.work-list {
  list-style: none;
  margin-top: 40px;
}

.work-item {
  border-top: 1px solid var(--border);
  position: relative;
  overflow: hidden;
  opacity: 0;
  transform: translateY(20px);
  transition: opacity 0.5s, transform 0.5s;
}

.work-item.revealed {
  opacity: 1;
  transform: translateY(0);
}

.work-item:last-child { border-bottom: 1px solid var(--border); }

.work-item-link {
  display: flex;
  align-items: center;
  gap: 24px;
  padding: 24px 0;
  text-decoration: none;
  color: var(--accent);
  transition: color 0.2s;
  position: relative;
  z-index: 1;
}

.work-item:hover .work-item-link { color: var(--accent); }

/* Hover line reveal */
.work-item-hover-line {
  position: absolute;
  inset: 0;
  background: rgba(242, 246, 255, 0.03);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform 0.3s ease;
}

.work-item:hover .work-item-hover-line { transform: scaleX(1); }

.work-item-num {
  font-size: 0.72rem;
  font-weight: 500;
  color: var(--muted);
  font-variant-numeric: tabular-nums;
  width: 28px;
  flex-shrink: 0;
}

.work-item-title {
  font-size: clamp(1rem, 2.5vw, 1.5rem);
  font-weight: 700;
  letter-spacing: -0.02em;
  flex: 1;
}

.work-item-type {
  font-size: 0.75rem;
  color: var(--muted);
  font-weight: 400;
  flex-shrink: 0;
}

.work-item-year {
  font-size: 0.75rem;
  color: var(--muted);
  font-variant-numeric: tabular-nums;
  flex-shrink: 0;
}

.work-item-arrow {
  font-size: 0.9rem;
  color: var(--muted);
  flex-shrink: 0;
  transform: rotate(45deg);
  transition: transform 0.2s, color 0.2s;
}

.work-item:hover .work-item-arrow {
  transform: rotate(0deg);
  color: var(--accent);
}

/* â”€â”€ Floating Cursor Dot â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
#cursor-dot {
  position: fixed;
  width: 8px;
  height: 8px;
  background: var(--accent);
  border-radius: 50%;
  pointer-events: none;
  z-index: 9000;
  transform: translate(-50%, -50%) scale(0);
  transition: transform 0.2s ease, top 0.12s ease, left 0.12s ease;
  mix-blend-mode: difference;
}

#cursor-dot.visible { transform: translate(-50%, -50%) scale(1); }

