*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --radius: 14px;
  --transition: 0.42s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  padding: 3rem 1.5rem;
}

.page {
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 3.5rem;
}

.demo-section { display: flex; flex-direction: column; gap: 1rem; }

.demo-label {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

/* â”€â”€ Carousel shell â”€â”€ */
.carousel {
  position: relative;
  outline: none;
}

.carousel-viewport {
  overflow: hidden;
  border-radius: var(--radius);
}

.carousel-track {
  display: flex;
  transition: transform var(--transition);
  will-change: transform;
}

/* â”€â”€ Single-item slides â”€â”€ */
.carousel-slide {
  min-width: 100%;
  height: 340px;
  display: flex;
  align-items: flex-end;
  padding: 2rem;
  border-radius: var(--radius);
  position: relative;
  overflow: hidden;
}

.slide--1 { background: linear-gradient(135deg, #0f2044 0%, #0a3a5c 100%); }
.slide--2 { background: linear-gradient(135deg, #1a0a3c 0%, #3b0f6e 100%); }
.slide--3 { background: linear-gradient(135deg, #072a1e 0%, #0d4a32 100%); }
.slide--4 { background: linear-gradient(135deg, #2a0f0a 0%, #5c1f10 100%); }
.slide--5 { background: linear-gradient(135deg, #1a1a0a 0%, #3d3800 100%); }

.slide-content {
  position: relative;
  z-index: 1;
}

.slide-badge {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  color: var(--accent);
  border: 1px solid rgba(56,189,248,0.3);
  border-radius: 4px;
  padding: 0.2em 0.5em;
  margin-bottom: 0.75rem;
  letter-spacing: 0.1em;
}

.slide-content h3 {
  font-size: 1.6rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  letter-spacing: -0.02em;
}

.slide-content p {
  color: rgba(242,246,255,0.65);
  font-size: 0.9rem;
  max-width: 360px;
  line-height: 1.6;
}

/* â”€â”€ Arrows â”€â”€ */
.carousel-arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: rgba(13,17,23,0.8);
  border: 1px solid var(--border);
  color: var(--text);
  font-size: 1.4rem;
  cursor: pointer;
  display: grid;
  place-items: center;
  transition: border-color 0.2s, background 0.2s, opacity 0.2s;
  z-index: 10;
  backdrop-filter: blur(6px);
  line-height: 1;
}

.carousel-arrow--prev { left: 0.75rem; }
.carousel-arrow--next { right: 0.75rem; }

.carousel-arrow:hover {
  border-color: var(--accent);
  background: rgba(56,189,248,0.12);
}

.carousel-arrow:disabled {
  opacity: 0.3;
  cursor: not-allowed;
}

/* â”€â”€ Dots â”€â”€ */
.carousel-dots {
  display: flex;
  justify-content: center;
  gap: 0.5rem;
  margin-top: 1rem;
}

.carousel-dot {
  width: 6px;
  height: 6px;
  border-radius: 50%;
  background: rgba(255,255,255,0.2);
  border: none;
  cursor: pointer;
  padding: 0;
  transition: background 0.25s, transform 0.25s, width 0.25s;
}

.carousel-dot.active {
  background: var(--accent);
  width: 18px;
  border-radius: 3px;
}

/* â”€â”€ Multi-card â”€â”€ */
.carousel-viewport--multi {
  border-radius: 0;
}

.carousel--multi .carousel-track {
  gap: 1rem;
}

.mc-card {
  min-width: calc((100% - 2rem) / 3);
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  padding: 1.5rem;
  flex-shrink: 0;
}

.mc-card__icon {
  font-size: 1.5rem;
  margin-bottom: 0.75rem;
}

.mc-card h4 {
  font-size: 1rem;
  font-weight: 600;
  margin-bottom: 0.4rem;
}

.mc-card p {
  font-size: 0.825rem;
  color: var(--muted);
  line-height: 1.55;
}

@media (max-width: 600px) {
  .mc-card { min-width: 80%; }
  .carousel-slide { height: 240px; }
}

