  --sb-thumb-hover: rgba(255, 255, 255, 0.28);
  --radius: 12px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  padding: 3rem 1.5rem;
}

.page {
  max-width: 720px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2.5rem;
}

.demo-section {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.demo-label {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.1em;
  color: var(--muted);
}

/* â”€â”€ Scroll area base â”€â”€ */
.scroll-area {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  outline: none;
  position: relative;
}

.scroll-area:focus-visible {
  box-shadow: 0 0 0 2px rgba(56,189,248,0.35);
}

/* â”€â”€ Webkit scrollbar â€” shared â”€â”€ */
.scroll-area::-webkit-scrollbar {
  width: 6px;
  height: 6px;
}

.scroll-area::-webkit-scrollbar-track {
  background: transparent;
}

.scroll-area::-webkit-scrollbar-corner {
  background: transparent;
}

.scroll-area::-webkit-scrollbar-thumb {
  background: transparent;
  border-radius: 3px;
  transition: background 0.25s;
}

/* Show thumb only while scrolling */
.scroll-area.scrolling::-webkit-scrollbar-thumb {
  background: var(--sb-thumb);
}

.scroll-area::-webkit-scrollbar-thumb:hover {
  background: var(--sb-thumb-hover);
}

/* Firefox */
.scroll-area {
  scrollbar-width: thin;
  scrollbar-color: transparent transparent;
}

.scroll-area.scrolling {
  scrollbar-color: var(--sb-thumb) transparent;
}

/* â”€â”€ Vertical â”€â”€ */
.scroll-area--vertical {
  height: 280px;
  overflow-y: auto;
  overflow-x: hidden;
}

/* â”€â”€ Horizontal â”€â”€ */
.scroll-area--horizontal {
  overflow-x: auto;
  overflow-y: hidden;
  padding-bottom: 0.5rem; /* space for scrollbar */
}

/* â”€â”€ Both â”€â”€ */
.scroll-area--both {
  height: 260px;
  overflow: auto;
}

