*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --card2: #111827;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --accent-bg: rgba(56, 189, 248, 0.14);
  --range-bg: rgba(56, 189, 248, 0.07);
  --today-dot: #38bdf8;
  --radius: 12px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  padding: 3rem 1.5rem;
}

.page {
  max-width: 560px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2.5rem;
}

.demo-block {
  display: flex;
  flex-direction: column;
  gap: 0.625rem;
}

.demo-label {
  display: block;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.09em;
  color: var(--muted);
}

/* â”€â”€ Input wrap â”€â”€ */
.dp-wrap,
.dp-range-wrap {
  position: relative;
}

.dp-range-wrap {
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.dp-input-wrap {
  position: relative;
  flex: 1;
}

.dp-input {
  width: 100%;
  background: var(--card);
  border: 1.5px solid var(--border);
  color: var(--text);
  padding: 0.6rem 2.5rem 0.6rem 0.875rem;
  border-radius: 10px;
  font-size: 0.875rem;
  cursor: pointer;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}

.dp-input:focus,
.dp-input[aria-expanded="true"] {
  border-color: var(--accent);
  box-shadow: 0 0 0 3px rgba(56,189,248,0.12);
}

.dp-icon {
  position: absolute;
  right: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
  font-size: 0.9rem;
  pointer-events: none;
  opacity: 0.55;
}

.dp-range-arrow {
  color: var(--muted);
  font-size: 0.85rem;
  flex-shrink: 0;
}

/* â”€â”€ Calendar popup â”€â”€ */
.dp-calendar {
  position: absolute;
  top: calc(100% + 8px);
  left: 0;
  background: var(--card2);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  box-shadow: 0 16px 48px rgba(0,0,0,0.5);
  padding: 1rem;
  min-width: 280px;
  z-index: 500;
  animation: cal-in 0.14s ease;
}

@keyframes cal-in {
  from { opacity: 0; transform: translateY(-6px); }
  to   { opacity: 1; transform: translateY(0); }
}

.dp-calendar[hidden] { display: none; }

.dp-calendar--range {
  left: 0;
}

/* Inline calendar */
.dp-calendar--inline {
  position: static;
  box-shadow: none;
  border: 1px solid var(--border);
  display: block;
}

.dp-inline {
  display: flex;
  flex-direction: column;
  gap: 0.875rem;
}

.dp-inline-value {
  font-size: 0.85rem;
  color: var(--muted);
  text-align: center;
}

/* â”€â”€ Calendar header â”€â”€ */
.cal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 0.875rem;
}

.cal-nav-btn {
  width: 28px;
  height: 28px;
  border-radius: 6px;
  background: none;
  border: 1px solid var(--border);
  color: var(--text);
  font-size: 1rem;
  cursor: pointer;
  display: grid;
  place-items: center;
  transition: background 0.2s, border-color 0.2s;
}

.cal-nav-btn:hover {
  background: rgba(255,255,255,0.06);
  border-color: rgba(255,255,255,0.2);
}

.cal-month-label {
  font-size: 0.9rem;
  font-weight: 600;
}

/* â”€â”€ Weekday row â”€â”€ */
.cal-weekdays {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
  margin-bottom: 4px;
}

.cal-weekday {
  font-size: 0.68rem;
  font-weight: 600;
  text-align: center;
  color: var(--muted);
  padding: 0.2rem 0;
  text-transform: uppercase;
  letter-spacing: 0.04em;
}

/* â”€â”€ Days grid â”€â”€ */
.cal-days {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
}

.cal-day {
  position: relative;
  width: 100%;
  aspect-ratio: 1;
  border-radius: 6px;
  background: none;
  border: none;
  color: var(--text);
  font-size: 0.82rem;
  cursor: pointer;
  display: grid;
  place-items: center;
  transition: background 0.15s, color 0.15s;
  outline: none;
}

.cal-day:hover:not([disabled]):not(.cal-day--selected):not(.cal-day--selected-end) {
  background: rgba(255,255,255,0.06);
}

.cal-day:focus-visible {
  box-shadow: 0 0 0 2px var(--accent);
}

/* Ghost days (prev/next month) */
.cal-day--ghost {
  color: rgba(71,85,105,0.4);
  cursor: default;
  pointer-events: none;
}

/* Disabled */
.cal-day[disabled] {
  color: rgba(71,85,105,0.35);
  cursor: not-allowed;
  pointer-events: none;
}

/* Today dot */
.cal-day--today::after {
  content: '';
  position: absolute;
  bottom: 3px;
  left: 50%;
  transform: translateX(-50%);
  width: 4px;
  height: 4px;
  background: var(--today-dot);
  border-radius: 50%;
}

/* Selected (single or range endpoints) */
.cal-day--selected,
.cal-day--selected-end {
  background: var(--accent);
  color: #050910;
  font-weight: 700;
}

.cal-day--selected:hover,
.cal-day--selected-end:hover {
  background: var(--accent);
}

/* In-range */
.cal-day--in-range {
  background: var(--range-bg);
  border-radius: 0;
}

/* Range start/end connectors */
.cal-day--selected.cal-day--range-start {
  border-radius: 6px 0 0 6px;
}

.cal-day--selected-end {
  border-radius: 0 6px 6px 0;
}

