*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo { width: 100%; max-width: 480px; }
.demo-title { font-size: 1.5rem; font-weight: 800; margin-bottom: 0.375rem; }
.demo-sub { color: #475569; font-size: 0.875rem; margin-bottom: 2rem; }
.section { margin-bottom: 2rem; }
.section-label {
  font-size: 0.72rem; font-weight: 600; letter-spacing: 0.08em;
  text-transform: uppercase; color: #475569; margin-bottom: 0.75rem;
}

/* â”€â”€ Wrapper â”€â”€ */
.cs-wrap { position: relative; width: 100%; }

/* â”€â”€ Trigger â”€â”€ */
.cs-trigger {
  display: flex; align-items: center; justify-content: space-between;
  width: 100%; height: 40px; padding: 0 0.75rem;
  background: #0d1117;
  border: 1px solid rgba(255,255,255,0.1);
  border-radius: 0.625rem;
  color: #475569;
  font-size: 0.875rem;
  cursor: pointer;
  outline: none;
  transition: border-color 0.15s;
  user-select: none;
}
.cs-trigger:focus, .cs-wrap.is-open .cs-trigger { border-color: #38bdf8; }
.cs-value { flex: 1; overflow: hidden; text-overflow: ellipsis; white-space: nowrap; }
.cs-value.has-value { color: #f2f6ff; }
.cs-arrow {
  width: 18px; height: 18px; flex-shrink: 0; margin-left: 0.5rem;
  color: #475569; transition: transform 0.2s;
}
.cs-wrap.is-open .cs-arrow { transform: rotate(180deg); }

/* â”€â”€ Dropdown â”€â”€ */
.cs-dropdown {
  position: absolute; top: calc(100% + 6px); left: 0; right: 0;
  background: #0d1117;
  border: 1px solid rgba(255,255,255,0.1);
  border-radius: 0.625rem;
  overflow: hidden;
  box-shadow: 0 16px 48px rgba(0,0,0,0.5);
  z-index: 200;
  opacity: 0;
  pointer-events: none;
  transform: translateY(-6px);
  transition: opacity 0.15s, transform 0.15s;
}
.cs-wrap.is-open .cs-dropdown { opacity: 1; pointer-events: auto; transform: translateY(0); }

/* â”€â”€ Search â”€â”€ */
.cs-search-wrap { padding: 0.5rem; border-bottom: 1px solid rgba(255,255,255,0.06); }
.cs-search {
  width: 100%; padding: 0.4rem 0.625rem;
  background: rgba(255,255,255,0.04);
  border: 1px solid rgba(255,255,255,0.08);
  border-radius: 0.4rem;
  color: #f2f6ff; font-size: 0.8rem; outline: none; font-family: inherit;
}
.cs-search::placeholder { color: #475569; }
.cs-search:focus { border-color: #38bdf8; }

/* â”€â”€ List â”€â”€ */
.cs-list { list-style: none; max-height: 220px; overflow-y: auto; padding: 0.375rem 0; }
.cs-list::-webkit-scrollbar { width: 4px; }
.cs-list::-webkit-scrollbar-track { background: transparent; }
.cs-list::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.1); border-radius: 2px; }

.cs-group-label {
  padding: 0.5rem 0.75rem 0.25rem;
  font-size: 0.68rem; font-weight: 700; letter-spacing: 0.08em;
  text-transform: uppercase; color: #334155;
}
.cs-option {
  padding: 0.5rem 0.75rem;
  font-size: 0.85rem; color: #94a3b8;
  cursor: pointer;
  transition: background 0.1s, color 0.1s;
  display: flex; align-items: center; justify-content: space-between;
}
.cs-option:hover, .cs-option.is-focused { background: rgba(255,255,255,0.05); color: #f2f6ff; }
.cs-option.is-selected { color: #38bdf8; }
.cs-option.is-selected::after { content: "âœ“"; font-size: 0.8rem; }
.cs-option[hidden] { display: none; }

.cs-empty { padding: 0.875rem 0.75rem; font-size: 0.82rem; color: #475569; text-align: center; }

/* â”€â”€ Multi-select trigger â”€â”€ */
.cs-trigger--multi {
  height: auto; min-height: 40px; padding: 0.375rem 0.5rem;
  flex-wrap: wrap; gap: 0.25rem; cursor: text;
}
.cs-tags { display: contents; }
.cs-tag {
  display: inline-flex; align-items: center; gap: 0.25rem;
  padding: 0.125rem 0.5rem 0.125rem 0.625rem;
  background: rgba(56,189,248,0.12);
  border: 1px solid rgba(56,189,248,0.25);
  border-radius: 999px;
  font-size: 0.75rem; color: #38bdf8;
  white-space: nowrap;
}
.cs-tag-remove {
  background: none; border: none; padding: 0; color: #38bdf8;
  cursor: pointer; font-size: 0.85rem; line-height: 1;
  display: inline-flex; align-items: center; opacity: 0.7;
  transition: opacity 0.15s;
}
.cs-tag-remove:hover { opacity: 1; }
.cs-tag-input {
  flex: 1; min-width: 80px; border: none; background: transparent;
  color: #f2f6ff; font-size: 0.85rem; outline: none; font-family: inherit;
  padding: 0.1rem 0;
}
.cs-tag-input::placeholder { color: #475569; }

