/* â”€â”€ Reset & Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:        #050910;
  --dark1:     #0d1117;
  --dark2:     #161b22;
  --dark3:     #1e2530;
  --sidebar:   #161b22;
  --card:      #1e2530;
  --accent:    #58a6ff;
  --green:     #3fb950;
  --red:       #f85149;
  --blue:      #58a6ff;
  --orange:    #d29922;
  --text:      #e6edf3;
  --muted:     #7d8590;
  --border:    rgba(255, 255, 255, 0.07);
  --border2:   rgba(255, 255, 255, 0.12);
  --radius:    8px;
}

html { scroll-behavior: smooth; }

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, 'Inter', -apple-system, sans-serif;
  font-size: 14px;
  line-height: 1.5;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* â”€â”€ Landing Nav â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.site-nav {
  position: sticky;
  top: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 14px 48px;
  background: rgba(5, 9, 16, 0.9);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border-bottom: 1px solid var(--border);
}

.site-logo {
  font-size: 1rem;
  font-weight: 700;
  color: var(--text);
  letter-spacing: -0.02em;
  display: flex;
  align-items: center;
  gap: 8px;
}

.site-logo-icon {
  width: 22px;
  height: 22px;
  background: var(--accent);
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.7rem;
  color: #000;
  font-weight: 800;
}

.site-nav-links {
  display: flex;
  align-items: center;
  gap: 28px;
  list-style: none;
}

.site-nav-links a {
  font-size: 0.85rem;
  color: var(--muted);
  text-decoration: none;
  transition: color 0.2s;
}

.site-nav-links a:hover { color: var(--text); }

.nav-btn {
  padding: 8px 18px;
  border-radius: var(--radius);
  background: var(--accent);
  color: #000;
  font-size: 0.82rem;
  font-weight: 700;
  border: none;
  cursor: pointer;
  transition: opacity 0.2s;
}

.nav-btn:hover { opacity: 0.85; }

/* â”€â”€ Landing Hero â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.landing-hero {
  padding: 80px 48px 64px;
  text-align: center;
  background: radial-gradient(ellipse 70% 50% at 50% 0%, rgba(88, 166, 255, 0.06) 0%, transparent 60%);
}

.hero-badge {
  display: inline-flex;
  align-items: center;
  gap: 6px;
  padding: 5px 12px;
  border-radius: 50px;
  border: 1px solid rgba(88, 166, 255, 0.25);
  background: rgba(88, 166, 255, 0.06);
  font-size: 0.72rem;
  font-weight: 600;
  color: var(--accent);
  margin-bottom: 20px;
  letter-spacing: 0.06em;
}

.hero-h1 {
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 900;
  letter-spacing: -0.04em;
  line-height: 1.05;
  margin-bottom: 16px;
}

.hero-h1 .hl { color: var(--accent); }

.hero-desc {
  font-size: 1rem;
  color: var(--muted);
  max-width: 500px;
  margin: 0 auto 36px;
  line-height: 1.7;
}

.hero-btns {
  display: flex;
  gap: 12px;
  justify-content: center;
  flex-wrap: wrap;
}

.btn-accent {
  padding: 12px 28px;
  border-radius: var(--radius);
  background: var(--accent);
  color: #000;
  font-size: 0.9rem;
  font-weight: 700;
  border: none;
  cursor: pointer;
  transition: opacity 0.2s, transform 0.2s;
}

.btn-accent:hover { opacity: 0.85; transform: translateY(-1px); }

.btn-ghost {
  padding: 12px 28px;
  border-radius: var(--radius);
  background: transparent;
  color: var(--text);
  font-size: 0.9rem;
  font-weight: 500;
  border: 1px solid var(--border2);
  cursor: pointer;
  transition: border-color 0.2s;
}

.btn-ghost:hover { border-color: rgba(88,166,255,0.35); }

/* â”€â”€ Dashboard Browser Chrome â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.dashboard-wrapper {
  margin: 0 48px 80px;
  border: 1px solid var(--border2);
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0 32px 80px rgba(0,0,0,0.6);
}

.browser-chrome {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 16px;
  background: var(--dark2);
  border-bottom: 1px solid var(--border);
}

.browser-dots {
  display: flex;
  gap: 6px;
}

.browser-dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
}

.browser-dot.red    { background: #ff5f56; }
.browser-dot.yellow { background: #ffbd2e; }
.browser-dot.green  { background: #27c93f; }

.browser-url {
  flex: 1;
  background: rgba(255,255,255,0.06);
  border-radius: 6px;
  padding: 5px 12px;
  font-size: 0.72rem;
  color: var(--muted);
  font-family: 'Courier New', monospace;
}

/* â”€â”€ Dashboard Layout â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.dashboard {
  display: flex;
  height: 620px;
  background: var(--dark1);
}

/* Sidebar */
.sidebar {
  width: 200px;
  flex-shrink: 0;
  background: var(--sidebar);
  border-right: 1px solid var(--border);
  display: flex;
  flex-direction: column;
  padding: 16px 0;
  transition: width 0.3s ease;
  overflow: hidden;
}

.sidebar.collapsed { width: 52px; }

.sidebar-logo {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 8px 16px;
  margin-bottom: 16px;
  font-size: 0.85rem;
  font-weight: 700;
  color: var(--text);
