.sidebar-item.active {
  color: var(--accent);
  background: rgba(88, 166, 255, 0.06);
  border-left-color: var(--accent);
}

.sidebar-icon { width: 14px; text-align: center; flex-shrink: 0; }

.sidebar-collapse {
  margin-top: auto;
  padding: 8px 16px;
  display: flex;
  align-items: center;
  gap: 10px;
  font-size: 0.78rem;
  color: var(--muted);
  cursor: pointer;
  border-top: 1px solid var(--border);
  white-space: nowrap;
  transition: color 0.2s;
}

.sidebar-collapse:hover { color: var(--text); }

/* Main content */
.dash-main {
  flex: 1;
  overflow-y: auto;
  padding: 20px 24px;
  scrollbar-width: thin;
  scrollbar-color: var(--border2) transparent;
}

