.dash-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 20px;
}

.dash-title {
  font-size: 0.95rem;
  font-weight: 700;
  color: var(--text);
}

.dash-period {
  font-size: 0.75rem;
  color: var(--muted);
