  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #475569;
  pointer-events: none;
}
.input-icon--left  { left: 0.75rem; }
.input-icon--right { right: 0.75rem; }
.input-icon--btn   { pointer-events: auto; background: none; border: none; cursor: pointer; transition: color 0.15s; }
.input-icon--btn:hover { color: #94a3b8; }

