*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }
:root { --navy: #0a1628; --gold: #b8963e; --warm: #f8f7f4; --text: #1e293b; --muted: #64748b; }
body { font-family: "Inter", system-ui, sans-serif; background: var(--warm); color: var(--text); }

/* NAV */
.nav { position: sticky; top: 0; z-index: 100; display: flex; align-items: center; justify-content: space-between; padding: 1rem 3rem; background: var(--warm); border-bottom: 1px solid #e2ddd5; gap: 2rem; }
.nav-brand { text-decoration: none; display: flex; flex-direction: column; gap: 0.1rem; }
.nav-name { font-family: "Playfair Display", Georgia, serif; font-size: 1.1rem; font-weight: 700; color: var(--navy); }
.nav-est { font-size: 0.65rem; letter-spacing: 0.1em; color: var(--gold); text-transform: uppercase; }
.nav-links { list-style: none; display: flex; gap: 2rem; }
.nav-links a { font-size: 0.875rem; color: var(--muted); text-decoration: none; font-weight: 500; transition: color 0.15s; }
.nav-links a:hover { color: var(--navy); }
.nav-cta { background: var(--navy) !important; color: var(--warm) !important; padding: 0.5rem 1.25rem !important; border-radius: 0.375rem; font-weight: 600 !important; }
.nav-mobile-toggle { display: none; background: none; border: none; font-size: 1.25rem; cursor: pointer; }

/* HERO */
.hero { display: grid; grid-template-columns: 1fr 1fr; min-height: 90vh; }
.hero-content { display: flex; flex-direction: column; justify-content: center; gap: 1.5rem; padding: 4rem 3rem 4rem 4rem; }
.hero-eyebrow { font-size: 0.72rem; font-weight: 700; letter-spacing: 0.14em; text-transform: uppercase; color: var(--gold); }
.hero-title { font-family: "Playfair Display", Georgia, serif; font-size: clamp(2rem, 3.5vw, 3.25rem); font-weight: 900; line-height: 1.15; color: var(--navy); }
.hero-title em { font-style: italic; color: var(--gold); }
.hero-sub { font-size: 0.9375rem; color: var(--muted); line-height: 1.7; max-width: 400px; }
.hero-ctas { display: flex; gap: 1rem; flex-wrap: wrap; margin-top: 0.5rem; }
.btn-primary { display: inline-flex; align-items: center; padding: 0.75rem 1.5rem; background: var(--navy); color: var(--warm); font-weight: 600; font-size: 0.875rem; border-radius: 0.375rem; text-decoration: none; transition: opacity 0.15s; }
.btn-primary:hover { opacity: 0.85; }
.btn-phone { display: inline-flex; align-items: center; gap: 0.5rem; padding: 0.75rem 1.25rem; font-weight: 600; font-size: 0.875rem; color: var(--navy); text-decoration: none; border: 1px solid #ddd8cf; border-radius: 0.375rem; transition: background 0.15s; }
.btn-phone:hover { background: #f0ece4; }
.hero-image { position: relative; background: linear-gradient(160deg, #1a2744, #0a1628); overflow: hidden; }
.hero-img-placeholder { position: absolute; inset: 0; background: linear-gradient(160deg, rgba(184,150,62,0.1), transparent 60%); }
.hero-badge { position: absolute; bottom: 2rem; right: 2rem; background: var(--warm); border-radius: 0.625rem; padding: 1rem 1.25rem; display: flex; align-items: center; gap: 0.75rem; box-shadow: 0 4px 20px rgba(0,0,0,0.3); }
.badge-val { font-family: "Playfair Display", serif; font-size: 1.75rem; font-weight: 900; color: var(--gold); line-height: 1; }
.badge-label { font-size: 0.7rem; color: var(--muted); line-height: 1.4; }

/* TRUST BAR */
.trust-bar { background: var(--navy); display: flex; align-items: center; justify-content: center; gap: 2rem; padding: 1rem 2rem; flex-wrap: wrap; }
.trust-item { font-size: 0.8rem; color: rgba(248,247,244,0.7); }
.trust-item strong { color: var(--warm); font-weight: 700; }
.trust-sep { color: rgba(184,150,62,0.4); }

/* SECTIONS */
.section { padding: 5rem 4rem; }
.section--dark { background: var(--navy); }
.section--warm { background: #f0ece4; }
.container { max-width: 1100px; margin: 0 auto; }
.container--narrow { max-width: 680px; margin: 0 auto; display: flex; flex-direction: column; gap: 2.5rem; }
.section-title { font-family: "Playfair Display", Georgia, serif; font-size: clamp(1.75rem, 3vw, 2.5rem); font-weight: 900; color: var(--navy); margin-bottom: 0.75rem; }
.section-title--light { color: var(--warm); }
.section-sub { font-size: 0.9375rem; color: var(--muted); max-width: 520px; margin-bottom: 2.5rem; line-height: 1.65; }

/* PRACTICE GRID */
.practice-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(280px, 1fr)); gap: 1.25rem; margin-top: 2rem; }
.practice-card { background: var(--warm); border: 1px solid #e2ddd5; border-radius: 0.625rem; padding: 1.75rem; display: flex; flex-direction: column; gap: 0.75rem; transition: box-shadow 0.2s; }
.practice-card:hover { box-shadow: 0 4px 20px rgba(10,22,40,0.1); }
.pc-icon { font-size: 1.5rem; }
.practice-card h3 { font-family: "Playfair Display", serif; font-size: 1rem; font-weight: 700; color: var(--navy); }
.practice-card p { font-size: 0.85rem; color: var(--muted); line-height: 1.6; flex: 1; }
.pc-link { font-size: 0.8rem; font-weight: 600; color: var(--gold); text-decoration: none; margin-top: auto; }

/* ATTORNEYS */
.attorneys-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(220px, 1fr)); gap: 1.5rem; margin-top: 2rem; }
.attorney-card { text-align: center; display: flex; flex-direction: column; align-items: center; gap: 0.5rem; }
.attorney-photo { width: 5rem; height: 5rem; border-radius: 50%; background: linear-gradient(135deg, #1a2744, #0a1628); border: 3px solid rgba(184,150,62,0.3); display: grid; place-items: center; font-size: 1.1rem; font-weight: 700; color: var(--gold); margin-bottom: 0.5rem; }
.attorney-card h3 { font-family: "Playfair Display", serif; font-size: 1rem; font-weight: 700; color: var(--warm); }
.attorney-title { font-size: 0.8rem; color: rgba(248,247,244,0.6); }
.attorney-bar { font-size: 0.72rem; color: var(--gold); }

/* RESULTS */
.results-list { display: flex; flex-direction: column; gap: 0; margin-top: 1.5rem; border-top: 1px solid #e2ddd5; }
.result-row { display: flex; align-items: baseline; gap: 1.5rem; padding: 1.25rem 0; border-bottom: 1px solid #e2ddd5; }
.result-amount { font-family: "Playfair Display", serif; font-size: 1.25rem; font-weight: 700; color: var(--gold); min-width: 7rem; }
.result-desc { flex: 1; font-size: 0.875rem; color: var(--muted); line-height: 1.5; }
.result-year { font-size: 0.8rem; color: #94a3b8; min-width: 3rem; text-align: right; }
.results-disclaimer { font-size: 0.72rem; color: #94a3b8; margin-top: 1rem; }

/* TESTIMONIALS */
.testimonial { border-left: 3px solid var(--gold); padding-left: 1.5rem; }
.testimonial-quote { font-family: "Playfair Display", serif; font-size: 1.125rem; font-style: italic; color: var(--navy); line-height: 1.7; }
.testimonial-attr { font-size: 0.8rem; color: var(--muted); margin-top: 0.75rem; }

/* CONTACT */
.contact-layout { display: grid; grid-template-columns: 1fr 1.5fr; gap: 4rem; }
.contact-sub { font-size: 0.9rem; color: rgba(248,247,244,0.6); margin: 0.5rem 0 2rem; line-height: 1.65; }
.office-info { display: flex; flex-direction: column; gap: 1rem; font-size: 0.85rem; color: rgba(248,247,244,0.6); line-height: 1.6; }
.office-info strong { color: var(--gold); }
.contact-form { display: flex; flex-direction: column; gap: 1.25rem; }
.form-row { display: grid; grid-template-columns: 1fr 1fr; gap: 1.25rem; }
.form-group { display: flex; flex-direction: column; gap: 0.4rem; }
.form-group label { font-size: 0.8rem; font-weight: 600; color: rgba(248,247,244,0.7); }
.form-group input, .form-group select, .form-group textarea { background: rgba(255,255,255,0.06); border: 1px solid rgba(255,255,255,0.1); border-radius: 0.375rem; padding: 0.75rem 1rem; font-size: 0.875rem; color: var(--warm); font-family: inherit; outline: none; transition: border-color 0.15s; }
.form-group input:focus, .form-group select:focus, .form-group textarea:focus { border-color: var(--gold); }
.form-group select option { background: var(--navy); }
.btn-submit { padding: 0.875rem 2rem; background: var(--gold); border: none; color: var(--navy); font-weight: 700; font-size: 0.9rem; font-family: inherit; border-radius: 0.375rem; cursor: pointer; transition: opacity 0.15s; align-self: flex-start; }
.btn-submit:hover { opacity: 0.85; }
.form-legal { font-size: 0.7rem; color: rgba(255,255,255,0.25); line-height: 1.5; }

/* FOOTER */
.footer { background: #040c1a; padding: 1.5rem 4rem; }
.footer-inner { display: flex; align-items: center; justify-content: space-between; }
.footer-brand { font-family: "Playfair Display", serif; font-size: 0.9rem; color: rgba(248,247,244,0.4); }
.footer-legal { font-size: 0.72rem; color: rgba(248,247,244,0.3); }

@media (max-width: 768px) {
  .hero { grid-template-columns: 1fr; }
  .hero-image { display: none; }
  .contact-layout { grid-template-columns: 1fr; }
  .section { padding: 3rem 1.5rem; }
  .nav-links { display: none; }
  .nav-mobile-toggle { display: block; }
}

