*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --card2: #0a0f1a;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --success: #4ade80;
  --radius: 12px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  padding: 2.5rem 1.5rem;
}

.page {
  max-width: 900px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 2.5rem;
}

.demo-section {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.demo-label {
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.09em;
  color: var(--muted);
}

/* â”€â”€ Panels container â”€â”€ */
.panels {
  border: 1px solid var(--border);
  border-radius: var(--radius);
  overflow: hidden;
  display: flex;
  height: 260px;
  background: var(--card);
  user-select: none;
}

.panels--v {
  flex-direction: column;
}

/* â”€â”€ Panel â”€â”€ */
.panel {
  flex: 1;
  min-width: 80px;
  min-height: 60px;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  flex-shrink: 0;
}

.panel-sidebar {
  flex-shrink: 0;
}

/* â”€â”€ Panel header â”€â”€ */
.panel-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.5rem 0.875rem;
  border-bottom: 1px solid var(--border);
  background: var(--card2);
  flex-shrink: 0;
  min-height: 36px;
}

.panel-title {
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--muted);
  letter-spacing: 0.04em;
}

.panel-tabs {
  display: flex;
  gap: 0.5rem;
}

.panel-tab {
  font-size: 0.72rem;
  color: var(--muted);
  padding: 0.2rem 0.5rem;
  border-radius: 4px;
  cursor: pointer;
}

.panel-tab.active {
  background: rgba(56,189,248,0.1);
  color: var(--accent);
}

/* â”€â”€ Panel body â”€â”€ */
.panel-body {
  flex: 1;
  overflow: auto;
  padding: 0.625rem;
  scrollbar-width: thin;
  scrollbar-color: rgba(255,255,255,0.08) transparent;
}

.panel-body::-webkit-scrollbar { width: 4px; }
.panel-body::-webkit-scrollbar-track { background: transparent; }
.panel-body::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.1); border-radius: 2px; }

/* â”€â”€ Resize handle â”€â”€ */
.resize-handle {
  flex-shrink: 0;
  background: var(--border);
  position: relative;
  z-index: 1;
  transition: background 0.15s;
}

.resize-handle--h {
  width: 4px;
  cursor: col-resize;
}

.resize-handle--v {
  height: 4px;
  cursor: row-resize;
}

.resize-handle:hover,
.resize-handle:focus-visible,
.resize-handle.dragging {
  background: var(--accent);
  outline: none;
}

/* Widen hit area without changing visual width */
.resize-handle--h::before {
  content: '';
  position: absolute;
  top: 0;
  left: -4px;
  right: -4px;
  bottom: 0;
}

.resize-handle--v::before {
  content: '';
  position: absolute;
  left: 0;
  top: -4px;
  right: 0;
  bottom: -4px;
}

/* â”€â”€ File tree â”€â”€ */
.file-tree {
  list-style: none;
  font-size: 0.8rem;
  line-height: 2.2;
}

.ft-dir > span,
.ft-file {
  display: flex;
  align-items: center;
  gap: 0.3rem;
  padding: 0 0.25rem;
  border-radius: 4px;
  cursor: pointer;
  white-space: nowrap;
  color: var(--muted);
  transition: background 0.15s, color 0.15s;
}

.ft-dir > span:hover,
.ft-file:hover {
  background: rgba(255,255,255,0.04);
  color: var(--text);
}

.ft-file.active {
  background: rgba(56,189,248,0.08);
  color: var(--accent);
}

.ft-dir ul {
  list-style: none;
  padding-left: 1rem;
}

/* â”€â”€ Code block â”€â”€ */
.panel-body--code {
  padding: 0.875rem;
}

.code-block {
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.78rem;
  line-height: 1.8;
  color: var(--text);
  white-space: pre;
}

.c-tag  { color: #7dd3fc; }
.c-attr { color: #86efac; }
.c-str  { color: #fde68a; }
.c-cmt  { color: var(--muted); font-style: italic; }

/* â”€â”€ Preview â”€â”€ */
.panel-body--preview {
  display: flex;
  align-items: center;
  justify-content: center;
}

.preview-box {
  width: 80%;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.preview-bar {
  height: 10px;
  background: rgba(56,189,248,0.15);
  border-radius: 5px;
}

.preview-bar--short  { width: 45%; background: rgba(56,189,248,0.08); }
.preview-bar--medium { width: 70%; background: rgba(56,189,248,0.08); }

/* â”€â”€ Terminal â”€â”€ */
.terminal-dots {
  display: flex;
  gap: 5px;
}

.tdot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
}

.tdot--red    { background: #f87171; }
.tdot--yellow { background: #fbbf24; }
.tdot--green  { background: #4ade80; }

.panel-body--terminal {
  padding: 0.625rem 0.875rem;
  font-family: 'SF Mono', 'Fira Code', monospace;
  font-size: 0.78rem;
}

.term-line {
  line-height: 2;
  color: var(--text);
}

.term-prompt {
  color: var(--accent);
  margin-right: 0.5rem;
}

.term-line--ok  { color: #4ade80; }
.term-line--dim { color: var(--muted); }

/* â”€â”€ Three-panel â”€â”€ */
.placeholder-list { display: flex; flex-direction: column; }

.pl-item {
  padding: 0.4rem 0.5rem;
  font-size: 0.82rem;
  color: var(--muted);
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.15s, color 0.15s;
}

.pl-item:hover { background: rgba(255,255,255,0.04); color: var(--text); }
.pl-item.active { background: rgba(56,189,248,0.08); color: var(--accent); }

.panel-body--center-placeholder {
  display: flex;
  align-items: center;
  justify-content: center;
}

.center-msg {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
  color: var(--muted);
  font-size: 0.85rem;
}

.prop-row {
  display: flex;
  justify-content: space-between;
  padding: 0.35rem 0.25rem;
  font-size: 0.8rem;
  border-bottom: 1px solid rgba(255,255,255,0.04);
}

.prop-key { color: var(--muted); }
.prop-val { color: var(--accent); font-variant-numeric: tabular-nums; }

