*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo { width: 100%; max-width: 480px; }
.demo-title { font-size: 1.5rem; font-weight: 800; margin-bottom: 0.375rem; }
.demo-sub { color: #475569; font-size: 0.875rem; margin-bottom: 2rem; }

.groups { display: flex; flex-direction: column; gap: 2rem; }
.group-row {}
.group-label {
  font-size: 0.72rem;
  font-weight: 600;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  color: #475569;
  margin-bottom: 0.75rem;
}

/* â”€â”€ Group â”€â”€ */
.avatar-group {
  display: flex;
  align-items: center;
}

.avatar-group > .avatar + .avatar { margin-left: -0.6rem; }
.avatar-group--sm > .avatar + .avatar { margin-left: -0.5rem; }
.avatar-group--lg > .avatar + .avatar { margin-left: -0.75rem; }
.avatar-group--spread > .avatar + .avatar { margin-left: 0.25rem; }

/* â”€â”€ Avatar â”€â”€ */
.avatar {
  --size: 36px;
  width: var(--size);
  height: var(--size);
  border-radius: 50%;
  border: 2px solid #050910;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: calc(var(--size) * 0.32);
  font-weight: 700;
  color: #f2f6ff;
  background: #1e293b;
  position: relative;
  cursor: default;
  flex-shrink: 0;
  transition: transform 0.15s;
}
.avatar:hover { transform: translateY(-3px); z-index: 10; }

/* Tooltip */
.avatar[data-name]::after {
  content: attr(data-name);
  position: absolute;
  bottom: calc(100% + 6px);
  left: 50%;
  transform: translateX(-50%);
  background: #0d1117;
  border: 1px solid rgba(255,255,255,0.08);
  color: #f2f6ff;
  font-size: 0.7rem;
  font-weight: 500;
  white-space: nowrap;
  padding: 0.25rem 0.5rem;
  border-radius: 0.375rem;
  pointer-events: none;
  opacity: 0;
  transition: opacity 0.15s;
}
.avatar:hover::after { opacity: 1; }

/* Sizes */
.avatar-group--sm .avatar { --size: 28px; }
.avatar-group--lg .avatar { --size: 48px; }

/* Colors */
.avatar--blue   { background: rgba(56,189,248,0.18);  color: #38bdf8; }
.avatar--green  { background: rgba(34,197,94,0.18);   color: #22c55e; }
.avatar--amber  { background: rgba(245,158,11,0.18);  color: #f59e0b; }
.avatar--red    { background: rgba(239,68,68,0.18);   color: #ef4444; }
.avatar--purple { background: rgba(167,139,250,0.18); color: #a78bfa; }

/* Overflow pill */
.avatar--overflow {
  background: rgba(255,255,255,0.07);
  color: #94a3b8;
  font-size: 0.7rem;
  font-weight: 600;
}

