}

.hero-image {
  position: absolute;
  inset: 0;
  background: linear-gradient(160deg, #1a1a14 0%, #0f0f0c 50%, #0a0a0a 100%);
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 6rem;
  opacity: 0.3;
}

.hero-gradient {
  position: absolute;
  inset: 0;
  background: linear-gradient(to top, rgba(10,10,10,0.95) 0%, rgba(10,10,10,0.5) 40%, rgba(10,10,10,0.1) 100%);
}

.hero-content {
  position: relative;
  z-index: 2;
  padding: 0 56px 72px;
