  padding-bottom: 4px;
}

.projects-scroll {
  display: flex;
  gap: 24px;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
  cursor: grab;
  padding-right: 56px;
  padding-bottom: 8px;
