}

.projects-scroll::-webkit-scrollbar { display: none; }
.projects-scroll.dragging { cursor: grabbing; }

.project-card {
  flex: 0 0 320px;
  scroll-snap-align: start;
  border: 1px solid var(--border);
  border-radius: 4px;
