  color: var(--off-white);
}

.contact-inner {
  max-width: 560px;
}

.contact-eyebrow {
  font-size: 0.7rem;
  font-weight: 500;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--gold);
  margin-bottom: 16px;
}

.contact-title {
  font-family: var(--font-serif);
