*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --danger: #f87171;
  --success: #4ade80;
  --warning: #fbbf24;
  --info: #60a5fa;
  --radius: 12px;
  --transition: 0.32s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* â”€â”€ Demo center â”€â”€ */
.demo-center {
  text-align: center;
  max-width: 420px;
  padding: 2rem;
}

.demo-title {
  font-size: 1.75rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
  letter-spacing: -0.02em;
}

.demo-subtitle {
  color: var(--muted);
  margin-bottom: 2rem;
  font-size: 0.9rem;
}

.trigger-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 0.75rem;
}

.trigger-btn {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  justify-content: center;
  background: var(--card);
  border: 1px solid var(--border);
  color: var(--text);
  padding: 0.75rem 1rem;
  border-radius: var(--radius);
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: border-color var(--transition), background var(--transition);
}

.trigger-btn:hover {
  border-color: var(--accent);
  background: rgba(56, 189, 248, 0.06);
}

.trigger-icon {
  font-size: 1rem;
  color: var(--accent);
}

/* â”€â”€ Backdrop â”€â”€ */
.backdrop {
  position: fixed;
  inset: 0;
  background: rgba(5, 9, 16, 0.7);
  backdrop-filter: blur(2px);
  opacity: 0;
  pointer-events: none;
  transition: opacity var(--transition);
  z-index: 100;
}

.backdrop.active {
  opacity: 1;
  pointer-events: all;
}

/* â”€â”€ Drawers base â”€â”€ */
.drawer {
  position: fixed;
  background: var(--card);
  z-index: 200;
  display: flex;
  flex-direction: column;
  transition: transform var(--transition);
  border-color: var(--border);
}

.drawer__header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.125rem 1.25rem;
  border-bottom: 1px solid var(--border);
  flex-shrink: 0;
}

.drawer__title {
  font-size: 1rem;
  font-weight: 600;
}

.drawer__logo {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--accent);
}

.drawer__close {
  background: none;
  border: none;
  color: var(--muted);
  font-size: 1rem;
  cursor: pointer;
  width: 28px;
  height: 28px;
  border-radius: 6px;
  display: grid;
  place-items: center;
  transition: color 0.2s, background 0.2s;
}

.drawer__close:hover {
  color: var(--text);
  background: rgba(255,255,255,0.06);
}

.drawer__content {
  flex: 1;
  overflow-y: auto;
  padding: 1.25rem;
  scrollbar-width: thin;
  scrollbar-color: rgba(255,255,255,0.1) transparent;
}

.drawer__content--horizontal {
  display: flex;
  gap: 1rem;
  overflow-x: auto;
  overflow-y: hidden;
  align-items: flex-start;
}

.drawer__desc {
  color: var(--muted);
  font-size: 0.875rem;
  line-height: 1.6;
  margin-bottom: 1.25rem;
}

.drawer__footer {
  padding: 1rem 1.25rem;
  border-top: 1px solid var(--border);
  flex-shrink: 0;
}

/* â”€â”€ Bottom sheet â”€â”€ */
.drawer--bottom {
  bottom: 0;
  left: 0;
  right: 0;
  border-radius: var(--radius) var(--radius) 0 0;
  border-top: 1px solid var(--border);
  max-height: 80vh;
  transform: translateY(100%);
}

.drawer--bottom.open {
  transform: translateY(0);
}

.drawer__handle-bar {
  width: 36px;
  height: 4px;
  background: rgba(255,255,255,0.18);
  border-radius: 2px;
  margin: 0.625rem auto 0;
  flex-shrink: 0;
  cursor: grab;
}

/* â”€â”€ Right drawer â”€â”€ */
.drawer--right {
  top: 0;
  right: 0;
  bottom: 0;
  width: 320px;
  border-left: 1px solid var(--border);
  transform: translateX(100%);
}

.drawer--right.open {
  transform: translateX(0);
}

/* â”€â”€ Left drawer â”€â”€ */
.drawer--left {
  top: 0;
  left: 0;
  bottom: 0;
  width: 260px;
  border-right: 1px solid var(--border);
  transform: translateX(-100%);
}

.drawer--left.open {
  transform: translateX(0);
}

/* â”€â”€ Top drawer â”€â”€ */
.drawer--top {
  top: 0;
  left: 0;
  right: 0;
  border-bottom: 1px solid var(--border);
  transform: translateY(-100%);
}

.drawer--top.open {
  transform: translateY(0);
}

/* â”€â”€ Drawer list â”€â”€ */
.drawer__list {
  list-style: none;
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
}

.drawer__list-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.75rem;
  border-radius: 8px;
  cursor: pointer;
  font-size: 0.9rem;
  transition: background 0.2s;
}

.drawer__list-item:hover {
  background: rgba(255,255,255,0.04);
}

.drawer__list-item .danger {
  color: var(--danger);
}

.drawer__list-icon {
  font-size: 1.1rem;
  width: 24px;
  text-align: center;
}

/* â”€â”€ Settings controls â”€â”€ */
.drawer__section {
  margin-bottom: 1.5rem;
}

.drawer__label {
  display: block;
  font-size: 0.75rem;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.06em;
  color: var(--muted);
  margin-bottom: 0.625rem;
}

.drawer__toggle-row {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.5rem 0;
  font-size: 0.875rem;
}

.toggle {
  width: 40px;
  height: 22px;
  border-radius: 11px;
  background: rgba(255,255,255,0.1);
  cursor: pointer;
  position: relative;
  transition: background 0.2s;
  flex-shrink: 0;
}

.toggle::after {
  content: '';
  position: absolute;
  top: 3px;
  left: 3px;
  width: 16px;
  height: 16px;
  border-radius: 50%;
  background: var(--muted);
  transition: transform 0.2s, background 0.2s;
}

.toggle.active {
  background: rgba(56,189,248,0.25);
}

.toggle.active::after {
  transform: translateX(18px);
  background: var(--accent);
}

.drawer__select {
  width: 100%;
  background: rgba(255,255,255,0.04);
  border: 1px solid var(--border);
  color: var(--text);
  padding: 0.5rem 0.75rem;
  border-radius: 8px;
  font-size: 0.875rem;
  cursor: pointer;
}

/* â”€â”€ Nav items â”€â”€ */
.drawer__nav {
  display: flex;
  flex-direction: column;
  gap: 0.125rem;
}

.drawer__nav-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  padding: 0.625rem 0.75rem;
  border-radius: 8px;
  color: var(--muted);
  text-decoration: none;
  font-size: 0.9rem;
  transition: background 0.2s, color 0.2s;
}

.drawer__nav-item:hover,
.drawer__nav-item.active {
  background: rgba(56,189,248,0.08);
  color: var(--text);
}

.drawer__nav-item.active {
  color: var(--accent);
}

.drawer__nav-icon {
  font-size: 1rem;
  width: 20px;
  text-align: center;
}

.drawer__nav-divider {
  height: 1px;
  background: var(--border);
  margin: 0.5rem 0;
}

/* â”€â”€ Notifications â”€â”€ */
.notification {
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 0.875rem;
  border-radius: 8px;
  border: 1px solid var(--border);
  min-width: 220px;
  background: rgba(255,255,255,0.02);
}

.notification p {
  font-size: 0.8rem;
  color: var(--muted);
  margin-top: 0.2rem;
}

.notification strong {
  font-size: 0.875rem;
}

.notification__icon {
  font-size: 1rem;
  flex-shrink: 0;
  width: 28px;
  height: 28px;
  border-radius: 50%;
  display: grid;
  place-items: center;
}

.notification--success .notification__icon { background: rgba(74,222,128,0.12); color: var(--success); }
.notification--warning .notification__icon { background: rgba(251,191,36,0.12); color: var(--warning); }
.notification--info .notification__icon    { background: rgba(96,165,250,0.12); color: var(--info); }

/* â”€â”€ Buttons â”€â”€ */
.btn-primary {
  display: block;
  width: 100%;
  padding: 0.625rem;
  background: var(--accent);
  color: #050910;
  border: none;
  border-radius: 8px;
  font-size: 0.875rem;
  font-weight: 600;
  cursor: pointer;
  transition: opacity 0.2s;
}

.btn-primary:hover { opacity: 0.85; }

