*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

body {
  font-family: Inter, system-ui, sans-serif;
  background: #050910;
  color: #f2f6ff;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
}

.demo { text-align: center; }
.demo-title { font-size: 1.5rem; font-weight: 800; margin-bottom: 0.375rem; }
.demo-sub { color: #475569; font-size: 0.875rem; margin-bottom: 2rem; }
.triggers { display: flex; gap: 1rem; justify-content: center; flex-wrap: wrap; }

/* â”€â”€ Buttons â”€â”€ */
.btn {
  display: inline-flex; align-items: center; gap: 0.5rem;
  padding: 0.625rem 1.25rem; border-radius: 0.625rem; font-size: 0.875rem;
  font-weight: 600; border: 1px solid transparent; cursor: pointer;
  transition: background 0.15s, opacity 0.15s;
}
.btn--primary { background: #38bdf8; color: #0f172a; }
.btn--primary:hover { background: #7dd3fc; }
.btn--ghost { background: rgba(255,255,255,0.06); color: #94a3b8; border-color: rgba(255,255,255,0.1); }
.btn--ghost:hover { background: rgba(255,255,255,0.1); }
.btn--danger { background: #ef4444; color: #fff; }
.btn--danger:hover { background: #f87171; }

/* â”€â”€ Backdrop â”€â”€ */
.modal-backdrop {
  position: fixed; inset: 0;
  background: rgba(0,0,0,0.75);
  backdrop-filter: blur(4px);
  display: flex; align-items: center; justify-content: center;
  padding: 1rem;
  z-index: 1000;
  opacity: 0;
  pointer-events: none;
  transition: opacity 0.2s;
}
.modal-backdrop.is-open { opacity: 1; pointer-events: auto; }

/* â”€â”€ Modal â”€â”€ */
.modal {
  background: #0d1117;
  border: 1px solid rgba(255,255,255,0.1);
  border-radius: 1rem;
  width: 100%;
  max-width: 460px;
  transform: translateY(12px) scale(0.97);
  transition: transform 0.25s cubic-bezier(0.34,1.56,0.64,1);
  overflow: hidden;
}
.modal-backdrop.is-open .modal { transform: none; }
.modal--sm { max-width: 360px; }
.modal--lg { max-width: 560px; }

/* â”€â”€ Modal header â”€â”€ */
.modal-header {
  display: flex; align-items: center; justify-content: space-between;
  padding: 1.25rem 1.5rem 0;
}
.modal-header--alert { justify-content: center; padding-top: 1.75rem; }
.modal-title { font-size: 1.05rem; font-weight: 700; }
.modal-close {
  background: none; border: none; color: #475569; cursor: pointer; font-size: 1.4rem;
  line-height: 1; padding: 0.25rem; border-radius: 0.375rem; transition: color 0.15s, background 0.15s;
}
.modal-close:hover { color: #f2f6ff; background: rgba(255,255,255,0.06); }

.alert-icon-wrap {
  font-size: 2rem; width: 56px; height: 56px; border-radius: 50%;
  background: rgba(245,158,11,0.15); color: #f59e0b;
  display: flex; align-items: center; justify-content: center;
}

/* â”€â”€ Modal body â”€â”€ */
.modal-body {
  padding: 1rem 1.5rem 1.25rem;
  font-size: 0.875rem; color: #94a3b8; line-height: 1.6;
}
.modal-body--center { text-align: center; }
.modal-body--center .modal-title { margin-bottom: 0.625rem; }

.form-group { display: flex; flex-direction: column; gap: 0.375rem; margin-bottom: 1rem; }
.form-label { font-size: 0.78rem; font-weight: 500; color: #94a3b8; }
.input {
  padding: 0.625rem 0.875rem; background: #050910; border: 1px solid rgba(255,255,255,0.1);
  border-radius: 0.5rem; color: #f2f6ff; font-size: 0.875rem; font-family: inherit; outline: none;
  transition: border-color 0.15s;
}
.input:focus { border-color: #38bdf8; }

/* â”€â”€ Modal footer â”€â”€ */
.modal-footer {
  display: flex; justify-content: flex-end; gap: 0.75rem;
  padding: 0 1.5rem 1.5rem;
}
.modal-footer--center { justify-content: center; }

