(function () {
  var FOCUSABLE = 'a[href],button:not([disabled]),input,select,textarea,[tabindex]:not([tabindex="-1"])';

  function openModal(backdrop) {
    backdrop.classList.add("is-open");
    document.body.style.overflow = "hidden";
    var first = backdrop.querySelector(FOCUSABLE);
    if (first) first.focus();
  }

  function closeModal(backdrop) {
    backdrop.classList.remove("is-open");
    document.body.style.overflow = "";
  }

  // Open
  document.querySelectorAll("[data-modal]").forEach(function (btn) {
    btn.addEventListener("click", function () {
      var target = document.getElementById(btn.dataset.modal);
      if (target) openModal(target);
    });
  });

  // Close on Ã— and Cancel
  document.querySelectorAll(".modal-close, .modal-cancel").forEach(function (btn) {
    btn.addEventListener("click", function () {
      var backdrop = btn.closest(".modal-backdrop");
      if (backdrop) closeModal(backdrop);
    });
  });

  // Close on backdrop click
  document.querySelectorAll(".modal-backdrop").forEach(function (backdrop) {
    backdrop.addEventListener("click", function (e) {
      if (e.target === backdrop) closeModal(backdrop);
    });
  });

  // Escape key
  document.addEventListener("keydown", function (e) {
    if (e.key === "Escape") {
      var open = document.querySelector(".modal-backdrop.is-open");
      if (open) closeModal(open);
    }
  });

  // Focus trap
  document.querySelectorAll(".modal-backdrop").forEach(function (backdrop) {
    backdrop.addEventListener("keydown", function (e) {
      if (e.key !== "Tab") return;
      var focusable = Array.from(backdrop.querySelectorAll(FOCUSABLE));
      if (!focusable.length) return;
      var first = focusable[0], last = focusable[focusable.length - 1];
      if (e.shiftKey && document.activeElement === first) { e.preventDefault(); last.focus(); }
      else if (!e.shiftKey && document.activeElement === last) { e.preventDefault(); first.focus(); }
    });
  });
}());

snippets.css:
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg: #050910;
  --card: #0d1117;
  --card2: #0a0f1a;
  --border: rgba(255, 255, 255, 0.08);
  --text: #f2f6ff;
  --muted: #475569;
  --accent: #38bdf8;
  --row-hover: rgba(255, 255, 255, 0.025);
  --row-alt:   rgba(255, 255, 255, 0.012);
  --sorted-col: rgba(56, 189, 248, 0.04);
  --danger: #f87171;
  --success: #4ade80;
  --warning: #fbbf24;
  --radius: 12px;
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: system-ui, -apple-system, sans-serif;
  min-height: 100vh;
  padding: 2rem 1rem;
}

.page {
  max-width: 1040px;
  margin: 0 auto;
}

/* â”€â”€ Table shell â”€â”€ */
.table-shell {
  background: var(--card);
  border: 1px solid var(--border);
  border-radius: var(--radius);
  overflow: hidden;
}

/* â”€â”€ Toolbar â”€â”€ */
.toolbar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.875rem 1.125rem;
  border-bottom: 1px solid var(--border);
  gap: 1rem;
  flex-wrap: wrap;
}

.toolbar-left { flex: 1; min-width: 180px; }

.search-wrap {
  position: relative;
  max-width: 300px;
}

.search-icon {
  position: absolute;
  left: 0.75rem;
  top: 50%;
  transform: translateY(-50%);
  color: var(--muted);
  font-size: 1.1rem;
  pointer-events: none;
}

.search-input {
  width: 100%;
  background: var(--card2);
  border: 1px solid var(--border);
  border-radius: 8px;
  color: var(--text);
  padding: 0.45rem 0.75rem 0.45rem 2.25rem;
  font-size: 0.855rem;
  outline: none;
  transition: border-color 0.2s, box-shadow 0.2s;
}

.search-input:focus {
  border-color: var(--accent);
  box-shadow: 0 0 0 2px rgba(56,189,248,0.12);
}

.search-input::placeholder { color: var(--muted); }

/* Column toggle button */
.col-toggle-wrap { position: relative; }

.icon-btn {
  display: inline-flex;
  align-items: center;
  gap: 0.4rem;
  background: var(--card2);
  border: 1px solid var(--border);
  color: var(--muted);
  padding: 0.45rem 0.875rem;
  border-radius: 8px;
  font-size: 0.82rem;
  cursor: pointer;
  transition: color 0.2s, border-color 0.2s;
}

.icon-btn:hover { color: var(--text); border-color: rgba(255,255,255,0.18); }

.col-dropdown {
  position: absolute;
  right: 0;
  top: calc(100% + 6px);
  background: #111827;
  border: 1px solid var(--border);
  border-radius: 8px;
  padding: 0.4rem;
  min-width: 160px;
  z-index: 100;
  box-shadow: 0 8px 30px rgba(0,0,0,0.4);
}

.col-item {
  display: flex;
  align-items: center;
  gap: 0.6rem;
  padding: 0.45rem 0.6rem;
  border-radius: 6px;
  cursor: pointer;
  font-size: 0.835rem;
  color: var(--text);
  transition: background 0.15s;
}

.col-item:hover { background: rgba(255,255,255,0.05); }

.col-item input[type="checkbox"] {
  accent-color: var(--accent);
  width: 14px;
  height: 14px;
  cursor: pointer;
}

/* â”€â”€ Bulk bar â”€â”€ */
.bulk-bar {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.625rem 1.125rem;
  background: rgba(56,189,248,0.06);
  border-bottom: 1px solid rgba(56,189,248,0.15);
  animation: slide-down 0.18s ease;
}

@keyframes slide-down {
  from { opacity: 0; transform: translateY(-4px); }
  to   { opacity: 1; transform: translateY(0); }
}

.bulk-count {
  font-size: 0.85rem;
  font-weight: 600;
  color: var(--accent);
}

.bulk-actions { display: flex; gap: 0.5rem; }

.bulk-btn {
  padding: 0.35rem 0.875rem;
  border-radius: 6px;
  font-size: 0.82rem;
  font-weight: 500;
  cursor: pointer;
  border: 1px solid var(--border);
  background: rgba(255,255,255,0.05);
  color: var(--text);
  transition: background 0.2s, border-color 0.2s;
}

.bulk-btn:hover { background: rgba(255,255,255,0.1); }
.bulk-btn--danger { color: var(--danger); border-color: rgba(248,113,113,0.3); }
.bulk-btn--danger:hover { background: rgba(248,113,113,0.1); }

/* â”€â”€ Table wrap â”€â”€ */
.table-wrap {
  overflow-x: auto;
  scrollbar-width: thin;
  scrollbar-color: rgba(255,255,255,0.1) transparent;
}

.table-wrap::-webkit-scrollbar { height: 5px; }
.table-wrap::-webkit-scrollbar-track { background: transparent; }
.table-wrap::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.1); border-radius: 2px; }

/* â”€â”€ Data table â”€â”€ */
.data-table {
  width: 100%;
  border-collapse: collapse;
  font-size: 0.855rem;
}

/* Sticky header */
.data-table thead {
  position: sticky;
  top: 0;
  z-index: 10;
  background: var(--card2);
}

.data-table th {
  padding: 0.625rem 0.875rem;
  text-align: left;
  font-size: 0.75rem;
  font-weight: 600;
  color: var(--muted);
  border-bottom: 1px solid var(--border);
  white-space: nowrap;
  user-select: none;
}

.data-table th.sortable {
  cursor: pointer;
  transition: color 0.2s;
}

.data-table th.sortable:hover { color: var(--text); }

.th-inner {
  display: inline-flex;
  align-items: center;
  gap: 0.3rem;
}

.sort-icon {
  font-size: 0.7rem;
  color: var(--muted);
  transition: opacity 0.2s;
}

.sort-icon--active { color: var(--accent); }

/* Sorted column highlight */
.data-table td.col-sorted,
.data-table th.col-sorted {
  background: var(--sorted-col);
}

/* â”€â”€ Rows â”€â”€ */
.data-table tbody tr {
  border-bottom: 1px solid var(--border);
  transition: background 0.15s;
}

.data-table tbody tr:nth-child(even) { background: var(--row-alt); }
.data-table tbody tr:hover           { background: var(--row-hover); }
.data-table tbody tr.selected        { background: rgba(56,189,248,0.06); }

.data-table td {
  padding: 0.75rem 0.875rem;
  color: var(--text);
  vertical-align: middle;
}

/* Checkbox column */
.col-check { width: 40px; }

.data-table input[type="checkbox"] {
  accent-color: var(--accent);
  width: 15px;
  height: 15px;
  cursor: pointer;
}

/* â”€â”€ Name cell â”€â”€ */
.user-name {
  font-weight: 500;
}

/* â”€â”€ Email cell â”€â”€ */
.user-email {
  color: var(--muted);
  font-size: 0.82rem;
}

/* â”€â”€ Role badge â”€â”€ */
.role-badge {
  display: inline-block;
  padding: 0.2em 0.6em;
  border-radius: 4px;
  font-size: 0.72rem;
  font-weight: 600;
  background: rgba(255,255,255,0.06);
  color: var(--text);
}

/* â”€â”€ Status badges â”€â”€ */
.status-badge {
  display: inline-flex;
  align-items: center;
  gap: 0.3rem;
  padding: 0.2em 0.6em;
  border-radius: 999px;
  font-size: 0.73rem;
  font-weight: 600;
}

.status-badge::before {
  content: '';
  width: 5px;
  height: 5px;
  border-radius: 50%;
}

.status-badge--active  { background: rgba(74,222,128,0.1); color: var(--success); }
.status-badge--active::before  { background: var(--success); }
.status-badge--inactive { background: rgba(71,85,105,0.18); color: var(--muted); }
.status-badge--inactive::before { background: var(--muted); }
.status-badge--pending { background: rgba(251,191,36,0.1); color: var(--warning); }
.status-badge--pending::before { background: var(--warning); }

/* â”€â”€ Actions â”€â”€ */
.actions-cell {
  display: flex;
  gap: 0.375rem;
  align-items: center;
}

.action-btn {
  width: 28px;
  height: 28px;
  border-radius: 6px;
  background: none;
  border: 1px solid transparent;
  color: var(--muted);
  cursor: pointer;
  display: grid;
  place-items: center;
  font-size: 0.9rem;
  transition: background 0.15s, color 0.15s, border-color 0.15s;
}

.action-btn:hover {
  background: rgba(255,255,255,0.06);
  border-color: var(--border);
  color: var(--text);
}

.action-btn--delete:hover {
  background: rgba(248,113,113,0.1);
  border-color: rgba(248,113,113,0.3);
  color: var(--danger);
}

/* â”€â”€ Empty state â”€â”€ */
.empty-row td {
  text-align: center;
  padding: 3rem;
  color: var(--muted);
  font-size: 0.875rem;
}

/* â”€â”€ Pagination â”€â”€ */
.pagination {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0.75rem 1.125rem;
  border-top: 1px solid var(--border);
  flex-wrap: wrap;
  gap: 0.5rem;
}

.pagination-info {
  font-size: 0.8rem;
  color: var(--muted);
}

.page-btns {
  display: flex;
  gap: 0.25rem;
  align-items: center;
}

.page-btn {
  min-width: 30px;
  height: 30px;
  border-radius: 6px;
  background: none;
  border: 1px solid var(--border);
  color: var(--muted);
  font-size: 0.82rem;
  cursor: pointer;
  display: grid;
  place-items: center;
  padding: 0 0.25rem;
  transition: background 0.15s, color 0.15s, border-color 0.15s;
}

.page-btn:hover:not([disabled]) {
  background: rgba(255,255,255,0.06);
  color: var(--text);
  border-color: rgba(255,255,255,0.18);
}

.page-btn.active {
  background: var(--accent);
  border-color: var(--accent);
  color: #050910;
  font-weight: 700;
}

.page-btn[disabled] {
  opacity: 0.35;
  cursor: not-allowed;
}

.page-ellipsis {
  color: var(--muted);
  font-size: 0.8rem;
  padding: 0 0.25rem;
}

