/* â”€â”€ Toggle component â”€â”€ */
.toggle {
  --toggle-color: #38bdf8;
  --toggle-w: 2.75rem;
  --toggle-h: 1.5rem;
  --thumb-size: 1.125rem;
  --thumb-offset: calc((var(--toggle-h) - var(--thumb-size)) / 2);

  position: relative;
  display: inline-block;
  flex-shrink: 0;
  cursor: pointer;
}

/* Hide native checkbox */
.toggle input {
  position: absolute;
  opacity: 0;
  width: 0;
  height: 0;
}

/* Track */
.toggle__track {
  display: block;
  width: var(--toggle-w);
  height: var(--toggle-h);
  background: rgba(255,255,255,0.1);
  border-radius: 999px;
  transition: background 0.25s ease;
  position: relative;
}

/* Thumb */
.toggle__thumb {
  position: absolute;
  top: var(--thumb-offset);
  left: var(--thumb-offset);
  width: var(--thumb-size);
  height: var(--thumb-size);
  background: #fff;
  border-radius: 50%;
  box-shadow: 0 1px 4px rgba(0,0,0,0.3);
  transition:
    transform 0.28s cubic-bezier(0.34,1.56,0.64,1),
    background 0.25s ease;
}

/* Checked state */
.toggle input:checked + .toggle__track {
  background: var(--toggle-color);
}

.toggle input:checked + .toggle__track .toggle__thumb {
  transform: translateX(calc(var(--toggle-w) - var(--thumb-size) - var(--thumb-offset) * 2));
}

/* Focus ring */
.toggle input:focus-visible + .toggle__track {
  outline: 2px solid var(--toggle-color);
  outline-offset: 2px;
}

/* Disabled state */
.toggle input:disabled + .toggle__track {
  opacity: 0.35;
  cursor: not-allowed;
}

.toggle input:disabled ~ * { cursor: not-allowed; }
.toggle:has(input:disabled) { cursor: not-allowed; }

