  font-size: 0.7rem;
  color: var(--lime);
  margin-bottom: 1rem;
}

.lead {
  color: var(--muted);
  font-size: 1.1rem;
  line-height: 1.6;
  max-width: 540px;
}

.muted {
  color: var(--muted);
}

.hero-actions {
  margin-top: 2rem;
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.primary,
.ghost {
  border: none;
  padding: 0.9rem 1.6rem;
  border-radius: 999px;
  font-size: 0.95rem;
  font-weight: 600;
  cursor: pointer;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.primary {
  background: var(--accent);
  color: #120d0b;
  box-shadow: 0 20px 40px rgba(242, 107, 58, 0.3);
}

.ghost {
  background: transparent;
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: var(--text);
}

.primary:hover,
.ghost:hover {
  transform: translateY(-2px);
}

.status {
  margin-top: 2rem;
  display: flex;
  align-items: center;
  gap: 0.75rem;
  font-size: 0.85rem;
  color: var(--muted);
}

.status .dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: var(--lime);
  box-shadow: 0 0 12px rgba(182, 255, 87, 0.8);
}

.hero-panel {
  background: var(--glass);
  border: 1px solid var(--stroke);
  border-radius: 24px;
  padding: 2rem;
  backdrop-filter: blur(16px);
}

.panel-title {
  font-family: "Syne", sans-serif;
  font-weight: 600;
  margin-bottom: 1.5rem;
}

.panel-grid {
  display: grid;
  gap: 1rem;
}

.panel-card {
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
  border-radius: 16px;
  padding: 1rem;
  display: grid;
  gap: 0.4rem;
}

.section {
  margin-top: 5rem;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-end;
  gap: 2rem;
  flex-wrap: wrap;
}

.section-header h2 {
  font-family: "Syne", sans-serif;
  font-size: clamp(1.8rem, 3vw, 2.6rem);
}

.scheme-controls {
  display: flex;
  align-items: center;
  gap: 1rem;
  color: var(--muted);
  font-size: 0.85rem;
}

.scheme-buttons {
  display: flex;
  gap: 0.5rem;
}

.scheme-btn {
  border-radius: 999px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  background: rgba(255, 255, 255, 0.08);
  color: var(--text);
  padding: 0.4rem 0.9rem;
  font-size: 0.75rem;
  cursor: pointer;
}

.scheme-btn.active {
  background: var(--accent-soft);
  border-color: rgba(242, 107, 58, 0.5);
  color: var(--accent);
}

.projects {
  margin-top: 2.5rem;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
  gap: 1.5rem;
}

.project-card {
  background: var(--glass);
  border: 1px solid var(--stroke);
  border-radius: 24px;
  padding: 1.5rem;
  display: grid;
  gap: 1rem;
  min-height: 220px;
  backdrop-filter: blur(16px);
}

.project-meta {
  display: flex;
  justify-content: space-between;
  color: var(--muted);
  font-size: 0.75rem;
}

.project-card h3 {
  font-family: "Syne", sans-serif;
  font-size: 1.3rem;
}

.tag-list {
  display: flex;
  gap: 0.5rem;
  flex-wrap: wrap;
}

.tag {
  border: 1px solid rgba(255, 255, 255, 0.18);
  border-radius: 999px;
  padding: 0.2rem 0.7rem;
  font-size: 0.7rem;
  color: var(--muted);
}

.services {
  margin-top: 2.5rem;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
  gap: 1.5rem;
}

.services article {
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.12);
  border-radius: 20px;
  padding: 1.5rem;
  min-height: 180px;
}

.split {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 3rem;
}

.facts {
  list-style: none;
  margin-top: 1.5rem;
  display: grid;
  gap: 0.6rem;
  color: var(--muted);
}

.timeline {
  background: var(--glass);
  border: 1px solid var(--stroke);
  border-radius: 24px;
  padding: 2rem;
  display: grid;
  gap: 1.2rem;
  backdrop-filter: blur(16px);
}

.timeline-item {
  display: grid;
  gap: 0.3rem;
  padding-bottom: 1rem;
  border-bottom: 1px solid rgba(255, 255, 255, 0.08);
}

.timeline-item:last-child {
  border-bottom: none;
  padding-bottom: 0;
}

.contact-card {
  background: rgba(15, 18, 32, 0.7);
  border: 1px solid rgba(255, 255, 255, 0.15);
  border-radius: 24px;
  padding: 2.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 2rem;
}

.contact-actions {
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
}

.site-footer {
  position: relative;
  z-index: 2;
  padding: 2rem 5vw 3rem;
  display: flex;
  justify-content: space-between;
  color: var(--muted);
  font-size: 0.85rem;
}

.custom-cursor {
  position: fixed;
  top: 0;
  left: 0;
  width: 46px;
  height: 46px;
  border-radius: 50%;
  border: 1px solid rgba(255, 255, 255, 0.35);
  background: radial-gradient(
    circle at 30% 30%,
    rgba(255, 255, 255, 0.45),
    rgba(255, 255, 255, 0.05) 55%,
    rgba(0, 0, 0, 0.2) 100%
  );
  box-shadow: 0 0 18px rgba(255, 255, 255, 0.2),
    inset 0 0 12px rgba(255, 255, 255, 0.2);
  mix-blend-mode: screen;
  pointer-events: none;
  z-index: 1000;
  transform: translate(-50%, -50%);
  transition: width 0.2s ease, height 0.2s ease, border-color 0.2s ease,
    box-shadow 0.2s ease, background 0.2s ease;
}

.custom-cursor.is-active {
  width: 64px;
  height: 64px;
  border-color: rgba(255, 255, 255, 0.6);
  box-shadow: 0 0 30px rgba(255, 255, 255, 0.4),
    inset 0 0 16px rgba(255, 255, 255, 0.35);
  background: radial-gradient(
    circle at 30% 30%,
    rgba(255, 255, 255, 0.6),
    rgba(242, 107, 58, 0.12) 55%,
    rgba(0, 0, 0, 0.2) 100%
  );
}

.custom-cursor.is-cta {
  width: 72px;
  height: 72px;
  border-color: rgba(182, 255, 87, 0.8);
  box-shadow: 0 0 36px rgba(182, 255, 87, 0.35),
    inset 0 0 20px rgba(255, 255, 255, 0.35);
  background: radial-gradient(
    circle at 30% 30%,
    rgba(182, 255, 87, 0.7),
    rgba(61, 224, 195, 0.2) 60%,
    rgba(0, 0, 0, 0.2) 100%
  );
}

@media (max-width: 900px) {
  .nav-pill {
    flex-direction: column;
    align-items: flex-start;
    border-radius: 28px;
    gap: 1rem;
  }

  .nav-links {
    flex-wrap: wrap;
    gap: 0.9rem;
    position: static;
    transform: none;
  }

  .contact-card {
    flex-direction: column;
    align-items: flex-start;
  }
}

@media (max-width: 640px) {
  .site-header {
    top: 1rem;
  }

  .hero {
    padding-top: 3rem;
  }

  .scheme-controls {
    width: 100%;
    justify-content: space-between;
  }
}

