class TouchTexture {
  constructor() {
    this.size = 64;
    this.width = this.size;
    this.height = this.size;
    this.maxAge = 64;
    this.radius = 0.2 * this.size;
    this.trail = [];
    this.last = null;
    this.initTexture();
  }

  initTexture() {
    this.canvas = document.createElement("canvas");
    this.canvas.width = this.width;
    this.canvas.height = this.height;
    this.ctx = this.canvas.getContext("2d");
    this.ctx.fillStyle = "black";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
    this.texture = new THREE.Texture(this.canvas);
  }

  addTouch(point) {
    let force = 0;
    let vx = 0;
    let vy = 0;
    if (this.last) {
      const dx = point.x - this.last.x;
      const dy = point.y - this.last.y;
      const dd = dx * dx + dy * dy;
      const d = Math.sqrt(dd);
      vx = dx / d || 0;
      vy = dy / d || 0;
      force = Math.min(dd * 20000, 1.5);
    }
    this.last = { x: point.x, y: point.y };
    this.trail.push({ x: point.x, y: point.y, age: 0, force, vx, vy });
  }

  update() {
    this.clear();
    for (let i = this.trail.length - 1; i >= 0; i--) {
      const point = this.trail[i];
      point.age++;
      if (point.age > this.maxAge) {
        this.trail.splice(i, 1);
      } else {
        this.drawPoint(point);
      }
    }
    this.texture.needsUpdate = true;
  }

  clear() {
    this.ctx.fillStyle = "black";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
  }

  drawPoint(point) {
    const pos = {
      x: point.x * this.width,
      y: (1 - point.y) * this.height
    };

    const intensity = 1 - point.age / this.maxAge;
    const radius = this.radius;
    const color = `rgba(${(point.vx + 1) * 120}, ${(point.vy + 1) * 120}, ${intensity * 255}, ${intensity})`;

    this.ctx.beginPath();
    this.ctx.fillStyle = color;
    this.ctx.shadowBlur = radius * 1.2;
    this.ctx.shadowColor = color;
    this.ctx.arc(pos.x, pos.y, radius, 0, Math.PI * 2);
    this.ctx.fill();
  }
}

class GradientScene {
  constructor() {
    this.renderer = new THREE.WebGLRenderer({
      antialias: true,
      alpha: true
    });
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    this.renderer.domElement.id = "webGLApp";
    document.body.appendChild(this.renderer.domElement);

    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(
      45,
      window.innerWidth / window.innerHeight,
      0.1,
      100
    );
    this.camera.position.z = 6;

    this.clock = new THREE.Clock();
    this.touchTexture = new TouchTexture();
    this.uniforms = {
      uTime: { value: 0 },
      uResolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) },
      uMouse: { value: new THREE.Vector2(0.5, 0.5) },
      uTouch: { value: this.touchTexture.texture },
      uColorA: { value: new THREE.Color("#f26b3a") },
      uColorB: { value: new THREE.Color("#3de0c3") },
      uColorC: { value: new THREE.Color("#0a1222") }
    };

    this.colorSchemes = [
      {
        a: "#f26b3a",
        b: "#3de0c3",
        c: "#0a1222"
      },
      {
        a: "#1d3b4f",
        b: "#58ffd0",
        c: "#05060a"
      },
      {
        a: "#ffb347",
        b: "#ffe66d",
        c: "#0b1b16"
      },
      {
        a: "#ffdf3a",
        b: "#ff2d55",
        c: "#1a0b12"
      },
      {
        a: "#8b5bff",
        b: "#57ff6b",
        c: "#0a0e16"
      }
    ];

    this.initMesh();
    this.bindEvents();
    this.render();
  }

  initMesh() {
    const geometry = new THREE.PlaneGeometry(10, 6, 1, 1);
    const material = new THREE.ShaderMaterial({
      uniforms: this.uniforms,
      vertexShader: `
        varying vec2 vUv;
        void main() {
          vUv = uv;
          gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
        }
      `,
      fragmentShader: `
        precision highp float;
        uniform float uTime;
        uniform vec2 uResolution;
        uniform vec2 uMouse;
        uniform sampler2D uTouch;
        uniform vec3 uColorA;
        uniform vec3 uColorB;
        uniform vec3 uColorC;
        varying vec2 vUv;

        float noise(vec2 p) {
          return sin(p.x) * sin(p.y);
        }

        void main() {
          vec2 uv = vUv;
          vec2 st = uv * 2.0 - 1.0;
          st.x *= uResolution.x / uResolution.y;

          vec4 touch = texture2D(uTouch, uv);
          float ripple = (touch.r + touch.g) * 0.35;
          uv += (touch.rg - 0.5) * 0.12;

          float t = uTime * 0.4;
          float wave = sin(st.x * 1.8 + t) * 0.15 + cos(st.y * 2.2 - t) * 0.1;
          float swirl = sin(length(st) * 2.4 - t * 1.3) * 0.2;

          float mask = smoothstep(0.9, 0.1, length(st));
          vec3 color = mix(uColorC, uColorA, wave + 0.5);
          color = mix(color, uColorB, swirl + 0.5);
          color = mix(uColorC, color, mask + ripple);

          float grain = noise(uv * uResolution * 0.01 + uTime);
          color += grain * 0.05;

          float luma = dot(color, vec3(0.299, 0.587, 0.114));
          color = mix(vec3(luma), color, 1.2);

          gl_FragColor = vec4(color, 1.0);
        }
      `
    });

    const mesh = new THREE.Mesh(geometry, material);
    this.scene.add(mesh);
  }

  setScheme(index) {
    const scheme = this.colorSchemes[index];
    if (!scheme) return;
    this.uniforms.uColorA.value.set(scheme.a);
    this.uniforms.uColorB.value.set(scheme.b);
    this.uniforms.uColorC.value.set(scheme.c);
  }

  bindEvents() {
    window.addEventListener("resize", () => this.onResize());
    window.addEventListener("mousemove", (event) => this.onPointerMove(event));
    window.addEventListener("touchmove", (event) => this.onTouchMove(event));
  }

  onPointerMove(event) {
    const x = event.clientX / window.innerWidth;
    const y = 1 - event.clientY / window.innerHeight;
    this.uniforms.uMouse.value.set(x, y);
    this.touchTexture.addTouch({ x, y });
  }

  onTouchMove(event) {
    const touch = event.touches[0];
    if (!touch) return;
    this.onPointerMove({ clientX: touch.clientX, clientY: touch.clientY });
  }

  onResize() {
    this.camera.aspect = window.innerWidth / window.innerHeight;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.uniforms.uResolution.value.set(window.innerWidth, window.innerHeight);
  }

  render() {
    const delta = this.clock.getDelta();
    this.uniforms.uTime.value += delta;
    this.touchTexture.update();
    this.renderer.render(this.scene, this.camera);
    requestAnimationFrame(() => this.render());
  }
}

const gradientScene = new GradientScene();

const schemeButtons = document.querySelectorAll(".scheme-btn");
schemeButtons.forEach((btn) => {
  btn.addEventListener("click", () => {
    schemeButtons.forEach((button) => button.classList.remove("active"));
    btn.classList.add("active");
    gradientScene.setScheme(parseInt(btn.dataset.scheme, 10));
  });
});

const cursor = document.getElementById("customCursor");
let cursorX = window.innerWidth / 2;
let cursorY = window.innerHeight / 2;
let targetX = cursorX;
let targetY = cursorY;

function animateCursor() {
  cursorX += (targetX - cursorX) * 0.2;
  cursorY += (targetY - cursorY) * 0.2;
  cursor.style.left = `${cursorX}px`;
  cursor.style.top = `${cursorY}px`;
  requestAnimationFrame(animateCursor);
}

window.addEventListener("mousemove", (event) => {
  targetX = event.clientX;
  targetY = event.clientY;
});

window.addEventListener("touchmove", (event) => {
  const touch = event.touches[0];
  if (!touch) return;
  targetX = touch.clientX;
  targetY = touch.clientY;
});

document.querySelectorAll("button, a").forEach((el) => {
  el.addEventListener("mouseenter", () => cursor.classList.add("is-active"));
  el.addEventListener("mouseleave", () => cursor.classList.remove("is-active"));
});

document.querySelectorAll(".primary, .cta").forEach((el) => {
  el.addEventListener("mouseenter", () => cursor.classList.add("is-cta"));
  el.addEventListener("mouseleave", () => cursor.classList.remove("is-cta"));
});

animateCursor();

snippets.css:
:root {
  --bg: #070a12;
  --text: #f0f4fb;
  --panel: #121a2b;
  --border: #263249;
  --accent: #86e8ff;
  --muted: #8a95a8;
  --purple: #ae52ff;
  --pink: #ff40d6;
  --hue-shift: 0deg;
}

* { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
}

/* Fixed layers */
#velocity-canvas, #hero-canvas {
  position: fixed;
  inset: 0;
  pointer-events: none;
  z-index: 0;
}

#velocity-canvas { z-index: 2; }
#hero-canvas { z-index: 1; }

section { position: relative; z-index: 3; }

/* â”€â”€ Hero â”€â”€ */
.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem 2rem;
  background: radial-gradient(ellipse at 50% 40%, rgba(134, 232, 255, 0.04) 0%, transparent 65%);
}

.hero-content {
  max-width: 680px;
  text-align: center;
}

.hero-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  border: 1px solid rgba(134, 232, 255, 0.3);
  border-radius: 20px;
  padding: 0.3rem 1rem;
  margin-bottom: 1.5rem;
}

.hero h1 {
  font-size: 4rem;
  font-weight: 900;
  line-height: 1.05;
  margin-bottom: 1.5rem;
  letter-spacing: -2px;
}

.hero-desc {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2.5rem;
  line-height: 1.8;
}

/* Speed indicator */
.speed-indicator {
  display: flex;
  align-items: center;
  gap: 0.8rem;
  max-width: 320px;
  margin: 0 auto;
  padding: 0.8rem 1.2rem;
  background: rgba(18, 26, 43, 0.8);
  border: 1px solid var(--border);
  border-radius: 30px;
  backdrop-filter: blur(8px);
}

.speed-label {
  font-size: 0.72rem;
  font-weight: 700;
  letter-spacing: 1px;
  text-transform: uppercase;
  color: var(--muted);
  white-space: nowrap;
}

.speed-bar-track {
  flex: 1;
  height: 4px;
  background: var(--border);
  border-radius: 2px;
  overflow: hidden;
}

.speed-bar-fill {
  height: 100%;
  width: 0%;
  background: linear-gradient(to right, var(--accent), var(--purple));
  border-radius: 2px;
  transition: width 0.1s linear;
}

.speed-value {
  font-size: 0.8rem;
  font-weight: 700;
  color: var(--accent);
  min-width: 28px;
  text-align: right;
  font-variant-numeric: tabular-nums;
}

/* â”€â”€ Gradient Text â”€â”€ */
.gradient-text {
  background: linear-gradient(
    90deg,
    hsl(var(--hue-shift), 80%, 55%),
    hsl(calc(var(--hue-shift) + 70deg), 80%, 55%),
    hsl(calc(var(--hue-shift) + 140deg), 80%, 55%)
  );
  background-size: 200% 100%;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: gradShift 6s linear infinite;
}

@keyframes gradShift {
  0% { background-position: 0% center; }
  100% { background-position: 200% center; }
}

@supports not (-webkit-background-clip: text) {
  .gradient-text { color: var(--accent); background: none; }
}

/* â”€â”€ Depth Section â”€â”€ */
.depth-section {
  padding: 8rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.85);
  backdrop-filter: blur(4px);
}

.depth-cards {
  max-width: 1000px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  perspective: 1200px;
}

.depth-card {
  padding: 2rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 12px;
  transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.depth-card:hover {
  border-color: hsl(var(--card-hue), 80%, 55%);
  box-shadow: 0 20px 50px rgba(0, 0, 0, 0.4);
}

.dc-icon {
  font-size: 2rem;
  color: hsl(var(--card-hue), 80%, 55%);
  margin-bottom: 1rem;
}

.depth-card h3 {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.8rem;
}

.depth-card p {
  font-size: 0.9rem;
  color: var(--muted);
  line-height: 1.7;
}

/* â”€â”€ Stats â”€â”€ */
.stats-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.9);
}

.section-header {
  text-align: center;
  max-width: 560px;
  margin: 0 auto 4rem;
}

.section-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

.section-header h2 {
  font-size: 2.5rem;
  font-weight: 800;
  letter-spacing: -0.5px;
}

.stats-grid {
  max-width: 800px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1.5rem;
}

.stat-card {
  text-align: center;
  padding: 2rem 1rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  transition: all 0.3s ease;
}

.stat-card:hover {
  border-color: var(--accent);
  transform: translateY(-3px);
}

.stat-value {
  font-size: 2rem;
  font-weight: 800;
  color: var(--accent);
  letter-spacing: -1px;
  margin-bottom: 0.5rem;
  font-variant-numeric: tabular-nums;
}

.stat-label {
  font-size: 0.8rem;
  color: var(--muted);
  line-height: 1.5;
}

/* â”€â”€ Features â”€â”€ */
.features-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(7, 10, 18, 0.85);
}

.features-list {
  max-width: 800px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: 0;
}

.feature-item {
  display: flex;
  gap: 2rem;
  align-items: flex-start;
  padding: 2rem 0;
  border-bottom: 1px solid var(--border);
}

.feature-item:last-child { border-bottom: none; }

.feature-num {
  font-size: 0.7rem;
  font-weight: 800;
  letter-spacing: 2px;
  color: var(--accent);
  opacity: 0.5;
  padding-top: 0.3rem;
  flex-shrink: 0;
  width: 28px;
}

.feature-text h3 {
  font-size: 1.1rem;
  font-weight: 700;
  color: var(--text);
  margin-bottom: 0.6rem;
}

.feature-text p {
  font-size: 0.95rem;
  color: var(--muted);
  line-height: 1.8;
}

code {
  background: rgba(134, 232, 255, 0.1);
  color: var(--accent);
  padding: 0.15rem 0.4rem;
  border-radius: 4px;
  font-family: 'Monaco', monospace;
  font-size: 0.88em;
}

/* â”€â”€ CTA â”€â”€ */
.cta-section {
  padding: 8rem 2rem;
  text-align: center;
  border-top: 1px solid var(--border);
  background: radial-gradient(ellipse at 50% 50%, rgba(134, 232, 255, 0.06) 0%, transparent 70%);
}

.cta-section h2 {
  font-size: 3.5rem;
  font-weight: 900;
  margin-bottom: 1rem;
  letter-spacing: -1.5px;
}

.cta-section p {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2rem;
}

.btn-primary {
  display: inline-block;
  padding: 0.9rem 2rem;
  background: var(--accent);
  color: var(--bg);
  border-radius: 6px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-primary:hover {
  background: rgba(134, 232, 255, 0.85);
  transform: translateY(-2px);
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 900px) {
  .stats-grid { grid-template-columns: repeat(2, 1fr); }
}

@media (max-width: 768px) {
  .hero h1 { font-size: 2.8rem; }
  .depth-cards { grid-template-columns: 1fr; }
  .stats-grid { grid-template-columns: repeat(2, 1fr); }
  .section-header h2 { font-size: 2rem; }
}

@media (max-width: 480px) {
  .hero h1 { font-size: 2.2rem; }
  .cta-section h2 { font-size: 2.5rem; }
  .stats-grid { grid-template-columns: 1fr 1fr; }
}

