*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

:root {
  --accent: #86e8ff;
  --neon-purple: #ae52ff;
  --neon-pink: #ff40d6;
}

body {
  background: #030510;
  color: #f0f4fb;
  font-family: 'Inter', 'SF Pro Display', system-ui, sans-serif;
  overflow: hidden;
  height: 100vh;
  cursor: none;
}

@media (hover: none) {
  body { cursor: auto; }
}

/* â”€â”€ Stage â”€â”€ */
.stage {
