  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.hidden-text p {
  font-size: 1rem;
  color: rgba(255, 255, 255, 0.7);
}

/* â”€â”€ Cover layer (dark overlay with spotlight mask) â”€â”€ */
.cover-layer {
  position: absolute;
  inset: 0;
  z-index: 10;
  background: #030510;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  /* The mask is set via JS on mousemove */
  -webkit-mask-image: radial-gradient(circle 0px at 50% 50%, transparent 0%, black 100%);
  mask-image: radial-gradient(circle 0px at 50% 50%, transparent 0%, black 100%);
  -webkit-mask-composite: source-over;
  pointer-events: none;
