.cover-content {
  padding: 2rem;
  pointer-events: auto;
}

.eyebrow {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  text-transform: uppercase;
  letter-spacing: 0.18em;
  color: var(--accent);
  margin-bottom: 1rem;
}

h1 {
  font-size: clamp(2.5rem, 7vw, 5rem);
  font-weight: 700;
  letter-spacing: -0.03em;
}

.subtitle {
  font-size: clamp(0.9rem, 2vw, 1.05rem);
  color: #8a95a8;
  max-width: 460px;
  margin: 0.75rem auto 0;
  line-height: 1.6;
}

.hint {
  margin-top: 2rem;
  font-size: 0.8rem;
  color: rgba(134, 232, 255, 0.5);
  text-transform: uppercase;
  letter-spacing: 0.1em;
}

/* â”€â”€ Trail â”€â”€ */
.trail {
  position: fixed;
  inset: 0;
  z-index: 5;
  pointer-events: none;
}

.trail-dot {
  position: absolute;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: rgba(134, 232, 255, 0.4);
  transform: translate(-50%, -50%);
  will-change: transform, opacity;
}

/* â”€â”€ Back button â”€â”€ */
.btn-back {
  position: fixed;
  bottom: 2rem;
  left: 50%;
  transform: translateX(-50%);
  z-index: 20;
  display: inline-block;
  padding: 0.7rem 2rem;
  border-radius: 999px;
  border: 1px solid rgba(134, 232, 255, 0.3);
  color: var(--accent);
  text-decoration: none;
  font: 600 0.85rem/1 'Inter', system-ui, sans-serif;
  transition: all 0.25s ease;
  background: rgba(3, 5, 16, 0.8);
  backdrop-filter: blur(8px);
}

.btn-back:hover {
  background: rgba(134, 232, 255, 0.08);
  border-color: var(--accent);
}

/* â”€â”€ Reduced motion â”€â”€ */
.reduced-motion .cover-layer {
  -webkit-mask-image: none;
  mask-image: none;
  background: rgba(3, 5, 16, 0.5);
}
.reduced-motion body { cursor: auto; }

