/* â”€â”€â”€ Reset & Base â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
*, *::before, *::after { box-sizing: border-box; margin: 0; padding: 0; }

:root {
  --bg:      #0a0a0a;
  --text:    #f5f5f5;
  --muted:   #555;
  --accent:  #e8ff00;
  --border:  #1c1c1c;
  --rule:    1px solid var(--border);
  --pad:     clamp(1.5rem, 5vw, 4rem);
}

html {
  font-size: 16px;
  scroll-behavior: auto; /* Lenis handles smoothing */
}

body {
  background: var(--bg);
  color: var(--text);
  font-family: -apple-system, 'Helvetica Neue', Arial, sans-serif;
  font-weight: 400;
  line-height: 1.5;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* Reduced-motion: disable all transitions */
.reduced-motion *, .reduced-motion *::before, .reduced-motion *::after {
  transition-duration: 0.001ms !important;
  animation-duration: 0.001ms !important;
}

/* â”€â”€â”€ Reusable â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.label {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.65rem;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted);
}

.label--right { margin-left: auto; }

.section-index {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.6rem;
  color: var(--accent);
  letter-spacing: 0.1em;
}

.section-label {
  font-family: 'SF Mono', 'Fira Code', 'Courier New', monospace;
  font-size: 0.6rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: var(--muted);
}

/* â”€â”€â”€ Hero â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
.hero {
  min-height: 100dvh;
