  border-bottom: var(--rule);
}

.hero__inner {
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: var(--pad);
  padding-top: calc(var(--pad) + 3rem); /* offset for demo-shell bar */
}

.hero__meta {
