import Lenis from 'lenis';

gsap.registerPlugin(ScrollTrigger, SplitText);

// â”€â”€â”€ Shell â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
initDemoShell({
  title: 'Software Engineer Portfolio',
  category: 'pages',
  tech: ['gsap', 'lenis', 'scrolltrigger', 'splittext'],
});

// â”€â”€â”€ Reduced Motion â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
let reduced = prefersReducedMotion();
if (reduced) document.documentElement.classList.add('reduced-motion');

window.addEventListener('motion-preference', (e) => {
  reduced = e.detail.reduced;
  document.documentElement.classList.toggle('reduced-motion', reduced);
  ScrollTrigger.refresh();
});

const dur = (d) => reduced ? 0 : d;

// â”€â”€â”€ Lenis â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const lenis = new Lenis({ lerp: 0.1, smoothWheel: true });
lenis.on('scroll', ScrollTrigger.update);
gsap.ticker.add((time) => lenis.raf(time * 1000));
gsap.ticker.lagSmoothing(0);

// â”€â”€â”€ Hero â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateHero() {
  const nameEl = document.getElementById('heroName');
  if (!nameEl) return;

  const split = new SplitText(nameEl, { type: 'chars,lines', linesClass: 'line' });

  gsap.set(split.chars, { y: '110%', opacity: 0 });

  gsap.to(split.chars, {
    y: '0%',
    opacity: 1,
    duration: dur(1),
    ease: 'expo.out',
    stagger: { each: dur(0.025) },
    delay: dur(0.2),
  });

  // Tagline fade in
  gsap.from('.hero__tagline', {
    opacity: 0,
    y: reduced ? 0 : 20,
    duration: dur(0.8),
    ease: 'expo.out',
    delay: dur(0.7),
  });

  // Scroll cue fade in
  gsap.from('.hero__scroll-cue', {
    opacity: 0,
    duration: dur(1),
    delay: dur(1.2),
  });
}

// â”€â”€â”€ Section Headers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateSectionHeaders() {
  document.querySelectorAll('.section__header').forEach((el) => {
    gsap.to(el, {
      opacity: 1,
      x: 0,
      duration: dur(0.7),
      ease: 'expo.out',
      scrollTrigger: {
        trigger: el,
        start: 'top 85%',
        toggleActions: 'play none none reverse',
      },
    });
  });
}

// â”€â”€â”€ About Bio â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateAbout() {
  const textEl = document.getElementById('aboutText');
  if (!textEl) return;

  const split = new SplitText(textEl, { type: 'lines', linesClass: 'bio-line' });
  gsap.set(split.lines, { opacity: 0, x: reduced ? 0 : -30 });

  gsap.to(split.lines, {
    opacity: 1,
    x: 0,
    duration: dur(0.7),
    ease: 'expo.out',
    stagger: { each: dur(0.08) },
    scrollTrigger: {
      trigger: textEl,
      start: 'top 80%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€â”€ Stat Counters â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateStats() {
  document.querySelectorAll('.stat').forEach((statEl) => {
    const numEl = statEl.querySelector('.stat__num');
    const target = numEl.dataset.target;

    // Skip the infinity symbol
    if (target === 'âˆ' || !target) return;

    const end = parseInt(target, 10);
    const counter = { val: 0 };

    ScrollTrigger.create({
      trigger: statEl,
      start: 'top 80%',
      once: true,
      onEnter: () => {
        if (reduced) { numEl.textContent = end; return; }
        gsap.to(counter, {
          val: end,
          duration: 1.6,
          ease: 'power2.out',
          onUpdate: () => { numEl.textContent = Math.round(counter.val); },
        });
      },
    });
  });
}

// â”€â”€â”€ Skills â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateSkills() {
  document.querySelectorAll('.skill-row').forEach((row, i) => {
    const pct = parseInt(row.dataset.pct, 10);
    const fill = row.querySelector('.skill-row__fill');

    // Slide row in
    gsap.to(row, {
      opacity: 1,
      x: 0,
      duration: dur(0.6),
      ease: 'expo.out',
      delay: dur(i * 0.07),
      scrollTrigger: {
        trigger: row,
        start: 'top 85%',
        toggleActions: 'play none none reverse',
      },
    });

    // Animate bar fill
    ScrollTrigger.create({
      trigger: row,
      start: 'top 85%',
      once: true,
      onEnter: () => {
        gsap.to(fill, {
          width: `${pct}%`,
          duration: dur(1.2),
          ease: 'power2.out',
          delay: dur(0.15 + i * 0.07),
        });
      },
    });
  });
}

// â”€â”€â”€ Projects â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateProjects() {
  const cards = document.querySelectorAll('.project-card');

  gsap.to(cards, {
    opacity: 1,
    y: 0,
    duration: dur(0.8),
    ease: 'expo.out',
    stagger: { each: dur(0.12) },
    scrollTrigger: {
      trigger: '.projects__grid',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€â”€ Contact CTA â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateContact() {
  const ctaEl = document.getElementById('contactCta');
  if (!ctaEl) return;

  const split = new SplitText(ctaEl, { type: 'words,chars', wordsClass: 'word' });
  gsap.set(split.chars, { y: '110%', opacity: 0 });

  gsap.to(split.chars, {
    y: '0%',
    opacity: 1,
    duration: dur(0.9),
    ease: 'expo.out',
    stagger: { each: dur(0.018) },
    scrollTrigger: {
      trigger: ctaEl,
      start: 'top 80%',
      toggleActions: 'play none none reverse',
    },
  });

  // Email + social fade
  gsap.from('.contact__links', {
    opacity: 0,
    y: reduced ? 0 : 20,
    duration: dur(0.8),
    ease: 'expo.out',
    delay: dur(0.3),
    scrollTrigger: {
      trigger: '.contact__inner',
      start: 'top 75%',
      toggleActions: 'play none none reverse',
    },
  });
}

// â”€â”€â”€ Init â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
animateHero();
animateSectionHeaders();
animateAbout();
animateStats();
animateSkills();
animateProjects();
animateContact();

