if (!window.MotionPreference) {
  const __mql = window.matchMedia("(prefers-reduced-motion: reduce)");
  const __listeners = new Set();

  const MotionPreference = {
    prefersReducedMotion() {
      return __mql.matches;
    },
    setOverride(value) {
      const reduced = Boolean(value);
      document.documentElement.classList.toggle("reduced-motion", reduced);
      window.dispatchEvent(new CustomEvent("motion-preference", { detail: { reduced } }));
      for (const listener of __listeners) {
        try {
          listener({ reduced, override: reduced, systemReduced: __mql.matches });
        } catch {}
      }
    },
    onChange(listener) {
      __listeners.add(listener);
      try {
        listener({
          reduced: __mql.matches,
          override: null,
          systemReduced: __mql.matches,
        });
      } catch {}
      return () => __listeners.delete(listener);
    },
    getState() {
      return { reduced: __mql.matches, override: null, systemReduced: __mql.matches };
    },
  };

  window.MotionPreference = MotionPreference;
}

const root = document.documentElement;
const track = document.getElementById("track");
const progressBar = document.getElementById("progressBar");
const progressLabel = document.getElementById("progressLabel");
const storySteps = Array.from(document.querySelectorAll(".story-step"));
const mobileQuery = window.matchMedia("(max-width: 1000px)");

const state = {
  reduced: Boolean(window.MotionPreference && window.MotionPreference.prefersReducedMotion()),
  mobile: mobileQuery.matches,
  raf: 0
};

const thresholds = [0, 0.36, 0.72];

function clamp(value, min, max) {
  return Math.min(max, Math.max(min, value));
}

function getScrollProgress() {
  const rect = track.getBoundingClientRect();
  const distance = rect.height - window.innerHeight;
  if (distance <= 0) return 1;
  return clamp(-rect.top / distance, 0, 1);
}

function applyStepState(progress) {
  let activeIndex = 0;
  if (progress >= thresholds[2]) {
    activeIndex = 2;
  } else if (progress >= thresholds[1]) {
    activeIndex = 1;
  }

  storySteps.forEach((step, index) => {
    step.classList.toggle("active", index <= activeIndex);
  });
}

function render() {
  state.raf = 0;
  const progress = state.reduced || state.mobile ? 1 : getScrollProgress();
  const reveal = 8 + progress * 92;
  const revealText = `${Math.round(reveal)}%`;

  root.style.setProperty("--split-progress", progress.toFixed(4));
  root.style.setProperty("--split-reveal", `${reveal.toFixed(2)}%`);

  progressLabel.textContent = revealText;
  progressBar.setAttribute("aria-valuenow", String(Math.round(reveal)));
  progressBar.setAttribute("aria-valuetext", `Reveal progress ${Math.round(reveal)} percent`);

  applyStepState(progress);
}

function scheduleRender() {
  if (state.raf) return;
  state.raf = window.requestAnimationFrame(render);
}

if (mobileQuery.addEventListener) {
  mobileQuery.addEventListener("change", (event) => {
    state.mobile = event.matches;
    scheduleRender();
  });
} else if (mobileQuery.addListener) {
  mobileQuery.addListener((event) => {
    state.mobile = event.matches;
    scheduleRender();
  });
}

if (window.MotionPreference && window.MotionPreference.onChange) {
  window.MotionPreference.onChange((next) => {
    state.reduced = Boolean(next.reduced);
    scheduleRender();
  });
}

window.addEventListener("scroll", scheduleRender, { passive: true });
window.addEventListener("resize", scheduleRender);
scheduleRender();

snippets.css:
:root {
  color-scheme: light;
  --bg: #f7f1e8;
  --panel: rgba(255, 255, 255, 0.82);
  --stroke: rgba(82, 57, 46, 0.2);
  --text: #3b2a22;
  --muted: rgba(59, 42, 34, 0.6);
  --accent: #b36b3e;
  --accent-2: #f4b183;
  --accent-3: #7b4f35;
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  min-height: 100vh;
  font-family: "Quicksand", sans-serif;
  background: radial-gradient(circle at 10% 10%, rgba(244, 177, 131, 0.28), transparent 55%),
    radial-gradient(circle at 90% 20%, rgba(179, 107, 62, 0.2), transparent 60%),
    #f6efe6;
  color: var(--text);
  overflow-x: hidden;
}

a {
  color: inherit;
  text-decoration: none;
}

#bg {
  position: fixed;
  inset: 0;
  z-index: 0;
}

.loader {
  position: fixed;
  inset: 0;
  background: #f6efe6;
  display: grid;
  place-items: center;
  gap: 18px;
  z-index: 999;
  color: var(--text);
  font-family: "Playfair Display", serif;
  letter-spacing: 0.2em;
  text-transform: uppercase;
}

.loader-ring {
  width: 90px;
  height: 90px;
  border-radius: 50%;
  border: 2px solid rgba(179, 107, 62, 0.4);
  border-top-color: var(--accent);
  animation: spin 1.4s linear infinite;
}

.loader-cup {
  position: absolute;
  width: 36px;
  height: 36px;
  border-radius: 8px 8px 12px 12px;
  border: 2px solid var(--accent-3);
  background: rgba(179, 107, 62, 0.1);
  display: grid;
  place-items: center;
}

.steam {
  position: absolute;
  width: 6px;
  height: 18px;
  border-radius: 50%;
  border: 1px solid rgba(179, 107, 62, 0.5);
  top: -20px;
  animation: steam 1.2s ease-in-out infinite;
}

.steam:nth-child(2) {
  left: 12px;
  animation-delay: 0.2s;
}

.steam:nth-child(3) {
  left: -12px;
  animation-delay: 0.4s;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

@keyframes steam {
  0%, 100% {
    opacity: 0;
    transform: translateY(0);
  }
  50% {
    opacity: 1;
    transform: translateY(-8px);
  }
}

.topbar {
  position: sticky;
  top: 16px;
  z-index: 5;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 24px;
  margin: 0 clamp(16px, 4vw, 60px);
  padding: 16px 24px;
  border-radius: 999px;
  background: rgba(255, 250, 243, 0.85);
  backdrop-filter: blur(18px);
  border: 1px solid rgba(179, 107, 62, 0.2);
  box-shadow: 0 18px 40px rgba(82, 57, 46, 0.15);
}

.brand {
  display: flex;
  align-items: center;
  gap: 16px;
}

.mark {
  width: 48px;
  height: 48px;
  border-radius: 14px;
  background: linear-gradient(140deg, var(--accent), var(--accent-2));
  display: grid;
  place-items: center;
  font-weight: 700;
  color: #fff7ef;
  font-family: "Playfair Display", serif;
}

.brand-title {
  font-family: "Playfair Display", serif;
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.85rem;
  margin: 0;
}

.brand-sub {
  margin: 4px 0 0;
  color: var(--muted);
  font-size: 0.9rem;
}

.nav {
  display: flex;
  align-items: center;
  gap: 18px;
  font-size: 0.75rem;
  letter-spacing: 0.18em;
  text-transform: uppercase;
}

.nav a {
  color: var(--muted);
  position: relative;
}

.nav a::after {
  content: "";
  position: absolute;
  left: 0;
  bottom: -6px;
  width: 100%;
  height: 2px;
  background: var(--accent);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform 0.3s ease;
}

.nav a:hover::after {
  transform: scaleX(1);
}

.cta {
  padding: 0.5rem 1.3rem;
  border-radius: 999px;
  border: 1px solid rgba(179, 107, 62, 0.5);
  background: rgba(179, 107, 62, 0.12);
  color: var(--text);
}

.page {
  position: relative;
  z-index: 2;
  padding: 0 clamp(20px, 6vw, 80px) 80px;
}

.hero {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 40px;
  padding: 80px 0 40px;
}

.eyebrow {
  text-transform: uppercase;
  letter-spacing: 0.35em;
  font-size: 0.7rem;
  color: var(--accent);
  font-family: "Playfair Display", serif;
}

.hero-copy h1 {
  font-family: "Playfair Display", serif;
  font-size: clamp(2.6rem, 5vw, 4.6rem);
  margin: 16px 0;
}

.lead {
  color: var(--muted);
  line-height: 1.7;
  max-width: 55ch;
}

.hero-actions {
  display: flex;
  gap: 14px;
  flex-wrap: wrap;
  margin-top: 24px;
}

.btn {
  padding: 0.85rem 1.6rem;
  border-radius: 999px;
  border: 1px solid rgba(59, 42, 34, 0.25);
  background: transparent;
  color: var(--text);
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
  cursor: pointer;
}

.btn.primary {
  background: linear-gradient(120deg, rgba(179, 107, 62, 0.25), rgba(244, 177, 131, 0.35));
  border-color: rgba(179, 107, 62, 0.6);
}

.btn.ghost {
  border-color: rgba(244, 177, 131, 0.55);
}

.hero-metrics {
  margin-top: 24px;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
  gap: 12px;
}

.hero-metrics div {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 14px;
  padding: 14px;
}

.hero-panel {
  display: grid;
  gap: 18px;
}

.panel-card {
  background: var(--panel);
  border: 1px solid var(--stroke);
  border-radius: 18px;
  padding: 20px;
  box-shadow: 0 0 30px rgba(179, 107, 62, 0.12);
}

.panel-title {
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.65rem;
  color: var(--muted);
}

.marquee {
  overflow: hidden;
  border-top: 1px solid rgba(82, 57, 46, 0.1);
  border-bottom: 1px solid rgba(82, 57, 46, 0.1);
}

.marquee-track {
  display: flex;
  gap: 48px;
  padding: 16px 0;
  text-transform: uppercase;
  letter-spacing: 0.35em;
  font-size: 0.65rem;
  color: var(--muted);
  white-space: nowrap;
}

.section {
  padding: 64px 0;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
  gap: 24px;
}

.section-header p {
  color: var(--muted);
}

.section-header.subhead {
  margin-top: 28px;
}

.menu-grid,
.service-grid {
  display: grid;
  gap: 24px;
  margin-top: 28px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.menu-card,
.service-card {
  background: rgba(255, 255, 255, 0.78);
  border: 1px solid var(--stroke);
  border-radius: 18px;
  padding: 22px;
}

.menu-card h3,
.service-card h3 {
  margin: 14px 0 10px;
}

.menu-card p,
.service-card p {
  color: var(--muted);
  line-height: 1.6;
}

.menu-meta {
  display: flex;
  justify-content: space-between;
  color: var(--muted);
  font-size: 0.85rem;
}

.shop-layout {
  display: grid;
  gap: 24px;
  grid-template-columns: minmax(0, 1fr) minmax(220px, 320px);
  align-items: start;
}

.shop-grid,
.people-grid {
  display: grid;
  gap: 22px;
  margin-top: 24px;
  grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

.product-card,
.person-card {
  background: rgba(255, 255, 255, 0.82);
  border: 1px solid var(--stroke);
  border-radius: 18px;
  padding: 20px;
  display: grid;
  gap: 10px;
}

.product-image {
  height: 140px;
  border-radius: 14px;
  background: linear-gradient(140deg, rgba(179, 107, 62, 0.4), rgba(244, 177, 131, 0.4));
  border: 1px solid rgba(179, 107, 62, 0.25);
  display: grid;
  place-items: center;
  font-family: "Playfair Display", serif;
  font-size: 1.2rem;
  color: var(--accent-3);
}

.product-meta {
  display: flex;
  justify-content: space-between;
  color: var(--muted);
  font-size: 0.85rem;
}

.product-price {
  font-family: "Playfair Display", serif;
  font-size: 1.2rem;
  color: var(--accent-3);
}

.add-btn {
  margin-top: 6px;
  padding: 0.6rem 1rem;
  border-radius: 999px;
  border: 1px solid rgba(179, 107, 62, 0.4);
  background: rgba(179, 107, 62, 0.12);
  cursor: pointer;
  font-size: 0.75rem;
  text-transform: uppercase;
  letter-spacing: 0.2em;
}

.cart {
  background: rgba(255, 255, 255, 0.85);
  border: 1px solid var(--stroke);
  border-radius: 18px;
  padding: 20px;
  display: grid;
  gap: 14px;
  position: sticky;
  top: 110px;
}

.cart-header {
  display: flex;
  justify-content: space-between;
  align-items: baseline;
}

.cart-items {
  display: grid;
  gap: 10px;
  max-height: 280px;
  overflow: auto;
}

.cart-item {
  display: flex;
  justify-content: space-between;
  gap: 12px;
  font-size: 0.9rem;
  color: var(--muted);
}

.cart-total {
  display: flex;
  justify-content: space-between;
  font-weight: 600;
}

.person-chip {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  font-size: 0.8rem;
  color: var(--muted);
}

.avatar {
  width: 54px;
  height: 54px;
  border-radius: 50%;
  background: linear-gradient(140deg, var(--accent), var(--accent-2));
  display: grid;
  place-items: center;
  color: #fff7ef;
  font-weight: 700;
  font-family: "Playfair Display", serif;
}

.split {
  display: grid;
  gap: 28px;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
}

.panel {
  padding: 26px;
  background: rgba(255, 255, 255, 0.75);
  border-radius: 18px;
  border: 1px solid rgba(179, 107, 62, 0.2);
}

.panel ul {
  margin: 16px 0 0;
  padding-left: 18px;
  color: var(--muted);
  line-height: 1.7;
}

.chips {
  display: flex;
  flex-wrap: wrap;
  gap: 12px;
  margin-top: 18px;
}

.chips span {
  padding: 0.45rem 0.9rem;
  border-radius: 999px;
  border: 1px solid rgba(179, 107, 62, 0.4);
  font-size: 0.75rem;
}

.stats {
  display: grid;
  gap: 24px;
  grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
  text-align: center;
}

.stats h2 {
  font-family: "Playfair Display", serif;
  font-size: 2.4rem;
  margin: 0 0 8px;
  color: var(--accent);
}

.contact {
  display: grid;
  gap: 32px;
  grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
}

.contact-form {
  display: grid;
  gap: 16px;
}

.contact-form input,
.contact-form textarea {
  background: rgba(255, 255, 255, 0.85);
  border: 1px solid rgba(82, 57, 46, 0.2);
  border-radius: 12px;
  padding: 12px 14px;
