class BeanField {
  constructor(container) {
    this.container = container;
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(55, window.innerWidth / window.innerHeight, 0.1, 200);
    this.camera.position.z = 24;

    this.renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.setPixelRatio(Math.min(window.devicePixelRatio, 2));
    container.appendChild(this.renderer.domElement);

    this.clock = new THREE.Clock();
    this.group = new THREE.Group();
    this.scene.add(this.group);

    this.createBeans();
    this.createSteam();
    this.bindEvents();
    this.animate();
  }

  createBeans() {
    const material = new THREE.MeshStandardMaterial({
      color: 0xb36b3e,
      metalness: 0.2,
      roughness: 0.7
    });

    const light = new THREE.DirectionalLight(0xffffff, 0.9);
    light.position.set(5, 10, 6);
    this.scene.add(light);
    this.scene.add(new THREE.AmbientLight(0xffffff, 0.6));

    for (let i = 0; i < 40; i++) {
      const geometry = new THREE.SphereGeometry(0.45, 16, 16);
      const bean = new THREE.Mesh(geometry, material.clone());
      bean.scale.set(1.1, 0.7, 0.6);
      bean.position.set(
        (Math.random() - 0.5) * 20,
        (Math.random() - 0.5) * 10,
        (Math.random() - 0.5) * 20
      );
      bean.rotation.set(Math.random(), Math.random(), Math.random());
      this.group.add(bean);
    }
  }

  createSteam() {
    const count = 120;
    const positions = new Float32Array(count * 3);
    for (let i = 0; i < count; i++) {
      positions[i * 3] = (Math.random() - 0.5) * 16;
      positions[i * 3 + 1] = Math.random() * 10;
      positions[i * 3 + 2] = (Math.random() - 0.5) * 16;
    }
    const geometry = new THREE.BufferGeometry();
    geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
    const material = new THREE.PointsMaterial({ color: 0xf4b183, size: 0.1, transparent: true, opacity: 0.4 });
    this.steam = new THREE.Points(geometry, material);
    this.scene.add(this.steam);
  }

  bindEvents() {
    window.addEventListener("resize", () => this.onResize());
  }

  onResize() {
    this.camera.aspect = window.innerWidth / window.innerHeight;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(window.innerWidth, window.innerHeight);
  }

  animate() {
    const t = this.clock.getElapsedTime();
    this.group.rotation.y = t * 0.08;
    this.group.rotation.x = t * 0.03;
    this.steam.rotation.y = -t * 0.04;
    this.renderer.render(this.scene, this.camera);
    requestAnimationFrame(() => this.animate());
  }
}

const bg = document.getElementById("bg");
if (bg && window.THREE) {
  new BeanField(bg);
}

