  font-size: clamp(18px, 4vw, 28px);
  color: var(--accent-4);
  text-shadow: 3px 3px 0 rgba(255, 71, 87, 0.5);
  letter-spacing: 0.15em;
}

.contact-sub {
  font-family: var(--pixel-font);
  font-size: clamp(12px, 2.5vw, 18px);
