.pixel-btn--no {
  color: var(--accent-4);
  border-color: var(--accent-4);
}
.pixel-btn--no:hover {
  background: rgba(255, 71, 87, 0.1);
  box-shadow: 0 0 16px rgba(255, 71, 87, 0.25);
}

.btn-cursor { animation: blink 0.8s steps(1) infinite; }

