const stars = Array.from({ length: 150 }, () => ({
  x:       Math.random(),          // normalized 0-1
  y:       Math.random(),
  size:    Math.random() * 2 + 1,  // 1-3px
  twinkle: Math.random() * Math.PI * 2,
  speed:   0.02 + Math.random() * 0.04,
  color:   Math.random() < 0.1
             ? (Math.random() < 0.5 ? '#ff6b9d' : '#4ecdc4')
             : '#ffffff',
}));

function drawStars(time) {
  const w = canvas.width;
  const h = canvas.height;
  stars.forEach((s) => {
    const alpha = 0.4 + Math.sin(s.twinkle + time * s.speed * 30) * 0.4;
    ctx.fillStyle =
      s.color === '#ffffff'
        ? `rgba(255, 255, 255, ${alpha})`
        : s.color + Math.round(alpha * 255).toString(16).padStart(2, '0');
    // pixel-crisp: use fillRect for square pixels, no antialiasing
    ctx.fillRect(Math.round(s.x * w), Math.round(s.y * h), s.size, s.size);
  });
}

// â”€â”€â”€ Pixel Character â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
// 8x12 pixel grid; each cell = PIXEL_SCALE px square
const PIXEL_SCALE = 4;
const COLORS = {
  0: null,           // transparent
  1: '#f4c88a',      // skin
  2: '#3d2314',      // hair / dark
  3: '#4ecdc4',      // body (teal armor)
  4: '#ffdd57',      // accent (yellow visor / details)
  5: '#ff6b9d',      // accent-2 (pink trim)
  6: '#1a1a3a',      // shadow / dark belt
  7: '#ffffff',      // white
  8: '#ff4757',      // red detail
};

// 8-frame walk cycle â€” 8 columns wide, 12 rows tall
// frame index selects leg pattern (bottom 4 rows vary)
const FRAME_TOPS = [
  // rows 0-7: shared upper body (helmet, visor, torso, arms)
  [0,0,2,2,2,2,0,0],   // 0 â€” helmet top
  [0,2,4,4,4,4,2,0],   // 1 â€” visor
  [2,2,4,4,4,4,2,2],   // 2 â€” visor lower / helmet side
  [0,2,1,1,1,1,2,0],   // 3 â€” face
  [0,0,1,1,1,1,0,0],   // 4 â€” chin
  [5,3,3,3,3,3,3,5],   // 5 â€” shoulders
  [0,3,3,6,6,3,3,0],   // 6 â€” torso / belt
  [0,3,3,6,6,3,3,0],   // 7 â€” lower torso
];

// Leg frames â€” 4 rows, cycled across 8 animation frames
const LEG_FRAMES = [
  // frame 0: stand
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,0,0,3,0],[0,4,0,0,0,0,4,0]],
  // frame 1: step right fwd
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,3,3,0,3,0,0],[0,0,4,4,0,4,0,0]],
  // frame 2: step right
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,0,3,0,3,0,0],[0,0,0,4,0,4,0,0]],
  // frame 3: step right back
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,0,3,0,0,3,3,0],[0,0,4,0,0,4,4,0]],
  // frame 4: stand (mirror)
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,0,0,3,0],[0,4,0,0,0,0,4,0]],
  // frame 5: step left fwd
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,3,3,0,0],[0,4,0,0,4,4,0,0]],
  // frame 6: step left
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,0,0,3,0,0,0],[0,4,0,0,4,0,0,0]],
  // frame 7: step left back
  [[0,3,3,0,0,3,3,0],[0,3,3,0,0,3,3,0],[0,3,3,0,0,0,3,0],[0,4,4,0,0,0,4,0]],
];

function drawPixelChar(cx, cy, frameIndex) {
  const ps = PIXEL_SCALE;
  const fi = frameIndex % LEG_FRAMES.length;

  // upper body (shared)
  FRAME_TOPS.forEach((row, ry) => {
    row.forEach((colorKey, rx) => {
      if (!colorKey) return;
      ctx.fillStyle = COLORS[colorKey];
      ctx.fillRect(
        Math.round(cx + rx * ps - 4 * ps),
        Math.round(cy + ry * ps - 6 * ps),
        ps, ps
      );
    });
  });

  // legs (animated)
  LEG_FRAMES[fi].forEach((row, ry) => {
    row.forEach((colorKey, rx) => {
      if (!colorKey) return;
      ctx.fillStyle = COLORS[colorKey];
      ctx.fillRect(
        Math.round(cx + rx * ps - 4 * ps),
        Math.round(cy + (ry + 8) * ps - 6 * ps),
        ps, ps
      );
    });
  });
}

// â”€â”€â”€ Animation loop â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
let animFrame = 0;
let lastFrameTime = 0;
const FRAME_INTERVAL = 120; // ms per walk frame
let charY = 0; // slight bob

function animate(time) {
  if (!canvas.width || !canvas.height) {
    requestAnimationFrame(animate);
    return;
  }

  const w = canvas.width;
  const h = canvas.height;

  // Clear
  ctx.clearRect(0, 0, w, h);

  // Deep space gradient bg
  const grad = ctx.createLinearGradient(0, 0, 0, h);
  grad.addColorStop(0, '#0a0a1a');
  grad.addColorStop(1, '#0f0f2a');
  ctx.fillStyle = grad;
  ctx.fillRect(0, 0, w, h);

  // Stars
  drawStars(time / 1000);

  // Subtle ground grid
  if (!reducedMotion) {
    ctx.strokeStyle = 'rgba(78, 205, 196, 0.07)';
    ctx.lineWidth = 1;
    const gridSize = 40;
    const scrollOffset = (time * 0.02) % gridSize;
    for (let x = 0; x < w + gridSize; x += gridSize) {
      ctx.beginPath();
      ctx.moveTo(x - scrollOffset, h * 0.7);
      ctx.lineTo(x - scrollOffset, h);
      ctx.stroke();
    }
    for (let y = h * 0.7; y < h; y += gridSize * 0.5) {
      ctx.beginPath();
      ctx.moveTo(0, y);
      ctx.lineTo(w, y);
      ctx.stroke();
    }
  }

  // Advance walk frame
  if (!reducedMotion && time - lastFrameTime > FRAME_INTERVAL) {
    animFrame = (animFrame + 1) % 8;
    lastFrameTime = time;
  }

  // Character bob
  if (!reducedMotion) {
    charY = Math.sin(time / 500) * 3;
  }

  // Draw character at center, slightly below mid
  const charX = w / 2;
  const charCy = h * 0.65 + charY;
  drawPixelChar(charX, charCy, animFrame);

  // Glow under character
  const glowGrad = ctx.createRadialGradient(charX, charCy + 20, 0, charX, charCy + 20, 40);
  glowGrad.addColorStop(0, 'rgba(78, 205, 196, 0.15)');
  glowGrad.addColorStop(1, 'transparent');
  ctx.fillStyle = glowGrad;
  ctx.fillRect(charX - 40, charCy, 80, 40);

  requestAnimationFrame(animate);
}

// â”€â”€â”€ Score counter â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const scoreEl = document.getElementById('scoreVal');
let score = 0;
function tickScore() {
  if (reducedMotion) return;
  score += Math.floor(Math.random() * 37 + 1);
  if (score > 999999) score = 0;
  scoreEl.textContent = String(score).padStart(6, '0');
  setTimeout(tickScore, 80 + Math.random() * 140);
}

// â”€â”€â”€ Pixel Nav visibility â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const nav = document.getElementById('pixelNav');
const heroSection = document.getElementById('hero');

ScrollTrigger.create({
  trigger: heroSection,
  start: 'bottom 80%',
  onEnter: () => nav.classList.add('visible'),
  onLeaveBack: () => nav.classList.remove('visible'),
});

// â”€â”€â”€ Active nav link on scroll â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const sections = ['hero', 'stats', 'games', 'tech', 'contact'];
sections.forEach((id) => {
  ScrollTrigger.create({
    trigger: `#${id}`,
    start: 'top 50%',
    end: 'bottom 50%',
    onToggle: ({ isActive }) => {
      const link = document.querySelector(`.nav-link[data-section="${id}"]`);
      if (link) link.classList.toggle('active', isActive);
    },
  });
});

// â”€â”€â”€ View Transition helper â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const vtOverlay = document.getElementById('vtOverlay');

function triggerViewTransition(targetId) {
  if (reducedMotion) {
    document.getElementById(targetId)?.scrollIntoView({ behavior: 'smooth' });
    return;
  }

  const supportsVT = typeof document.startViewTransition === 'function';

  if (supportsVT) {
    document.startViewTransition(() => {
      document.getElementById(targetId)?.scrollIntoView({ behavior: 'instant' });
    });
  } else {
    // Fallback: manual overlay
    vtOverlay.classList.add('active');
    setTimeout(() => {
      document.getElementById(targetId)?.scrollIntoView({ behavior: 'instant' });
      setTimeout(() => vtOverlay.classList.remove('active'), 300);
    }, 250);
  }
}

// Nav link click handler
document.querySelectorAll('.nav-link').forEach((link) => {
  link.addEventListener('click', (e) => {
    e.preventDefault();
    const target = link.dataset.section;
    triggerViewTransition(target);
  });
});

// â”€â”€â”€ Stat bars â€” animate on scroll enter â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function initStatAnimations() {
  const statCards = document.querySelectorAll('.stat-card');

  statCards.forEach((card) => {
    const bar     = card.querySelector('.stat-bar');
    const numEl   = card.querySelector('.stat-num');
    const barWidth  = parseInt(bar?.dataset.width || '0', 10);
    const numTarget = parseInt(numEl?.dataset.target || '0', 10);

    ScrollTrigger.create({
      trigger: card,
      start: 'top 80%',
      once: true,
      onEnter: () => {
        if (reducedMotion) {
          if (bar)   bar.style.width = barWidth + '%';
          if (numEl) numEl.textContent = numTarget;
          return;
        }
        // Animate bar
        if (bar) {
          gsap.to(bar, {
            width: barWidth + '%',
            duration: 1.2,
            ease: 'steps(20)',
          });
        }
        // Count up number
        if (numEl) {
          gsap.to({ val: 0 }, {
            val: numTarget,
            duration: 1.4,
            ease: 'steps(20)',
            onUpdate: function () {
              numEl.textContent = Math.round(this.targets()[0].val);
            },
          });
        }
      },
    });
  });

  // XP bar
  const xpBar = document.querySelector('.xp-bar-inner');
  const xpNum = document.querySelector('.xp-pct-num');

  if (xpBar) {
    ScrollTrigger.create({
      trigger: xpBar,
      start: 'top 85%',
      once: true,
      onEnter: () => {
        const target = parseInt(xpBar.dataset.xp || '0', 10);
        if (reducedMotion) {
          xpBar.style.width = target + '%';
          if (xpNum) xpNum.textContent = target;
          return;
        }
        gsap.to(xpBar, { width: target + '%', duration: 1.5, ease: 'steps(25)' });
        if (xpNum) {
          gsap.to({ val: 0 }, {
            val: target,
            duration: 1.5,
            ease: 'steps(25)',
            onUpdate: function () {
              xpNum.textContent = Math.round(this.targets()[0].val);
            },
          });
        }
      },
    });
  }
}

// â”€â”€â”€ Section reveal animations â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function initSectionReveals() {
  if (reducedMotion) return;

  // Stat cards stagger
  gsap.from('.stat-card', {
    scrollTrigger: {
      trigger: '.stats-grid',
      start: 'top 85%',
    },
    y: 20,
    opacity: 0,
    duration: 0.4,
    ease: 'steps(4)',
    stagger: 0.08,
  });

  // Game cards
  gsap.from('.game-card', {
    scrollTrigger: {
      trigger: '.games-grid',
      start: 'top 85%',
    },
    y: 30,
    opacity: 0,
    duration: 0.5,
    ease: 'steps(5)',
    stagger: 0.12,
  });

  // Inventory items
  gsap.from('.inv-item', {
    scrollTrigger: {
      trigger: '.inventory-grid',
      start: 'top 85%',
    },
    scale: 0.9,
    opacity: 0,
    duration: 0.3,
    ease: 'steps(3)',
    stagger: 0.06,
  });

  // Contact section
  gsap.from('.contact-screen > *', {
    scrollTrigger: {
      trigger: '.contact-screen',
      start: 'top 85%',
    },
    y: 16,
    opacity: 0,
    duration: 0.4,
    ease: 'steps(4)',
    stagger: 0.1,
  });

  // Section headers
  document.querySelectorAll('.section-header-pixel').forEach((header) => {
    gsap.from(header, {
      scrollTrigger: { trigger: header, start: 'top 90%' },
      x: -20,
      opacity: 0,
      duration: 0.5,
      ease: 'steps(5)',
    });
  });
}

// â”€â”€â”€ Hero entrance animation â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function animateHeroEntrance() {
  if (reducedMotion) return;

  const tl = gsap.timeline({ delay: 0.2 });
  tl.from('.hero-eyebrow', { opacity: 0, y: -10, duration: 0.4, ease: 'steps(4)' })
    .from('.hero-name',    { opacity: 0, y: 20,  duration: 0.5, ease: 'steps(5)' }, '-=0.1')
    .from('.hero-subtitle',{ opacity: 0,         duration: 0.3, ease: 'steps(3)' }, '-=0.1')
    .from('.hero-tags .hero-tag', {
      opacity: 0,
      y: 8,
      duration: 0.3,
      ease: 'steps(3)',
      stagger: 0.07,
    }, '-=0.1')
    .from('.hero-scroll-cue', { opacity: 0, duration: 0.4, ease: 'steps(4)' }, '-=0.1')
    .from('.hud-corner', { opacity: 0, duration: 0.3, ease: 'steps(3)', stagger: 0.15 }, '-=0.2')
    .from('.hud-score',  { opacity: 0, duration: 0.3, ease: 'steps(3)' }, '-=0.1');
}

// â”€â”€â”€ "No" button easter egg â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const btnNo = document.getElementById('btnNo');
let noClicks = 0;
const noMessages = [
  'âœ• ARE YOU SURE?',
  'âœ• REALLY SURE?',
  'âœ• FINAL ANSWER?',
  'âœ• WELL OK THEN',
  'âœ• YOUR LOSS!',
  'âœ• ...JUST KIDDING!',
];
if (btnNo) {
  btnNo.addEventListener('click', () => {
    noClicks = Math.min(noClicks + 1, noMessages.length - 1);
    btnNo.textContent = noMessages[noClicks];
    if (noClicks === noMessages.length - 1) {
      setTimeout(() => {
        noClicks = 0;
        btnNo.textContent = 'âœ• NO / QUIT';
      }, 2000);
    }
  });
}

// â”€â”€â”€ Keyboard: any key â†’ scroll to stats â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
let hasScrolled = false;
function onAnyKey(e) {
  if (hasScrolled) return;
  if (['Tab', 'Shift', 'Control', 'Alt', 'Meta'].includes(e.key)) return;
  hasScrolled = true;
  triggerViewTransition('stats');
  window.removeEventListener('keydown', onAnyKey);
}
window.addEventListener('keydown', onAnyKey);

// â”€â”€â”€ Boot sequence â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
async function boot() {
  await runLoadingScreen();

  // Start canvas animation loop
  requestAnimationFrame(animate);

  // Start score counter
  tickScore();

  // GSAP animations
  initSectionReveals();
  initStatAnimations();
  animateHeroEntrance();
}

boot();

snippets.css:
/* â”€â”€ Demo 46: Real Estate â€” Light Minimal â”€â”€ */
/* Palette: White / Warm Gray / Charcoal / Soft Terracotta accent */
/* Fonts: Fraunces (display serif italic) + Inter (clean UI/body) */

:root {
  --white: #ffffff;
  --off-white: #fafaf8;
  --light: #f2f0ed;
  --mid: #e4e1db;
  --stone: #b8b4ad;
  --muted: #8a8680;
  --charcoal: #2a2825;
  --black: #1a1816;
  --accent: #c4856a;       /* warm terracotta */
  --accent-light: #f0e6e0; /* blush tint */
  --accent-dark: #9c5c45;
  --green: #5a7a65;        /* sage */
}

*, *::before, *::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
  background: var(--white);
  color: var(--charcoal);
  line-height: 1.6;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* â”€â”€ Typography Utilities â”€â”€ */
.serif {
  font-family: 'Fraunces', Georgia, serif;
}

.serif-italic {
  font-family: 'Fraunces', Georgia, serif;
  font-style: italic;
}

/* â”€â”€ Navigation â”€â”€ */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.5rem 3rem;
  background: transparent;
  transition: background 0.4s ease, padding 0.4s ease, border-bottom 0.4s ease;
}

.nav.scrolled {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(12px);
  padding: 1rem 3rem;
  border-bottom: 1px solid var(--mid);
}

.nav-brand {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  text-decoration: none;
  color: var(--charcoal);
}

.nav-mark {
  width: 28px;
  height: 28px;
  background: var(--charcoal);
  clip-path: polygon(50% 0%, 100% 50%, 50% 100%, 0% 50%);
}

.nav-name {
  font-family: 'Fraunces', serif;
  font-size: 1.1rem;
  font-weight: 400;
  letter-spacing: 0.05em;
  color: var(--charcoal);
}

.nav-links {
  display: flex;
  align-items: center;
  gap: 2.5rem;
  list-style: none;
}

.nav-links a {
  text-decoration: none;
  color: var(--charcoal);
  font-size: 0.85rem;
  font-weight: 500;
  letter-spacing: 0.04em;
  transition: color 0.2s;
}

.nav-links a:hover {
  color: var(--accent);
}

.nav-cta {
  display: inline-block;
  padding: 0.65rem 1.5rem;
  background: var(--charcoal);
  color: var(--white);
  text-decoration: none;
  font-size: 0.8rem;
  font-weight: 600;
  letter-spacing: 0.06em;
  text-transform: uppercase;
  border-radius: 2px;
  transition: background 0.2s, transform 0.2s;
}

.nav-cta:hover {
  background: var(--accent);
  transform: translateY(-1px);
}

/* â”€â”€ Hero â”€â”€ */
.hero {
  display: grid;
  grid-template-columns: 1fr 1fr;
  min-height: 100vh;
  padding-top: 5rem;
}

.hero-left {
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 5rem 3rem 5rem 3rem;
  background: var(--off-white);
}

.hero-label {
  font-size: 0.72rem;
  font-weight: 600;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 2rem;
}

.hero-h1 {
  font-family: 'Fraunces', serif;
  font-size: clamp(2.8rem, 5vw, 4.5rem);
  font-weight: 300;
  line-height: 1.15;
  color: var(--black);
  margin-bottom: 1.5rem;
}

.hero-h1 .h1-italic {
  font-style: italic;
  color: var(--accent);
}

.hero-body {
  font-size: 1rem;
  color: var(--muted);
  line-height: 1.8;
  max-width: 400px;
  margin-bottom: 2.5rem;
}

.hero-btns {
  display: flex;
  gap: 1rem;
  align-items: center;
  margin-bottom: 4rem;
}

.btn-dark {
  display: inline-block;
  padding: 0.85rem 2rem;
  background: var(--charcoal);
  color: var(--white);
  text-decoration: none;
  font-size: 0.82rem;
  font-weight: 600;
  letter-spacing: 0.08em;
  text-transform: uppercase;
  border-radius: 2px;
  transition: background 0.2s, transform 0.2s;
}

.btn-dark:hover {
  background: var(--accent);
  transform: translateY(-2px);
}

.btn-line {
  display: inline-block;
  color: var(--charcoal);
  text-decoration: none;
  font-size: 0.85rem;
  font-weight: 500;
  border-bottom: 1px solid var(--charcoal);
  padding-bottom: 2px;
  transition: color 0.2s, border-color 0.2s;
}

.btn-line:hover {
  color: var(--accent);
  border-color: var(--accent);
}

.hero-metric-row {
  display: flex;
  align-items: center;
  gap: 2rem;
  padding-top: 2rem;
  border-top: 1px solid var(--mid);
}

.hero-metric {
  display: flex;
  flex-direction: column;
  gap: 0.25rem;
}

.hm-num {
  font-family: 'Fraunces', serif;
  font-size: 2rem;
  font-weight: 300;
  color: var(--black);
  line-height: 1;
}

.hm-label {
  font-size: 0.72rem;
  font-weight: 500;
  color: var(--stone);
  letter-spacing: 0.05em;
}

.hero-metric-div {
  width: 1px;
  height: 40px;
  background: var(--mid);
}

/* Hero right â€” image stack */
.hero-right {
  position: relative;
  background: var(--light);
  overflow: hidden;
}

.hero-img {
  position: absolute;
  border-radius: 4px;
  overflow: hidden;
}

.hero-img--main {
  top: 10%;
  left: 10%;
  right: 15%;
  bottom: 25%;
  background: linear-gradient(
    145deg,
    #d6cec4 0%,
    #c8bfb2 30%,
    #b8a99a 60%,
    #a89486 100%
  );
}

.hero-img--sub {
  bottom: 8%;
  right: 8%;
  width: 45%;
  height: 35%;
  background: linear-gradient(
    135deg,
    #e8e2d8 0%,
    #d4c9bc 40%,
    #c5b8aa 100%
  );
  box-shadow: 0 20px 60px rgba(0,0,0,0.12);
}

/* â”€â”€ Section shared â”€â”€ */
.section-eyebrow {
  display: inline-block;
  font-size: 0.68rem;
  font-weight: 700;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

/* â”€â”€ Listings Section â”€â”€ */
.listings-section {
  padding: 6rem 3rem;
  background: var(--white);
  border-top: 1px solid var(--mid);
}

.listings-header {
  display: flex;
  align-items: baseline;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 1.5rem;
  margin-bottom: 3rem;
}

.listings-header h2 {
  font-family: 'Fraunces', serif;
  font-size: clamp(1.8rem, 3vw, 2.5rem);
  font-weight: 300;
  color: var(--black);
}

.filter-bar {
  display: flex;
  gap: 0.5rem;
}

.filter-btn {
  padding: 0.45rem 1rem;
  border: 1px solid var(--mid);
  background: transparent;
  color: var(--muted);
  font-family: 'Inter', sans-serif;
  font-size: 0.78rem;
  font-weight: 500;
  letter-spacing: 0.04em;
  border-radius: 2px;
  cursor: pointer;
  transition: all 0.2s;
}

.filter-btn:hover,
.filter-btn.active {
  background: var(--charcoal);
  color: var(--white);
  border-color: var(--charcoal);
}

.listings-grid {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  grid-template-rows: auto;
  gap: 1.5rem;
}

.listing-card--large {
  grid-column: 1 / 2;
  grid-row: 1 / 3;
}

.listing-card {
  background: var(--off-white);
  border: 1px solid var(--mid);
  border-radius: 4px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.listing-card:hover {
  transform: translateY(-4px);
  box-shadow: 0 20px 60px rgba(0,0,0,0.08);
}

.lc-img {
  position: relative;
  overflow: hidden;
}

.listing-card--large .lc-img {
  height: 320px;
}

.lc-img--1 {
  background: linear-gradient(145deg, #b8b0a4 0%, #9a9088 40%, #7c7068 100%);
}

.lc-img--2 {
  height: 180px;
  background: linear-gradient(135deg, #c0bdb8 0%, #a8a49e 50%, #8a8680 100%);
}

.lc-img--3 {
  height: 180px;
  background: linear-gradient(125deg, #c8c2ba 0%, #b0aa9e 50%, #948e86 100%);
}

.lc-badge {
  position: absolute;
  top: 1rem;
  left: 1rem;
  padding: 0.3rem 0.7rem;
  background: var(--accent);
  color: var(--white);
  font-size: 0.68rem;
  font-weight: 700;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  border-radius: 2px;
}

.lc-info {
  padding: 1.5rem;
}

.lc-info--sm {
  padding: 1.25rem;
