import * as THREE from "three";

const host = document.getElementById("scene");
const toggle = document.getElementById("toggleMotion");
let motionEnabled = !window.MotionPreference.prefersReducedMotion();

const scene = new THREE.Scene();
const camera = new THREE.OrthographicCamera(-1, 1, 1, -1, 0.1, 10);
camera.position.z = 1;

const renderer = new THREE.WebGLRenderer({ antialias: true });
renderer.setPixelRatio(Math.min(window.devicePixelRatio || 1, 2));
renderer.setSize(window.innerWidth, window.innerHeight);
host.appendChild(renderer.domElement);

const uniforms = {
  uTime: { value: 0 },
  uResolution: { value: new THREE.Vector2(window.innerWidth, window.innerHeight) }
};

const material = new THREE.ShaderMaterial({
  uniforms,
  vertexShader: `
    varying vec2 vUv;
    void main() {
      vUv = uv;
      gl_Position = vec4(position, 1.0);
    }
  `,
  fragmentShader: `
    precision highp float;
    varying vec2 vUv;
    uniform float uTime;
    uniform vec2 uResolution;

    float noise(vec2 p){
      return sin(p.x) * sin(p.y);
    }

    void main() {
      vec2 uv = vUv;
      vec2 p = (uv - 0.5) * vec2(uResolution.x / uResolution.y, 1.0);
      float t = uTime * 0.25;

      float wave = sin(p.x * 3.2 + t) * 0.12 + cos(p.y * 2.4 - t * 1.2) * 0.1;
      vec2 q = p + vec2(wave, wave * 0.7);

      float d = length(q);
      float glow = 0.25 / (d + 0.12);
      float grain = noise(q * 8.0 + t);

      vec3 col = vec3(0.03, 0.05, 0.12);
      col += vec3(0.15, 0.32, 0.55) * glow;
      col += vec3(0.35, 0.18, 0.6) * max(0.0, sin(q.x * 6.0 + t));
      col += grain * 0.015;

      gl_FragColor = vec4(col, 1.0);
    }
  `
});

const quad = new THREE.Mesh(new THREE.PlaneGeometry(2, 2), material);
scene.add(quad);

function setLabel() {
  toggle.textContent = motionEnabled ? "Disable motion" : "Enable motion";
}

function animate() {
  requestAnimationFrame(animate);
  if (motionEnabled) uniforms.uTime.value += 0.016;
  renderer.render(scene, camera);
}

function onResize() {
  renderer.setSize(window.innerWidth, window.innerHeight);
  uniforms.uResolution.value.set(window.innerWidth, window.innerHeight);
}

toggle.addEventListener("click", () => {
  motionEnabled = !motionEnabled;
  setLabel();
});

window.addEventListener("resize", onResize);
setLabel();
animate();

snippets.css:
:root {
  --page-bg: #faf8f4;
  --page-surface: #ffffff;
  --page-border: #e8e4dc;
  --page-text: #1a1a1a;
  --page-muted: #8a8478;
  --page-accent: #c4682b;
  --page-accent-dark: #8b4513;
  --page-sea: #2e7d9c;
  --page-sky: #87ceeb;
  --page-sand: #d4b896;
}

*, *::before, *::after { margin: 0; padding: 0; box-sizing: border-box; }

body {
  background: var(--page-bg);
  color: var(--page-text);
  font-family: Georgia, 'Times New Roman', 'Playfair Display', serif;
  line-height: 1.8;
  overflow-x: hidden;
}

/* â”€â”€ Demo shell overrides for light theme â”€â”€ */
.demo-shell-back {
  background: rgba(255, 255, 255, 0.85) !important;
  border-color: rgba(0, 0, 0, 0.12) !important;
  color: var(--page-accent) !important;
}

.demo-shell-info {
  background: rgba(250, 248, 244, 0.95) !important;
  border-top-color: rgba(0, 0, 0, 0.08) !important;
  color: var(--page-muted) !important;
}

.demo-shell-info .demo-title { color: var(--page-text) !important; }

.demo-shell-info .demo-tag {
  background: rgba(196, 104, 43, 0.1) !important;
  border-color: rgba(196, 104, 43, 0.2) !important;
  color: var(--page-accent) !important;
}

.motion-toggle {
  background: rgba(255, 255, 255, 0.85) !important;
  border-color: rgba(0, 0, 0, 0.12) !important;
  color: var(--page-accent) !important;
}

/* â”€â”€ Sections â”€â”€ */
.section {
  position: relative;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 6rem 2rem;
}

/* â”€â”€ Hero â”€â”€ */
.hero-section {
  position: relative;
  overflow: hidden;
  text-align: center;
}

.hero-bg {
  position: absolute;
  inset: 0;
  background: linear-gradient(180deg,
    var(--page-sky) 0%,
    #5ba8c8 30%,
    var(--page-sea) 50%,
    var(--page-bg) 85%,
    var(--page-bg) 100%
  );
  z-index: 0;
}

.hero-content {
  position: relative;
  z-index: 1;
}

.hero-overline {
  display: block;
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.7rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: rgba(255, 255, 255, 0.7);
  margin-bottom: 1.5rem;
  font-weight: 500;
}

.hero-title {
  font-size: clamp(3rem, 10vw, 7rem);
  font-weight: 700;
  color: #ffffff;
  line-height: 1;
  letter-spacing: -0.02em;
  text-shadow: 0 4px 40px rgba(0, 0, 0, 0.15);
  margin-bottom: 1rem;
}

.hero-rule {
  width: 60px;
  height: 2px;
  border: none;
  background: var(--page-accent);
  margin: 1.5rem auto;
  opacity: 0;
}

.hero-subtitle {
  font-size: clamp(1rem, 3vw, 1.5rem);
  color: rgba(255, 255, 255, 0.8);
  font-style: italic;
  font-weight: 400;
  opacity: 0;
}

.scroll-arrow {
  margin-top: 4rem;
  color: rgba(255, 255, 255, 0.5);
  opacity: 0;
  animation: scroll-float 2.5s ease-in-out infinite;
}

@keyframes scroll-float {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(8px); }
}

.reduced-motion .scroll-arrow { animation: none; }

/* â”€â”€ Prose Section â”€â”€ */
.prose-section {
  min-height: auto;
  padding: 8rem 2rem;
}

.prose-column {
  max-width: 680px;
  width: 100%;
}

.prose-text {
  font-size: clamp(1rem, 2vw, 1.15rem);
  color: var(--page-muted);
  margin-bottom: 2rem;
  transition: color 0.3s ease;
}

.prose-text.revealed {
  color: var(--page-text);
}

.reduced-motion .prose-text {
  color: var(--page-text);
}

/* Drop cap */
.dropcap::first-letter {
  float: left;
  font-size: 3.5em;
  line-height: 0.8;
  padding-right: 0.1em;
  color: var(--page-accent);
  font-weight: 700;
}

/* Pull quote */
.pull-quote {
  border-left: 3px solid var(--page-accent);
  padding: 1rem 0 1rem 2rem;
  margin: 3rem 0;
}

.quote-text {
  font-size: clamp(1.1rem, 2.5vw, 1.35rem);
  font-style: italic;
  color: var(--page-accent-dark);
  line-height: 1.6;
}

/* â”€â”€ Horizontal Gallery â”€â”€ */
.gallery-section {
  position: relative;
  height: 400vh;
}

.gallery-track {
  position: sticky;
  top: 0;
  height: 100vh;
  display: flex;
  width: 400vw;
  overflow: hidden;
}

.gallery-panel {
  width: 100vw;
  height: 100vh;
  position: relative;
  flex-shrink: 0;
  overflow: hidden;
}

.panel-landscape {
  position: absolute;
  inset: 0;
}

/* CSS-only landscape compositions */
.panel-cliffs .panel-landscape {
  background:
    linear-gradient(180deg, var(--page-sky) 0%, #6bb3d4 40%, transparent 50%),
    linear-gradient(180deg, transparent 40%, var(--page-sea) 55%, #1a5f7a 100%);
}

.panel-cliffs .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 0;
  left: 10%;
  width: 35%;
  height: 75%;
  background: linear-gradient(160deg, #d4a574 0%, #a67c52 40%, #8b6342 100%);
  clip-path: polygon(20% 100%, 0% 40%, 15% 15%, 40% 0%, 65% 10%, 80% 25%, 100% 50%, 90% 100%);
}

.panel-cliffs .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 0;
  right: 5%;
  width: 25%;
  height: 60%;
  background: linear-gradient(170deg, #c49a6c 0%, #9e7a54 100%);
  clip-path: polygon(10% 100%, 0% 50%, 30% 10%, 60% 0%, 90% 20%, 100% 60%, 95% 100%);
}

.panel-sea .panel-landscape {
  background: linear-gradient(180deg, #6bb3d4 0%, var(--page-sea) 30%, #1a6b8a 60%, #144d65 100%);
}

.panel-sea .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 30%;
  left: 0;
  right: 0;
  height: 20%;
  background: rgba(255, 255, 255, 0.08);
  border-radius: 50% 50% 0 0 / 100% 100% 0 0;
  transform: scaleX(1.5);
}

.panel-sea .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 20%;
  left: -10%;
  right: -10%;
  height: 15%;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 50% 50% 0 0 / 100% 100% 0 0;
}

.panel-sunset .panel-landscape {
  background: linear-gradient(180deg,
    #2c1654 0%,
    #8b3a62 20%,
    #d4614a 40%,
    #f0a040 55%,
    #ffd080 65%,
    var(--page-sea) 80%,
    #1a5f7a 100%
  );
}

.panel-sunset .panel-landscape::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 80px;
  height: 80px;
  border-radius: 50%;
  background: radial-gradient(circle, #fff8e0 0%, #ffd060 40%, #f0a040 70%, transparent 100%);
  filter: blur(8px);
}

.panel-village .panel-landscape {
  background: linear-gradient(180deg,
    #2a1a3e 0%,
    #4a2a5e 25%,
    #8b5a3e 50%,
    #c49a6c 70%,
    var(--page-sand) 85%,
    var(--page-bg) 100%
  );
}

.panel-village .panel-landscape::before {
  content: '';
  position: absolute;
  bottom: 25%;
  left: 15%;
  width: 70%;
  height: 35%;
  background: #1a1a2e;
  clip-path: polygon(
    0% 100%, 2% 60%, 5% 40%, 8% 60%, 12% 30%, 15% 50%, 18% 20%, 22% 45%,
    25% 15%, 28% 35%, 32% 10%, 35% 25%, 38% 40%, 42% 20%, 45% 35%, 48% 5%,
    52% 30%, 55% 15%, 58% 40%, 62% 25%, 65% 45%, 68% 20%, 72% 50%, 75% 30%,
    78% 55%, 82% 35%, 85% 50%, 88% 40%, 92% 60%, 95% 45%, 100% 70%, 100% 100%
  );
  opacity: 0.7;
}

/* Tiny windows */
.panel-village .panel-landscape::after {
  content: '';
  position: absolute;
  bottom: 32%;
  left: 25%;
  width: 50%;
  height: 15%;
  background:
    radial-gradient(circle 2px at 10% 30%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 25% 50%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 40% 20%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 55% 60%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 70% 35%, #ffd060 0%, transparent 100%),
    radial-gradient(circle 2px at 85% 45%, #ffd060 0%, transparent 100%);
}

.panel-caption {
  position: absolute;
  bottom: 4rem;
  left: 4rem;
  z-index: 2;
  display: flex;
  align-items: baseline;
  gap: 1rem;
  opacity: 0;
  transform: translateY(20px);
}

.reduced-motion .panel-caption {
  opacity: 1;
  transform: none;
}

.caption-number {
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.7rem;
  font-weight: 600;
  letter-spacing: 0.1em;
  color: rgba(255, 255, 255, 0.5);
}

.caption-text {
  font-size: clamp(1.2rem, 3vw, 1.8rem);
  color: #ffffff;
  font-weight: 600;
  text-shadow: 0 2px 16px rgba(0, 0, 0, 0.3);
}

/* Gallery progress bar */
.gallery-progress {
  position: fixed;
  bottom: 50px;
  left: 50%;
  transform: translateX(-50%);
  width: 200px;
  height: 2px;
  background: rgba(196, 104, 43, 0.2);
  border-radius: 1px;
  z-index: 10;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.gallery-progress.visible {
  opacity: 1;
}

.gallery-progress-fill {
  height: 100%;
  width: 0%;
  background: var(--page-accent);
  border-radius: 1px;
  transition: width 0.1s linear;
}

/* â”€â”€ Stories Section (FLIP) â”€â”€ */
.stories-section {
  min-height: auto;
  padding: 8rem 2rem;
}

.stories-heading {
  font-size: clamp(1.8rem, 5vw, 3rem);
  font-weight: 700;
  text-align: center;
  margin-bottom: 3rem;
  color: var(--page-text);
}

.stories-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
  max-width: 1000px;
  width: 100%;
  margin: 0 auto;
}

.story-card {
  background: var(--page-surface);
  border: 1px solid var(--page-border);
  border-radius: 12px;
  overflow: hidden;
  cursor: pointer;
  transition: box-shadow 0.3s ease;
}

.story-card:hover {
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
}

.story-card.expanded {
  grid-column: 1 / -1;
  cursor: default;
}

.story-header {
  height: 120px;
}

.header-path {
  background: linear-gradient(135deg, #c49a6c 0%, #8b6342 60%, #5a3e2b 100%);
}

.header-lemon {
  background: linear-gradient(135deg, #ffd060 0%, #f0a040 50%, #c4682b 100%);
}

.header-fisher {
  background: linear-gradient(135deg, var(--page-sea) 0%, #1a5f7a 50%, #0e3d4f 100%);
}

.story-title {
  font-size: 1.2rem;
  font-weight: 700;
  padding: 1.25rem 1.5rem 0.5rem;
  color: var(--page-text);
}

.story-preview {
  font-size: 0.9rem;
  color: var(--page-muted);
  padding: 0 1.5rem 1.5rem;
  line-height: 1.6;
}

.story-full {
  display: none;
  padding: 0 1.5rem;
}

.story-card.expanded .story-full {
  display: block;
}

.story-full p {
  font-size: 0.95rem;
  color: var(--page-muted);
  margin-bottom: 1.25rem;
  line-height: 1.7;
}

.story-toggle {
  display: block;
  width: 100%;
  padding: 1rem 1.5rem;
  background: none;
  border: none;
  border-top: 1px solid var(--page-border);
  color: var(--page-accent);
  font: 600 0.8rem/1 'Inter', system-ui, sans-serif;
  cursor: pointer;
  text-align: left;
  transition: background 0.2s ease;
}

.story-toggle:hover {
  background: rgba(196, 104, 43, 0.05);
}

.story-card.expanded .story-toggle::after {
  content: ' (click to close)';
  color: var(--page-muted);
  font-weight: 400;
}

/* â”€â”€ Closing â”€â”€ */
.closing-section {
  padding: 10rem 2rem;
  text-align: center;
}

.closing-quote {
  font-size: clamp(1.5rem, 5vw, 3rem);
  font-weight: 700;
  font-style: italic;
  color: var(--page-text);
  max-width: 700px;
  margin-bottom: 3rem;
  line-height: 1.3;
}

.closing-meta {
  display: flex;
  gap: 1rem;
  justify-content: center;
  align-items: center;
  font-family: 'Inter', system-ui, sans-serif;
  font-size: 0.75rem;
  color: var(--page-muted);
  text-transform: uppercase;
  letter-spacing: 0.08em;
  flex-wrap: wrap;
}

.meta-divider {
  opacity: 0.3;
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 768px) {
  .stories-grid {
    grid-template-columns: 1fr;
  }

  .section {
    padding: 4rem 1.25rem;
  }

  .panel-caption {
    left: 2rem;
    bottom: 3rem;
  }

  .prose-column {
    padding: 0;
  }
}

