const toggle = document.getElementById("motionToggle");
const device = document.getElementById("device");
let noMotion = window.MotionPreference.prefersReducedMotion();

function applyMode() {
  document.body.classList.toggle("no-motion", noMotion);
  toggle.textContent = noMotion ? "Enable motion" : "Disable motion";
}

window.addEventListener("pointermove", (event) => {
  if (noMotion) return;
  const x = (event.clientX / window.innerWidth - 0.5) * 14;
  const y = (event.clientY / window.innerHeight - 0.5) * 12;
  device.style.transform = `rotateY(${x}deg) rotateX(${-y}deg)`;
});

toggle.addEventListener("click", () => {
  noMotion = !noMotion;
  if (noMotion) device.style.transform = "rotateY(0deg) rotateX(0deg)";
  applyMode();
});

applyMode();

snippets.css:
:root {
  --bg: #070a12;
  --text: #f0f4fb;
  --panel: #121a2b;
  --border: #263249;
  --accent: #86e8ff;
  --muted: #8a95a8;
  --purple: #ae52ff;
  --warm: #ffcc66;
  --pink: #ff40d6;
}

* { margin: 0; padding: 0; box-sizing: border-box; }

body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
  background: var(--bg);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
}

/* â”€â”€ Hero â”€â”€ */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 5rem 2rem;
  overflow: hidden;
}

/* Depth cards floating in background */
.hero-bg {
  position: absolute;
  inset: 0;
  pointer-events: none;
}

.hero-card {
  position: absolute;
  padding: 1rem 1.5rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  font-size: 0.75rem;
  font-weight: 700;
  letter-spacing: 1.5px;
  text-transform: uppercase;
  color: var(--muted);
}

.hc-1 { top: 18%; left: 8%; color: var(--accent); border-color: rgba(134, 232, 255, 0.25); }
.hc-2 { top: 55%; left: 5%; color: var(--warm); border-color: rgba(255, 204, 102, 0.25); }
.hc-3 { top: 25%; right: 8%; color: var(--purple); border-color: rgba(174, 82, 255, 0.25); }
.hc-4 { top: 65%; right: 10%; color: var(--pink); border-color: rgba(255, 64, 214, 0.25); }

.hero-content {
  position: relative;
  z-index: 1;
  max-width: 680px;
  text-align: center;
}

.eyebrow {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  border: 1px solid rgba(134, 232, 255, 0.25);
  border-radius: 20px;
  padding: 0.3rem 1rem;
  margin-bottom: 1.5rem;
}

.hero h1 {
  font-size: 3.8rem;
  font-weight: 900;
  line-height: 1.1;
  margin-bottom: 1.5rem;
  letter-spacing: -2px;
}

.hero h1 em {
  font-style: normal;
  color: var(--accent);
}

.hero-desc {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2rem;
  max-width: 540px;
  margin-left: auto;
  margin-right: auto;
  line-height: 1.8;
}

.hero-tags {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  justify-content: center;
}

.hero-tags span {
  font-size: 0.72rem;
  font-weight: 600;
  padding: 0.3rem 0.8rem;
  border: 1px solid var(--border);
  border-radius: 20px;
  color: var(--muted);
  background: rgba(18, 26, 43, 0.8);
}

/* â”€â”€ Challenge â”€â”€ */
.challenge-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
}

.content { max-width: 1100px; margin: 0 auto; }

.challenge-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: start;
}

.section-tag {
  display: inline-block;
  font-size: 0.7rem;
  font-weight: 700;
  letter-spacing: 2px;
  text-transform: uppercase;
  color: var(--accent);
  margin-bottom: 1rem;
}

.challenge-text h2 {
  font-size: 2.2rem;
  font-weight: 800;
  margin-bottom: 1.5rem;
  letter-spacing: -0.5px;
}

.challenge-text p {
  font-size: 1rem;
  color: var(--muted);
  margin-bottom: 1.2rem;
  line-height: 1.8;
}

.quote-block {
  margin-top: 2rem;
  padding: 1.5rem;
  border-left: 3px solid var(--accent);
  background: rgba(134, 232, 255, 0.04);
  border-radius: 0 8px 8px 0;
}

.quote-block blockquote {
  font-size: 1rem;
  color: var(--text);
  font-style: italic;
  margin-bottom: 0.8rem;
  line-height: 1.7;
}

.quote-block cite {
  font-size: 0.82rem;
  color: var(--muted);
  font-style: normal;
}

.challenge-stats {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.stat-pill {
  padding: 1.5rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 8px;
  transition: all 0.3s ease;
}

.stat-pill:hover {
  border-color: var(--accent);
}

.stat-val {
  display: block;
  font-size: 2rem;
  font-weight: 800;
  color: var(--accent);
  margin-bottom: 0.3rem;
  font-variant-numeric: tabular-nums;
}

.stat-label {
  font-size: 0.85rem;
  color: var(--muted);
}

/* â”€â”€ Process / SVG â”€â”€ */
.process-section {
  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(18, 26, 43, 0.5);
}

.section-header {
  text-align: center;
  max-width: 560px;
  margin: 0 auto 4rem;
}

.section-header h2 {
  font-size: 2.2rem;
  font-weight: 800;
  margin-bottom: 0.8rem;
  letter-spacing: -0.5px;
}

.section-header p {
  font-size: 1rem;
  color: var(--muted);
  line-height: 1.8;
}

.svg-wrapper {
  max-width: 960px;
  margin: 0 auto;
  padding: 0 1rem;
}

#process-svg {
  width: 100%;
  height: auto;
  overflow: visible;
}

