  font-size: 0.85rem;
  color: var(--text);
  font-weight: 600;
  margin-bottom: 0.5rem;
}

.result-delta {
  font-size: 0.78rem;
  color: var(--muted);
}

.trend-up { color: #4ade80; }

/* â”€â”€ Testimonials â”€â”€ */
