  padding: 7rem 2rem;
  border-top: 1px solid var(--border);
  background: rgba(18, 26, 43, 0.5);
}

.testimonials-grid {
  max-width: 1000px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5rem;
}

.tl-card {
  padding: 1.8rem;
  background: var(--panel);
  border: 1px solid var(--border);
  border-radius: 10px;
  transition: all 0.3s ease;
}

.tl-card:hover {
  border-color: rgba(134, 232, 255, 0.3);
  transform: translateY(-3px);
}

.tl-card blockquote {
  font-size: 0.95rem;
  color: var(--text);
  font-style: italic;
  line-height: 1.7;
  margin-bottom: 1.5rem;
}

.tl-author {
  display: flex;
  align-items: center;
  gap: 0.8rem;
}

.tl-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.75rem;
  font-weight: 800;
  background: hsl(var(--av-hue), 60%, 15%);
  border: 2px solid hsl(var(--av-hue), 60%, 30%);
  color: hsl(var(--av-hue), 70%, 60%);
  flex-shrink: 0;
}

.tl-author strong {
  display: block;
  font-size: 0.88rem;
  color: var(--text);
}

.tl-author span {
  font-size: 0.78rem;
  color: var(--muted);
}

/* â”€â”€ CTA â”€â”€ */
.cta-section {
  padding: 8rem 2rem;
  text-align: center;
  border-top: 1px solid var(--border);
  background: radial-gradient(ellipse at 50% 50%, rgba(134, 232, 255, 0.06) 0%, transparent 70%);
}

.cta-section h2 {
  font-size: 2.8rem;
  font-weight: 900;
  margin-bottom: 1rem;
  letter-spacing: -1px;
}

.cta-section p {
  font-size: 1.1rem;
  color: var(--muted);
  margin-bottom: 2rem;
}

.btn-primary {
  display: inline-block;
  padding: 0.9rem 2rem;
  background: var(--accent);
  color: var(--bg);
  border-radius: 6px;
  font-weight: 700;
  text-decoration: none;
  transition: all 0.3s ease;
}

.btn-primary:hover {
  background: rgba(134, 232, 255, 0.85);
  transform: translateY(-2px);
}

/* â”€â”€ Responsive â”€â”€ */
@media (max-width: 900px) {
  .results-grid { grid-template-columns: repeat(2, 1fr); }
  .testimonials-grid { grid-template-columns: 1fr; }
}

@media (max-width: 768px) {
  .hero h1 { font-size: 2.8rem; }
  .challenge-grid { grid-template-columns: 1fr; gap: 2rem; }
  .results-grid { grid-template-columns: repeat(2, 1fr); }
  .hc-1, .hc-2, .hc-3, .hc-4 { display: none; }
}

