// â”€â”€â”€ SVG Process Workflow (Demo 40 technique) â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const procLines = document.querySelectorAll('.proc-line');

procLines.forEach((path) => {
  const length = path.getTotalLength();
