  background:
    radial-gradient(circle at 15% 12%, rgba(70, 125, 236, 0.28), transparent 34%),
    radial-gradient(circle at 82% 78%, rgba(184, 83, 255, 0.24), transparent 36%),
    var(--bg);
}

.topbar {
  position: fixed;
  inset: 0 0 auto 0;
  z-index: var(--z-ui, 40);
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.7rem 1rem;
