p {
  color: var(--muted);
  max-width: 62ch;
}

.chips {
  display: flex;
  flex-wrap: wrap;
  gap: 0.55rem;
}

.chips span {
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 999px;
  padding: 0.25rem 0.62rem;
  background: rgba(255, 255, 255, 0.05);
  font-size: 0.83rem;
  color: #e4ecfa;
