.card-one { background: linear-gradient(140deg, #223c5e, #2d6db4); }
.card-two { background: linear-gradient(140deg, #3f2a61, #8d45c9); }
.card-three { background: linear-gradient(140deg, #2f3d62, #6c7fca); }

.note {
