@media (max-width: 900px) {
  .panel {
    grid-template-columns: 1fr;
    min-height: 86vh;
    padding-top: 0.6rem;
    padding-bottom: 0.8rem;
  }

  .visual {
    height: 32vh;
  }

  .stage-cards {
    height: 42vh;
  }
}

