}

*, *::before, *::after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  scroll-behavior: smooth;
}

body {
  font-family: 'EB Garamond', Georgia, serif;
  background: var(--black);
  color: var(--text);
  line-height: 1.6;
  overflow-x: hidden;
  -webkit-font-smoothing: antialiased;
}

/* â”€â”€ Grain overlay â”€â”€ */
.grain {
  position: fixed;
  inset: 0;
  z-index: 200;
  pointer-events: none;
  opacity: 0.045;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 256 256' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noise'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noise)'/%3E%3C/svg%3E");
  background-size: 256px 256px;
  background-repeat: repeat;
  mix-blend-mode: overlay;
}

/* â”€â”€ Navigation â”€â”€ */
.nav {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 100;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1.5rem 3rem;
  background: transparent;
  transition: background 0.5s ease, border-bottom 0.5s ease;
}

.nav.scrolled {
  background: rgba(12, 11, 9, 0.92);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid var(--border);
}

.nav-logo {
  font-family: 'EB Garamond', serif;
  font-size: 1.05rem;
  font-weight: 400;
  font-style: italic;
  color: var(--text);
  text-decoration: none;
  letter-spacing: 0.02em;
}

.nav-links {
  display: flex;
  gap: 2.5rem;
  list-style: none;
}

.nav-links a {
  font-family: 'Geist Mono', monospace;
  font-size: 0.72rem;
  font-weight: 400;
  letter-spacing: 0.08em;
  color: var(--muted);
  text-decoration: none;
  text-transform: uppercase;
  transition: color 0.25s;
}

.nav-links a:hover {
  color: var(--warm);
}

.nav-book {
  font-family: 'Geist Mono', monospace;
  font-size: 0.68rem;
  font-weight: 500;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--gold);
  text-decoration: none;
  border-bottom: 1px solid var(--gold-dim);
  padding-bottom: 1px;
  transition: color 0.2s, border-color 0.2s;
}

.nav-book:hover {
  color: var(--warm);
  border-color: var(--warm);
}

/* â”€â”€ Section label â”€â”€ */
.section-label {
  font-family: 'Geist Mono', monospace;
  font-size: 0.65rem;
  font-weight: 400;
  letter-spacing: 0.18em;
  text-transform: uppercase;
  color: var(--muted);
  display: block;
  margin-bottom: 0.75rem;
}

/* â”€â”€ Hero â”€â”€ */
.hero {
  position: relative;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  padding: 5rem 3rem 4rem;
  overflow: hidden;
}

.hero-inner {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  flex: 1;
  padding-top: 2rem;
}

.hero-eyebrow {
  font-family: 'Geist Mono', monospace;
  font-size: 0.65rem;
  font-weight: 400;
  letter-spacing: 0.12em;
  text-transform: uppercase;
  color: var(--muted);
  margin-bottom: 2rem;
}

.hero-h1 {
  font-family: 'EB Garamond', serif;
  font-size: clamp(3rem, 6vw, 5.5rem);
  font-weight: 400;
  line-height: 1.2;
  color: var(--text);
}

.hero-h1 em {
  font-style: italic;
  color: var(--gold);
}

/* Hero featured image */
.hero-featured {
  position: relative;
}

.hf-img--main {
  width: 100%;
  aspect-ratio: 3/4;
  background: linear-gradient(
    180deg,
    #2a2318 0%,
    #1a160e 30%,
    #121008 60%,
    #0e0c07 100%
  );
  /* Simulate a film photograph â€” warm dark composition */
  box-shadow: 0 30px 80px rgba(0,0,0,0.7);
  position: relative;
}

.hf-img--main::before {
  content: '';
  position: absolute;
  inset: 0;
  background:
    radial-gradient(ellipse at 60% 25%, rgba(200, 169, 110, 0.08) 0%, transparent 55%),
    radial-gradient(ellipse at 20% 80%, rgba(80, 60, 30, 0.2) 0%, transparent 40%);
}

.hf-caption {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 0.75rem;
}

.hf-title {
  font-family: 'Geist Mono', monospace;
  font-size: 0.68rem;
  color: var(--muted);
  letter-spacing: 0.08em;
}

.hf-series {
  font-family: 'EB Garamond', serif;
  font-size: 0.85rem;
  font-style: italic;
  color: var(--gold);
}

/* Scroll hint */
.hero-scroll-hint {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-top: 3rem;
}

.hero-scroll-hint span {
  font-family: 'Geist Mono', monospace;
  font-size: 0.62rem;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted);
}

.scroll-line {
  width: 60px;
  height: 1px;
  background: var(--border);
  position: relative;
  overflow: hidden;
}

.scroll-line::after {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 1px;
  background: var(--gold);
  animation: scroll-hint 2s ease-in-out infinite;
}

@keyframes scroll-hint {
  0%   { left: -100%; }
  100% { left: 100%; }
}

/* â”€â”€ Work Section / Masonry â”€â”€ */
.work-section {
  padding: 5rem 3rem;
  border-top: 1px solid var(--border);
}

.work-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 2.5rem;
}

.work-header .section-label {
  margin-bottom: 0;
}

.work-year {
  font-family: 'Geist Mono', monospace;
  font-size: 0.65rem;
  color: var(--muted);
  letter-spacing: 0.1em;
}

.masonry-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 1.5px;
  background: var(--border);
}

.photo-item {
