  transition: color 0.2s;
}

.cl-item:first-child {
  border-top: 1px solid var(--border);
}

.cl-label {
  font-family: 'Geist Mono', monospace;
  font-size: 0.6rem;
  font-weight: 400;
  letter-spacing: 0.15em;
  text-transform: uppercase;
  color: var(--muted);
}

.cl-value {
  font-family: 'EB Garamond', serif;
